css:
@-moz-document domain("bigclosetr.us") {
html, .links {
    color: #E0E0E0 !important;
}
html {
    background: none !important;
    background-color: #0A0A0A !important;
}
#page {
    background-color: #1A1A1A !important;
}
#header, .header-inner, #footer {
    background: none !important;
    background-color: #7c2c5c !important;
}
#menu-bar, .block-title {
    background-color: #631343 !important;
}
.block-inner, .comment.odd {
    background-color: #2A2A2A !important;
}
a:visited {
    color: #8f6ba2 !important;
}
a:link, a:active, a.active {
    color: #c576ee !important;
}
section.block .item-list {
    background-color: #2A2A2A !important;
    padding: 10px !important;
    border-radius: 0px 0px 12px 12px !important;
}
div.dpblock li {
    background-color: #1a1a1a !important;
    color: #e0e0e0 !important;
}
li.views-fluid-grid-item
{
    border: none !important;
}
ul.views-fluid-grid-items-box-shadow li.views-fluid-grid-item {
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}
#main-content .node-teaser.node-sticky {
    background-color: #2A2A2A !important;
}
blockquote {
    background-color: #1A1A1A !important;
}
}
