    height: 4.5em;
    background: #3e1616;
    top: 7em;
    right: 2.5em;
}
#imagelightbox-caption {
    background-color: #1a4b61;
    color: #FFC107;
    font-size: 20px;
    padding: 10px;
}

.b-videosaves__list_item:hover .td {
    background-color: #334192;
}

/* movie info */
.b-post__title h1 {        color: #e1e289;}
td.l, td.l h2 {            color: #1dd877!important}
span.b-post__info_rates, 
span.b-post__info_rates a {color: #96cd0a;}
span.bold {                color: #f45555;}
i {                        color: #6ab5ec!important;}
td.rd a {                  color: #e1e289;}
