css:
@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*")
{

/*[[theme]]*/

::-moz-selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}
::selection {
  color: var(--bg) !important;
  background: var(--fg-link) !important;
}

html, body, .firefox body, #stylus {
  background-color: var(--bg) }
header, #help-popup, #message-box-contents, #message-box-buttons {
  background-color: var(--bg-2) }
oddrow, select > optgroup, #message-box-title {
  background-color: var(--bg-odd) }
input[type], input:not([type]), select, textarea, .search-result, #search {
  background-color: var(--bg-input);
  color: var(--fg-input) }
input[type]:focus, input:not([type]):focus, select:focus, textarea:focus, .search-result:hover, .style-name:hover input[type="checkbox"], .style-name:hover input[type="checkbox"]:checked, #search:focus, #manage\.newUI\.sort {
  background-color: var(--bg-input-hover);
  color: var(--fg-input-hover) }
.search-result-meta {
  background-color: rgba(0, 0, 0, 0.5) }
.search-result:hover .search-result-meta {
  background-color: rgba(0, 0, 0, 0.8) }
#filters select {
  background-color: transparent }
.active #filters-stats {
  background-color: var(--fg-2) }
nobg {
  background-image: none }

html, body, .firefox body, #stylus {
  color: var(--fg) }
#header, h1, h2, h3, h4, h5, #help-popup .title {
  color: var(--fg-2) }
.muted {
  color: var(--fg-muted) }

a, a:link {
  color: var(--fg-link) }
a:hover, a:visited:hover, .breadcrumbs > .write-style-link:hover, .search-result:hover .search-result-title, .newUI .entry.enabled .style-name:hover .style-name-link {
  color: var(--fg-link-hover) }
a:visited {
  color: var(--fg-link-visited) }

noborder, pre code {
  border: none }
custom-border, input[type], input:not([type]), select, textarea, pre, code, .beautify-options select, #search, #manage\.newUI\.sort {
  border: var(--border) }
bordercolor, h1, h2, h3, h4, h5, hr, .CodeMirror-gutters, .CodeMirror, #installed, #header {
  border-color: var(--border-color) }
#help-popup .title {
  border-bottom: solid 0.5px var(--border-color) }
.dropdown-menu .divider, hr {
  background-color: var(--border-color) }
.CodeMirror-cursor, input[type]:focus, input[type]:focus:hover, input:not([type]):focus, input:not([type]):focus:hover, #search:focus, #manage\.newUI\.sort:focus {
  border-color: var(--fg-link) }
#header summary:hover h2 {
  border-color: var(--fg-2) }

noshadow {
  box-shadow: none }
baseshadow {
  box-shadow: 0 0 5px #000 }

textshadow {
  text-shadow: 0.3px 0.3px 0.5px rgba(0, 0, 0, 0.5) }

success, success a {
  background-color: var(--bg-success);
  color: var(--fg-success) }
info, info a, .alert-info {
  background-color: var(--bg-info);
  color: var(--fg-info) }
warning, warning a, .alert-warning {
  background-color: var(--bg-warning);
  color: var(--fg-warning) }
error, error a, .alert-danger {
  background-color: var(--bg-error);
  color: var(--fg-error) }
disabled, input[readonly] {
  background-color: var(--bg-disabled);
  color: var(--fg-disabled) }

#find-styles-link, #search-results .search-results-nav button {
  padding: 4px 10px;
  text-decoration: none }

button, #find-styles-link, #search-results .search-results-nav button {
  color: var(--fg-input);
  background-image: linear-gradient(var(--bg-input-hover), var(--bg-input));
  border: solid 0.5px var(--border-color);
  text-shadow: 0.3px 0.3px 0.5px #000 }

button:active, #find-styles-link:active {
  background-image: linear-gradient(var(--bg-input), var(--bg-input-hover)) }

button:focus, #find-styles-link:focus {
  background-color: var(--bg-input) !important;
  border: solid 0.5px var(--fg-link) !important;
  color: var(--fg-input-hover) !important }

input[type]:focus, input[type]:focus:hover, input:not([type]):focus, input:not([type]):focus:hover {
  box-shadow: 0 0 3px var(--fg-link) }

pre, code, .CodeMirror { background-color: var(--syntax-bg) }
.CodeMirror-gutter-filler, .CodeMirror-gutters, .CodeMirror-activeline-background { background-color: var(--syntax-bg-2) }
.CodeMirror pre, pre, code { color: var(--syntax-fg) }
div.CodeMirror span.CodeMirror-matchingbracket { color: var(--syntax-fg-2) }
.cm-s-default .cm-keyword,
.cm-s-default .cm-tag { color: var(--syntax-builtin) }
.cm-s-default .cm-atom,
.cm-s-default .cm-builtin{ color: var(--syntax-keyword) }
.cm-s-default .cm-qualifier { color: var(--syntax-function) }
.cm-s-default .cm-string { color: var(--syntax-string) }
.cm-s-default .cm-number { color: var(--syntax-number) }
.cm-s-default .cm-comment { color: var(--syntax-comment) }
.cm-s-default .cm-variable-2 { color: #3f3 }
.CodeMirror-foldmarker,
.cm-s-default .cm-variable-3 { color: #4af }
.CodeMirror-foldmarker { text-shadow: 1px 1px 2px #4af, -1px -1px 2px #4af, 1px -1px 2px #4af, -1px 1px 2px #4af }

.CodeMirror-selected { background-color: var(--bg-input) }
.CodeMirror-focused .CodeMirror-selected { background-color: var(--bg-input-hover) }

.svg-icon {
  fill: var(--fg) }
.svg-icon.info:hover, .svg-icon.settings:hover, a:hover .svg-icon {
  fill: var(--fg-link-hover) }
.svg-icon.select-arrow {
  fill: var(--fg-input) }

input[type="checkbox"]:not(.slider) {
  background-color: var(--bg-input) }
input[type="checkbox"]:not(.slider):hover {
  background-color: var(--bg-input-hover) }
.svg-icon.checked {
  fill: var(--fg-input) }

.firefox body {
  box-shadow: 0 -1px 3px var(--bg-2) inset }

}
