css:
@-moz-document domain("www.youtube.com") {
 #contents.ytd-section-list-renderer > *.ytd-section-list-renderer:not(:last-child):not(ytd-page-introduction-renderer) {
   border-bottom: 1px solid black;
 }
 ytd-page-manager > *.ytd-page-manager {
    background: url(http://www.vectorfree.com/media/vectors/boxes-lighting-backdrop.jpg);
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
 }  
 #logo-icon-container.ytd-topbar-logo-renderer {
    width: 80px;
    height: 24px;
    -ms-flex: none;
    -webkit-flex: none;
    flex: none;
    filter: invert(100%) brightness(150%);
 }
 #progress.ytd-thumbnail-overlay-resume-playback-renderer {
    height: 100%;
    background: url(https://thumbs.dreamstime.com/t/barras-de-cor-36572897.jpg);
 }
 ytd-masthead[mode="legacy"] ytd-searchbox.ytd-masthead, ytd-masthead[mode="material-left"] 
ytd-searchbox.ytd-masthead {
    -ms-flex: 1 1 0.000000001px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 0.000000001px;
    flex-basis: 0.000000001px;
    -ms-flex-pack: start;
    -webkit-justify-content: flex-start;
    justify-content: flex-start;
    background-color: transparent;
 }
 #container.ytd-masthead {
    background-color: #1a3f5e;
 }

 ytd-masthead[mode="legacy"] #container.ytd-masthead, ytd-masthead[mode="material-left"] #container.ytd-masthead {
    -ms-flex-pack: start;
    -webkit-justify-content: flex-start;
    justify-content: flex-start;
    background: url(http://img64.laughinggif.com/pic/HTTP3d3dy5ub3J0aGVhc3Rlcm4uZWR1L2RpZ2l0YWwtbmV3cy1maWxlcy9wdXJuaW1hV2hhbGUvd2F0ZXJfYmtnLmdpZgloglog.gif);
    box-shadow: 0px 0 20px black;
 }
 button.yt-icon-button > yt-icon {
    width: 100%;
    height: 100%;
    color: black;
 }
 ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox, ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox input.ytd-searchbox {
    background-color: white;
    color: black;
 }
 ytd-searchbox[mode=legacy] #container.ytd-searchbox, ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox {
    background-color: white;
    border: 1px solid var(--ytd-searchbox-legacy-border-color);
    border-right: none;
    box-shadow: inset 0 1px 2px var(--ytd-searchbox-legacy-border-shadow-color);
    padding: 2px 6px;
    border-radius: 2px 0 0 2px;
    box-shadow: 0px 0 20px white;
 }
 yt-formatted-string[ellipsis-truncate] a.yt-formatted-string:first-of-type {
    margin-left: 0;
    color: aquamarine;
    font-size: 15px;
 }
 #metadata-line.ytd-grid-video-renderer span.ytd-grid-video-renderer {
    display: inline-block;
    color: floralwhite;
 }
 yt-icon.ytd-badge-supported-renderer {
    width: var(--ytd-badge-icon-size, 13px);
    height: var(--ytd-badge-icon-size, 13px);
    color: black;
 }
 #details.ytd-grid-video-renderer a.ytd-grid-video-renderer {
    display: block;
    color: black;
 }
 paper-button.ytd-subscribe-button-renderer {
    background-color: aquamarine;
    border-radius: 2px;
    color: black;
    padding: 10px 16px;
    margin: auto 4px;
    white-space: nowrap;
    font-size: 1.4rem;
    font-weight: 500;
    letter-spacing: .007px;
    text-transform: uppercase;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
 }
 paper-button.ytd-subscribe-button-renderer:hover {
    background-color: hotpink;
 }
 .deemphasize.yt-formatted-string {
    color: var(--yt-formatted-string-deemphasize-color);
    display: var(--yt-formatted-string-deemphasize_-_display);
    color: white;
    margin-left: var(--yt-formatted-string-deemphasize_-_margin-left);
 }
 #description-text.ytd-video-renderer {
    color: black;
    padding-top: 8px;
    margin-bottom: 8px;
    display: block;
    line-height: 1.8rem;
    max-height: 3.6rem;
    overflow: hidden;
    font-size: 1.3rem;
    font-weight: 400;
    text-transform: none;
 }
 #metadata-line.ytd-video-meta-block span.ytd-video-meta-block {
    display: inline-block;
    color: white;
 }
 #thumbnail.ytd-thumbnail {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    box-shadow: 0px 0 20px black;
 }
 #search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 65px;
    border: 1px solid var(--ytd-searchbox-legacy-button-border-color);
    background-color: #2D2D49;
    border-radius: 0 2px 2px 0;
    margin: 0;
 }
 .gsfs {
    font-size: 1.4rem;
    color: white;
 }
 .gsfs:hover {
    font-size: 1.4rem;
    color: black;
 }
 .sbsb_b {
    list-style-type: none;
    margin: 0;
    padding: 0;
    background-color: #2D2D49;
 }
 .sbsb_a {
    background: #2D2D49;
 }
 #search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
    width: 20px;
    height: 20px;
    color: white;
    opacity: 0.6;
 }
 #sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child) {
    border-bottom: 1px solid var(--yt-border-color);
    background-color: #2D2D49;
 }
 #sections.ytd-guide-renderer > ytd-guide-section-renderer.ytd-guide-renderer:not(:first-child), #sections.ytd-guide-renderer > ytd-guide-subscriptions-section-renderer.ytd-guide-renderer {
    padding: 8px 0;
    background-color: #2D2D49;
 }
 #guide-links-primary.ytd-guide-renderer {
    padding: 24px 27px 0;
    border-top: 1px solid var(--yt-border-color);
    background-color: #2D2D37;
 }
 #guide-links-secondary.ytd-guide-renderer {
    padding: 12px 27px 0;
    background-color: #2D2D37;
 }
 #footer.ytd-guide-renderer > #vat-notice {
    padding: 8px 27px;
    color: var(--ytd-vat-notice-text);
    font-size: 1.3rem;
    font-weight: 500;
    line-height: 2.1rem;
    background-color: #2D2D37;
 }
 #footer.ytd-guide-renderer > #copyright {
    padding: 24px 27px;
    color: white;
    font-size: 1.3rem;
    font-weight: 500;
    line-height: 2.1rem;
    background-color: #2D2D37;
 }
/* pop ups */
 ytd-simple-menu-header-renderer {
    background-color: #2D2D49;
    color: var(--yt-primary-text-color);
    min-height: 48px;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
 }
 ytd-notification-renderer {
    position: relative;
    padding: 16px 16px 16px 0;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    background-color: #2D2D37;
    border-bottom: 1px solid #2D2D49;
    border-top: 1px solid #2D2D49;
 }
 a.yt-simple-endpoint.ytd-active-account-header-renderer {
    padding: 16px;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: #2D2D49;
 }
 #sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child) {
    border-bottom: 1px solid var(--yt-border-color);
    background-color: #2D2D37;
 }
 app-drawer.ytd-app:not([persistent]) #header.ytd-app {
    border-bottom: 1px solid var(--yt-border-color);
    background-color: #1a3f5e;
 }
 /* end pop-ups */
 #metadata.ytd-channel-renderer {
    display: block;
    line-height: 1.8rem;
    max-height: 3.6rem;
    overflow: hidden;
    font-size: 1.3rem;
    font-weight: 400;
    text-transform: none;
    color: #FFFF31;
 }
 #description.ytd-channel-renderer {
    color: black;
    display: block;
    line-height: 1.8rem;
    max-height: 3.6rem;
    overflow: hidden;
    font-size: 1.3rem;
    font-weight: 400;
    text-transform: none;
 }
 /* video */
 #like-bar.ytd-sentiment-bar-renderer {
    background: aquamarine;
    height: 2px;
    transition: width 0.3s;
 }
 .title.ytd-video-primary-info-renderer {
    display: block;
    max-height: 4.8rem;
    overflow: hidden;
    font-size: 1.8rem;
    font-weight: 400;
    line-height: 2.4rem;
    font-weight: bold;
    text-align: right;
    text-shadow: 2px 2px black;
    color: white;
    font-size: 19px;
    margin-top: 5px;
    margin-bottom: 15px;
}
 span.yt-view-count-renderer {
    color: white;
    text-shadow: 2px 1px black;
    font-size: 26px;
 }
 yt-view-count-renderer {
    display: inline-block;
    font-size: 1.6rem;
    font-weight: 400;
    line-height: 2rem;
    margin-top: 0px;
 }
 ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer {
    cursor: pointer;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    color: darkgray;
 }
 .description.ytd-video-secondary-info-renderer yt-formatted-string.ytd-video-secondary-info-renderer {
    --yt-endpoint-color: aquamarine;
    color: black;
 }
 .description.ytd-video-secondary-info-renderer yt-formatted-string.ytd-video-secondary-info-renderer {
    --yt-endpoint-color: aquamarine;
    color: black;
 }
 a.yt-simple-endpoint.yt-formatted-string:hover {
    color: white;
    text-decoration: none;
 }
 #content-text.ytd-comment-renderer {
    --yt-endpoint-color: hsl(206.1, 79.3%, 52.7%);
    color: black;
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 2.1rem;
    line-height: 2rem;
 }
 .html5-video-player:not(.ytp-transparent), .html5-video-player.ad-interrupting, .html5-video-player.ended-mode, .html5-video-player.ytp-fullscreen {
    background-color: #000;
    box-shadow: 0px 0 30px black;
 }
 paper-button[subscribed].ytd-subscribe-button-renderer {
    background-color: cyan;
    color: black;
 }
 ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer {
    background-color: none;
    color: black;
    padding: 4px 8px;
    margin: 0;
 }
 paper-tabs.ytd-c4-tabbed-header-renderer {
    --paper-tabs-selection-bar-color: var(--yt-lightsource-secondary-title-color);
    --paper-tabs-selection-bar_-_border-bottom-width: 3px;
    box-sizing: border-box;
    color: var(--yt-lightsource-secondary-title-color);
    height: 48px;
    padding-bottom: 1px;
    margin-left: 51px;
    font-size: 1.4rem;
    font-weight: 500;
    letter-spacing: .007px;
    text-transform: uppercase;
    background-color: #2D2D37;
    border-bottom: black;
 }
 #channel-header.ytd-c4-tabbed-header-renderer {
    background-color: #2D2D37;
    width: 100%;
    padding: 16px 107px 0;
    position: relative;
    z-index: 1;
    min-width: 0;
 }
 #tabs-inner-container.ytd-c4-tabbed-header-renderer {
    background-color: #2D2D34;
 }
  #details-container.ytd-channel-about-metadata-renderer table.ytd-channel-about-metadata-renderer {
    color: black;
    font-size: 1.3rem;
    font-weight: 400;
    line-height: 1.8rem;
    text-transform: none;
 }
 #byline.ytd-video-meta-block {
    --yt-endpoint-color: var(--yt-metadata-color);
    color: aquamarine;
    overflow-x: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    display: flex;
 }
 #container.ytd-sentiment-bar-renderer {
    height: 2px;
    background-color: black;
 }
 .style-scope .ytd-menu-renderer .force-icon-button .style-default-active {
    color: aquamarine;
    font-size: 15px;
    font-weight: bold;
 }
 #label.ytd-pinned-comment-badge-renderer {
    color: cyan;
 }
 a.yt-simple-endpoint.yt-formatted-string {
   color: aquamarine;
 }
 .title.ytd-guide-entry-renderer {
    margin-right: 24px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 2.1rem;
    -ms-flex: 1 1 0.000000001px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 0.000000001px;
    flex-basis: 0.000000001px;
    color: white;
 }
 .guide-icon.ytd-guide-entry-renderer {
    margin-right: 27px;
    color: hotpink;
    --iron-icon-height: var(--yt-guide-icon-spacing);
    --iron-icon-width: var(--yt-guide-icon-spacing);
 }
 .guide-entry-badge.ytd-guide-entry-renderer {
    width: 16px;
    height: 16px;
    color: cyan;
 }
 .guide-entry-count.ytd-guide-entry-renderer {
    opacity: .4;
    vertical-align: middle;
    text-align: center;
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 2.1rem;
    color: white;
 }
 a.yt-simple-endpoint.yt-formatted-string:visited {
    color: aquamarine;
 }
 #guide-icon.ytd-masthead {
    fill: white;
 }
 #title.ytd-shelf-renderer {
    color: white;
    text-shadow: -2px 2px black;
    display: block;
    max-height: 2rem;
    overflow: hidden;
    font-size: 1.6rem;
    font-weight: 500;
    line-height: 2rem;
 }
 #notification-count.ytd-notification-topbar-button-renderer {
    position: absolute;
    top: 4px;
    right: 0;
    color: white;
    background-color: hotpink;
    width: 18px;
    height: 18px;
    border-radius: 50%;
    line-height: 18px;
    font-size: 10px;
    text-align: center;
    cursor: pointer;
 }
 button.yt-icon-button > yt-icon:hover {
    width: 100%;
    height: 100%;
    color: aquamarine;
 }
 .message.ytd-notification-renderer {
    margin-bottom: 8px;
    display: block;
    max-height: 12.6rem;
    overflow: hidden;
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 2.1rem;
    color: white;
 }
 yt-formatted-string.style-scope.ytd-simple-menu-header-renderer {
    color: white;
 }
 #items.yt-multi-page-menu-section-renderer > *.yt-multi-page-menu-section-renderer:hover {
    background-color: darkgray;
 }
 .toggle-button.paper-toggle-button {
    background-color: gray; 
    cursor: pointer;
 }
 #autoplay.ytd-compact-autoplay-renderer {
    color: aquamarine;
 }
 .toggle-container.paper-toggle-button {
    background-color: gray;
    border-radius: 20px;
 }
 paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background-color: hotpink;
 }
 #upnext.ytd-compact-autoplay-renderer {
     color: white;
 }
 .magicplayer--detach-button {
    position: fixed;
    width: 53px;
    height: 36px;
    top: 0px;
    left: 50%;
    margin-top: 80px;
    margin-left: 34px;
    opacity: 0;
    transition: opacity 0.3s;
    cursor: pointer;
    z-index: 999999;
 }
 paper-tab:not(.iron-selected) > .tab-content.paper-tab {
    opacity: 0.8;
    opacity: 1;
    color: white;
 } 
 .tab-content.paper-tab {
    color: hotpink;
 }
 #subscriber-count.ytd-c4-tabbed-header-renderer {
    color: cyan;
    font-size: 1.4rem;
    font-weight: 400;
    line-height: 2.1rem;
 }
 ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer {
    color: deeppink;
 }
 paper-listbox {
    background-color: #2D2D49;
 }
 yt-formatted-string.ytd-menu-service-item-renderer { 
    color: white;
 }
 ytd-menu-service-item-renderer:hover {
    background-color: hotpink;
 }
 yt-formatted-string.ytd-guide-signin-promo-renderer {
    color: white;
 }
 #guide-section-title.ytd-guide-section-renderer {
    color: aquamarine;
 }
 #channel-title.ytd-c4-tabbed-header-renderer {
    color: white;
 }
 #info-text.ytd-video-primary-info-renderer {
    display: block;
    max-height: 3rem;
    overflow: hidden;
    font-size: 0.6rem;
    font-weight: 400;
    line-height: 2rem;
 }
 #subscribe-button.ytd-video-secondary-info-renderer {
    margin-top: -160px;
 }
 #info.ytd-video-primary-info-renderer {
    display: block;
 }
 #sponsor-button.ytd-video-owner-renderer {
     margin-right: -190px;
 }
 .ytp-load-progress {
    z-index: 33;
    background: aquamarine;
 }
 .ytp-play-progress {
    z-index: 34;
    background: deeppink;
 }
 .ytp-scrubber-button .ytp-swatch-background-color {
     background: deeppink;
 }
 .ytp-hover-progress-light {
    background: darkcyan;
    box-shadow: 0px 0 20px deeppink;
 }
 .ytp-svg-fill {
    fill: hotpink;
 }
}
