css:
@-moz-document domain('docs.google.com'){

.kix-appview-editor {
    background-color: #111;
    height: 100%;
}

/* slides */
#pages > svg > rect,
.punch-filmstrip-scroll > svg > rect,
.punch-filmstrip-thumbnail-background {
    fill: #000000 !important;
    fill-opacity: 1;
}

::-webkit-scrollbar {
	display: none;
}

/*********/

/*black w/ border*/
.goog-control-hover {
    background-color: #111 !important;
    color: white !important;
    border: 1px solid black !important;
}

/*black no border*/
#docs-toolbar-wrapper,
#docs-chrome,
.docs-titlebar-buttons,
#workspace-container {
    background-color: #111 !important;
    color: white !important;
    border: none !important;
}

/*invert*/
#docs-docos-commentsbutton,
.docs-folder-container,
.docs-title-input,
.goog-toolbar-button,

.goog-toolbar-menu-button:not(#textColorButton):not(#bgColorButton):not(#cellColorMenuButton):not(#borderColorMenuButton),

.goog-toolbar-combo-button,
#docs-script-button-bar > div,
.punch-start-presentation-container
{
    filter: invert(1) saturate(0);
}

#textColorButton, #bgColorButton, #cellColorMenuButton, #borderColorMenuButton {
    background-color: #555;
}

#kix-horizontal-ruler {
    display: none;
}

.goog-toolbar-separator, .docs-toolbar-small-separator {
    opacity: 0;
}

}
