@-moz-document domain(app.hubspot.com) {
/* Adjusted to give override background and text colour */

/* Adjusted to give override background and text colour */

.highlight pre,
pre,
.highlight .hll,
.ace-dawn,
.source-editor-container {
    background-color: #272822;
    border: 1px solid #ccc;
    padding: 6px 10px;
    border-radius: 0;
    color: #FFFFFF;
    font-size: 13px;
}
.cm-s-hubspot-canvas-dark,
.cm-s-hubspot-canvas-dark .CodeMirror-gutters {
    background: #272822 !important;
}
.ide-codemirror-wrapper .CodeMirror {
    font-size: 13px !important;
}
.gist pre {
    color: #313131;
}
