css:
@-moz-document url-prefix("http://app.teamweek.com"), domain("app.teamweek.com") {
.grid__axis-x,.grid__users{
	padding-top: 0;	
    top: 0;

}
.grid-datepicker{	
    top: 0;
}


.grid-navbar__content,.grid__users,.grid-datepicker,.grid__users__bottom-overlay{
background-color: #333;
}
.grid-datepicker{
    border-bottom: none;
}

.grid__axis-x-wrapper,.header__inner__cell .week{
/* background-color: #333; */
}
.header__columns .grid__cell{
background-color: #333;
}
.grid__cell.today{
}
.grid__content{
background-color: #333;
}
.grid__background{
	opacity: 0.03;
}

.text--color-new-link,.grid-navbar__group,.grid-datepicker__year,.header__inner__cell .month-name,.grid__user__name{
	color: #aaa;
}
.scrollbar__lane, .grid.scrollable{
	background: #333;
}
.header__inner__cell .month-name{
    color: #aaa;
}
.header__inner__cell .day, .header__inner__cell .week,.header__inner__cell .week-number{
	color: #555;
}
.grid-navbar__content{
	border-bottom: #222;
    opacity: 0;
    height: 7px;
/*     width: 50px !important; */
    transition: opacity 0.3s;
    background: #000;
    overflow: hidden;
}
.grid-navbar__content:hover{
    height: 35px;
    width: 100vw !important;
	opacity: 1;
}

.header__columns .grid__cell.new-week {
    border-left-color: #2f2f2f;
}
.header__inner__cell--today {
    background-color: #272727;
}
.header__columns .grid__cell.today:after{
	content: '';
    display: block;
    position: absolute;
    top: 0;
    height: 100vh;
    border-left: 20px solid #ffffff33;
    pointer-events: none;
}

.navbar__group{
	background: #2f2f2f;
}

.scrollbar__handle__inner {
    background: #2f2f2f;
    height: 10px;
}



.header__columns .grid__cell.tomorrow{
    border-left: none;
}
/* *{
border-color: #000 !important;
} */
}
