css:
@-moz-document domain("twitch.tv"){
 
  /**                                                 **/
/** Twitch Dark Mode (Grey/Purple) V0.0.1 by VooDoo **/
/**                                                 **/



/* CHANNEL PAGE */
.tw-theme--dark .twilight-root {
    background-color: #121212;
    color: #ccc;
}

.tw-theme--dark .top-nav__menu {
    background-color: #272727;
}

.tw-theme--dark .top-nav__home-link, .tw-theme--light .top-nav__home-link {
    color: #6441a4;
}

.top-nav__nav-link.active, .top-nav__nav-link:hover, .tw-theme--dark .top-nav__nav-link.active, .tw-theme--dark .top-nav__nav-link:hover, .tw-theme--light .top-nav__nav-link.active, .tw-theme--light .top-nav__nav-link:hover {
    color: #ccc;
}
.tw-theme--dark .tw-interactable--hover, .tw-theme--dark .tw-interactable:hover {
    background: #181818;
    color: #ccc;
}
.tw-theme--dark .tw-c-background-alt {
    background-color: #1e1e1e !important;
}

.tw-theme--dark .tw-c-background-alt-2 {
    background-color: #060606!important;
}

.tw-theme--dark .tw-border-b {
    border-bottom: 1px solid #6f47aa!important;
}

.tw-theme--dark .search-result-section__titlesep {
    color: #ccc;
    background: #424242;
}

.tw-theme--dark .tw-c-text-link {
    color: #ccc!important;
}

.tw-mg-t-05 {
    background-color: #101010;
}

.tw-theme--dark .scrollable-area .simplebar-track .simplebar-scrollbar {
    background: #333333;
}

.tw-theme--dark .tw-c-background {
    background-color: #101010!important;
}

.tw-theme--dark .tw-button-icon--secondary:hover {
    color: #6f47aa;
}

.tw-theme--dark .tw-button-icon:hover {
    background: rgba(198, 192, 210, 0.2);
}

.tw-theme--dark .tw-c-text-alt {
    color: #ccc!important;
}

.tw-theme--dark .tw-border-r {
    border-right: 1px solid #6f47aa!important;
}

.tw-theme--dark .tw-border-t {
    border-top: 1px solid #6f47aa!important;
}

.tw-theme--dark .channel-info-bar {
    background-color: #0a0a0a;
}

.tw-theme--dark .tw-c-text-alt-2 {
    color: #ccc!important;
}

.tw-theme--dark a {
    color: #6f47aa;
}

.tw-theme--dark .channel-header {
    background-color: #2b2b2b;
    border-bottom: 1px solid #66449c;
}

.tw-theme--dark .pinned-cheer__headline {
    color: #6f47aa;
}

.tw-theme--dark .pinned-cheer__bounding-box {
    background-color: #020202;
    border-bottom: 1px solid #1e6d2f;
}

.tw-theme--dark .tw-textarea {
    border: 1px solid #424242;
    background: #0c0c0c;
    color: #ccc;
}

.tw-textarea:focus, .tw-theme--dark .tw-textarea:focus, .tw-theme--light .tw-textarea:focus {
    border-color: #6f47aa;
    -webkit-box-shadow: 0 0 6px -2px #6f47aa;
    box-shadow: 0 0 6px -2px #6f47aa;
}

.tw-theme--dark .side-nav__overlay-wrapper, .tw-theme--dark .side-nav__theme-wrapper {
    background-color: #0a0a0a;
}

.tw-theme--dark .side-nav-card__link {
    background: #191919;
}

.tw-theme--dark .side-nav-card__link:hover {
    background: #484848;
}

.tw-theme--dark .side-nav-card__metadata {
    color: rgba(204, 204, 204, 0.6313725490196078);
}

.tw-theme--dark .side-nav-card__title {
    color: #ccc;
}

.tw-theme--dark .tw-input {
    border: 1px solid #424242;
    background: #0c0c0c;
    color: #ccc;
}

/* DASHBOARD */

.subdash-feature {
    background: #404040;
}

.subdash-feature-header {
    background: #282828;
    border-bottom: 1px solid #484848;
}

.subdash-feature-header__desc {
    color: #ababab;
}

.subdash-feature-header--sub {
    color: #ccc;
}

.subdash-feature-row__title {
    color: #ccc;
}

.subdash-feature-header__back:hover {
    background-color: #151515;
}

.top-nav__logo svg path, .top-nav__overflow svg path {
    fill: #6f47aa;
}

.theme--dark .vod-vertical-nav {
    background: #050505;
    border-right: 1px solid #7a4eaf;
}


/*	Scroll Bar Chrome */
*::-webkit-scrollbar{
    width:14px !important;
}
*::-webkit-scrollbar-thumb{
    min-height:35px !important;
    background: #424242 !important;
    border: none !important;
    border-radius: 0px !important;
}
*::-webkit-scrollbar-track-piece{
    background-color: #212121 !important;
    border: none !important;
}
/* Copyright */
li.Footer-item.Footer-copyright.copyright:after {
  content: " | Dark Style by @DearVooDoo"
}
  
}
