css:
@-moz-document domain("userstyles.org") {
 #navigation {
    background-color: #f5f7fa;
    min-width: 220px;
    padding: 0 20px 100px 42px;
    margin-left: -250px;
 }
 #iframe {
    height: 100%;
    width: 1000px;
 }
 table {
  box-shadow: 0px 12px 10px grey;
  width: 915px;
 }
 h2 {
    margin-bottom: -1px;
    box-shadow: -2px 5px 15px grey;
    padding-left: 15px;
    height: 30px;
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: #2e3e49;
    color: white;
    margin-top: 50px;
    width: 900px;
 }
 li {
    border-bottom: 2px solid black;
    padding: 14px 20px;
    width: 613px;
    list-style-type: none;
    padding-left: 10px;
    box-shadow: 0px 3px 25px grey;
    text-align: center;
    margin-left: 100px;
}
 *:not(.notes_textarea):not(.text_style_page):not(.style_description_long)>a {
    color: #5273a5;
    text-decoration: none;
 }
 li:hover {
    background-color: #2e3e49;
    box-shadow: 0px 0px 40px #3498db;
 }
 *:not(.notes_textarea):not(.text_style_page):not(.style_description_long)>a:hover {
    color: white;
    text-shadow: 0px 1px blue;
 }
 *:not(.notes_textarea):not(.text_style_page):not(.style_description_long)>a li:hover {
    color: white;
    text-shadow: 0px 1px blue;
 }
 .statistic table, th, td {
    border: 1px solid grey;
    padding: 6px;
 }
 .statistic table, th, td {
    border: 1px solid grey;
    padding: 6px;
    background-color: ghostwhite;
 }
 td:hover {
 }
 th:hover {
    background-color: #2e3e49;
    border-top: none;
    border-left: none;
    color: white;
 }
/* took ideas from RaitaroH - https://userstyles.org/styles/148112/userstyles-deepdark*/
 .author-styles a[href*="edit"], .author-styles a[href*="delete"], .author-styles a[href*="stats"]
 {
    visibility: hidden;
    position: relative;
    width: 24px !important;
    height: 24px !important;
    display: inline-block;
 }
 .author-styles a[href*="edit"]:after, .author-styles a[href*="delete"]:after, .author-styles a[href*="stats"]:after
 {
    visibility: visible;
    position: absolute;
    top: 0;
    left: 0;
    width: 24px !important;
    height: 24px !important;
    display: inline-block;
 }
 .author-styles a[href*='edit']:after {
    content: url(http://resizeimage.net/mypic/rfvI93pGgXpaoUZP/GfFHh/if_file_edit_48763.png);
 }
 .author-styles a[href*='delete']:after {
    content: url(http://resizeimage.net/mypic/baGvrpDyunsTiGLg/Oa1BA/if_file_delete_48762.png);
 }
  .author-styles a[href*='stats']:after {
    content: url(http://icons.iconarchive.com/icons/double-j-design/super-mono-3d/24/stats-icon.png);
 }
 .style-warning-icon {
    vertical-align: bottom;
    cursor: pointer;
    width: 21px;
    height: 21px;
 }
 .author-styles a[href*="edit"]:hover, .author-styles a[href*="delete"]:hover, .author-styles a[href*="stats"]:hover, .style-warning-icon:hover {
    filter: brightness(0%);
 }
/* feedback section */
}
@-moz-document domain("forum.userstyles.org") {
 #Panel .FilterMenu li:first-child, .PanelInfo li:first-child {
    border-top: 1px solid #ddd;
    border-top: 1px solid rgba(0, 0, 0, 0.1);
    width: 160px;
 }
 #Panel .FilterMenu li, .PanelInfo li {
    border-bottom: 1px solid #ddd;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    padding: 6px 18px;
    width: 160px;
 }
 ol, ul {
    list-style: none;
    margin-left: -99px;
 }
li {
    border-bottom: none;
    padding: none;
    width: 613px;
    list-style-type: none;
    padding-left: 10px;
    box-shadow: none;
    text-align: center;
    margin-left: 100px;
 }
 .DataList .Item, .NarrowList .Item {
    margin-left: 120px;
 }
 *:not(.notes_textarea):not(.text_style_page):not(.style_description_long)>a {
    color: black;
    text-decoration: none;
 }
 li:hover {
    background-color: #38abe3;
    box-shadow: 0px 0px 40px #3498db;
 }
}
