css:
@-moz-document domain("switchbrew.org") {
body {
    background-color: #549FFF;
}
#mw-head {
	/*background-image: linear-gradient(#003E8E 30px, #549FFF 100%);*/
    background-color: #549FFF;
}
.mw-body {
	background-color: #ABD0FF;
    border: 2px solid #2142A5;
    border-top: 0px;
    border-radius: 0px 20px 10px 10px;
}
.mw-wiki-logo {
    background-image: url(https://data.virtual-world-v2.fr/autres/SWITCHBREW.svg);
    margin-top: 10px;
}
div.vectorTabs ul li {
	background-image: linear-gradient(#60c1ff, #ABD0FF);
}
div.vectorTabs li.selected {
	background-image: linear-gradient(#a0daff, #ABD0FF);
}
div.body ul li a {
	color: white !important;
}
div.body ul li a:visited {
	color: #ededed !important;
}
div.body {
	margin: 0px !important;
}
div.portal {
	margin: 0px !important;
}
#p-navigation {
    border-style: solid;
    border-width: 2px 2px 0px 2px;
    padding: 4px 8px 4px 8px !important;
    border-radius: 10px 10px 0px 0px;
    background-color: #003E8E;
    border-color: #00306E;
}
#p-tb {
    border-style: solid;
    border-width: 0px 2px 2px 2px;
    padding: 4px 8px 4px 8px !important;
    border-radius: 0px 0px 10px 10px;
    background-color: #003E8E;
    border-color: #00306E;
}
#p-tb-label {
    text-align: center;
    padding: 3px !important;
    font-size: 16px !important;
    color: #fff !important;
}
#p-navigation-label {
    display: block !important;
    text-align: center;
    padding: 3px !important;
    font-size: 16px !important;
    color: #fff !important;
    border-bottom: 1px solid #dcdcdc !important;
}
#p-personal {
    left: inherit !important;
    bottom: inherit !important;
    right: inherit !important;
    top: inherit !important;
    border-style: solid;
    border-width: 0px 0px 1px 1px;
    padding: 8px;
    background-color: #dddddd;
    border-bottom-left-radius: 10px;
}
#p-personal ul {
	padding: 0px;
}
#pt-userpage,
#pt-mytalk,
#pt-preferences,
#pt-watchlist,
#pt-mycontris,
#pt-logout,
#pt-createaccount,
#pt-login {
	margin: 0px 4px 0px 4px !important;
}

div.noprint {
	background-color: #549FFF !important;
	background-image: none !important;
}

html {
    background-color: #549FFF !important;
    background-image: none;
}

#content {
	margin-right: 10px;
    margin-top: 0px;
}

#p-namespaces ul li span, #p-views ul li span, #p-cactions ul li span {
    background-image: linear-gradient(to bottom, #7db9e8 0%,#2142A5 100%);
	background-size: 2px 46px;
}

div.vectorTabs {
	background-image: linear-gradient(to bottom, #7db9e8 0%,#2142A5 100%);
	background-size: 2px 46px;
    padding-left: 2px;
}

#footer {
  margin: 20px;
  margin-left: 186px !important;
  background-color: #b7d6ff;
  border: 2px solid #2142A5;
  border-radius: 0px 0px 10px 10px;
  border-top: none;
}

#footer-poweredbyico {
  padding: 0px !important;
  margin: 0px !important;
}

#p-cactions {
  background-image: linear-gradient(#60c1ff, #ABD0FF);
}

h3#p-cactions-label {
  background-image: linear-gradient(to bottom, #7db9e8 0%,#2142A5 100%) !important;
	background-size: 2px 46px;
}
}
