@-moz-document domain("forum.blockland.us") {
body{
    background-image: url("https://imgur.com/fHhQEoX.jpg") !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed;
	padding-top:16px !important;
	margin:0 auto !important;
}
/* Normal, standard links. */
a:link, a:visited {
    color: #6f574f !important;
}
a:hover {
    color: #907d76 !important;
    text-decoration: underline !important;
}
 
/* Navigation links - for the link tree. */
.nav, .nav:link, .nav:visited
{
    color: #fff !important;
}
a.nav:hover
{
    color: #fff5e0!important;
    text-decoration: underline;
}
 
/* By default (td, body..) use Verdana in black. */
 
html body table tbody tr td center div[style="margin-top:10px; background-color:#AFC6DB; border:solid 10px #AFC6DB;"] {
    display: none;
}
/* Alternating backgrounds... */
 
.windowbg {
    background-color: #c3c6cc !important;
}
/* Titles - such as table headers. */
 
body>table:not(.tborder)>tbody>tr>td>span.smalltext, div.smalltext{
	color: white !important;
}
body>table:not(.tborder)>tbody>tr>td>span.smalltext a:link, body>table:not(.tborder)>tbody>tr>td>span.smalltext a:visited{
	color: white !important;
}
body>table:not(.tborder)>tbody>tr>td>span.smalltext a:hover{
	color: white !important;
}

.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg a:link,
.titlebg a:visited,
.titlebg2,
tr.titlebg2 th,
tr.titlebg2 td,
.titlebg2 a:link,
.titlebg2 a:visited {
    color: #ffffff !important;
    background-color: #7f8590 !important;
}
.tborder {
    border: 1px solid #000;
    background-color: white !important;
}
.hrcolor {
    height: 1px;
    border: 0;
    color: #6f574f;
    background-color: #6f574f;
}
/* The category headers, page indexes, and such things. */
 
.catbg3 {
    font-weight: bold;
    background-color: #fff5e0;
    color: #FFF;
}
.catbg {
    background-color: #7f8590 !important;
    background-image: url('https://imgur.com/hfpNdCf.gif') !important;
}
.catbg a:visited,
.catbg a:link {
    color: #FFF !important;
}
.catbg a:hover {
    color: #fff9dc !important;
}
body>table.tborder>tbody>tr,
body>table:not(.tborder)>tbody>tr,
body>table:not(.tborder)>tbody>tr>td {
    background-color: transparent !important;
}
.logoLink {
    width: 0 !important;
    height: 50px !important;
    padding-right: 400px !important;
    background-image: url("https://imgur.com/zH2f9J3.png") !important;
}
.quote {
    background-color: #e8e8e8 !important;
}
input,
textarea {
    background-color: #e8e8e8 !important;
}
select {
    background-color: #e8e8e8 !important;
}
input[type="submit"] {
    background-color: #c3c6cc !important;
}
