css:
@-moz-document domain('dnd5e.wikia.com'), domain('dnd5e.fandom.com') {
  /* writing color */
  body, h1.page-header__title {
    color: #b7b7b7 !important;
  }
  /* background */
  .wds-global-navigation__search-suggestions{
    background: #111;
  }
  .WikiaPageBackground {
    background: #111 !important;
  }
  .wds-global-navigation__wikis-menu {
    background: gray;
  }
  /* header */
  .wds-global-navigation, .wds-global-navigation__logo, .wds-global-navigation__link, .wds-global-navigation__dropdown-content, .wds-dropdown__content .wds-list.wds-is-linked > li, .wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list::after, .wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list::before, .wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list::after, wds-global-navigation__wikis-menu, .wds-global-navigation__search, .wds-global-navigation__search-label, .wds-global-navigation__search-input, .wds-global-navigation__account-menu, .wds-global-navigation__wikis-menu{
    background: gray !important;
  }
  .wds-global-navigation__start-a-wiki {
    display: none;
  }
  .WikiaSiteWrapper {
    background: #111;
  }
  /* background of the site */
  body.background-dynamic.skin-oasis::before, body.background-dynamic.skin-oasis::after {
    background: #111;
  }
  /* comment section */
  .SpeechBubble .speech-bubble-message, .WikiaArticleComments .MiniEditorWrapper .editarea textarea, .SpeechBubble.even .speech-bubble-message{
    background: #111;
  }
 /* tables */
  table.wikitable, .article-table {
    background-color: #252525;
    color: #b5b5b5;
 }
  table.wikitable>tr>th, table.wikitable>*>tr>th, .article-table>*>tr>th {
    background-color: #404040;
 }
 a {
    color: #e49122;
 }
  /* that annoying bar at the bottom */
 .wikia-bar, .wikia-bar-anon, #WikiaBar {
    display: none;
 }
 footer.wds-global-footer {
    display: none;
 }
 /* that annoying video at the top */
 .featured-video__wrapper {
    display: none;
 }
}
