css:
@-moz-document domain("forum.ss13.ru"), domain("hub.ss13.ru") {
html, body {
    background: /*[[background]]*/; /* Тут можно поменять цвет фона или поставить туда ваще картинку. Прим: background: url('https://hujpizda.com/huj.jpg'); */
    color: /*[[font-color]]*/; /* Тут меняется цвет текста */
}

a {
    color: /*[[a-color]]*/; /* Большинство ссылок */
}

.desc.lighter, .desc.lighter.blend_links a, .desc, .desc.blend_links a, p.posted_info, #primary_nav a, .ipsType_pagedesc, .ipsType_pagetitle, .ipsType_subtitle, .pagination.no_pages span, .topic_buttons li.non_button a, .edit, .popupInner h3, .maintitle, .ipsSideBlock h3, .general_box h3, #user_latest_status>div, .custom_fields .ft {
    color: /*[[font-color]]*/ !important; /* Ну а тут тоже цвет */
}

#board_stats .value, #ipsDebug_footer, .breadcrumb a, .custom_fields .fc, .breadcrumb {
    color: /*[[font-color]]*/; /* И тут */
}

.ipsSideBlock, .general_box, .statistics, .ipsVerticalTabbed_tabs li, .ipsHeaderMenu, .ipsLayout_smallleft.ipsLayout_withleft, #footer_utilities, .ipsUserPhoto, .post_wrap, .ipsBox_container, .bar, .header, .ipsBox_new, .popupInner, .maintitle, .ipsSideBlock h3, .general_box h3, table.ipb_table td, #primary_nav, .ipbmenu_content, .ipb_autocomplete, .ipbmenu_content li, .ipb_autocomplete li, .ipsForm_submit {
    background: /*[[bg-color]]*/ !important; /* Тут фон */
}

.ipsButton_secondary, .topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton, .popupInner h3, .reputation.zero, .input_submit, .pagination .pages li.active, .status_update, .topic_buttons li.non_button a, #user_latest_status>div, .message, .ipsSideMenu ul li.active a {
	background: /*[[fg-color]]*/ !important; /* Еще фон */
}

/* Все что дальше лучше не трогать, если не знаете че делаете */

.block_list>li, .ipsSideMenu ul li, .ipsSideMenu ul {
    border-bottom: 1px solid /*[[fg-color]]*/;
    border-top: 1px solid /*[[font-color]]*/;
}

.ipsUserPhoto {
    border: 1px solid /*[[fg-color]]*/;
}

#content {
	background: hsla(0, 0%, 23%, 0.3) !important;
        box-shadow: -2px -30px 91px /*[[bg-color]]*/;
}

.progress_bar span {
	background: #777;  /*Три семерки с дурой выпил одной...*/
}

.input_submit:hover {
	background: /*[[bg-color]]*/;
}

#board_index #categories {
    padding-right: 5px;
    padding-left: 5px;
}

#primary_nav .active a {
    box-shadow: 0 0 0px #000 inset;
}

table.ipb_table td {
    border-top: 1px solid /*[[fg-color]]*/;
}

.maintitle, table.ipb_table {
    -webkit-box-shadow: 0 4px 10px #000;
}

#primary_nav {
    padding: 0px 0px;
    border-radius: 0 !important;
}

}
