css:
@-moz-document domain("stud42.fr") {
:root {
    --friend: #006b60;
    --random: #27416b;
    --finder: #b57442;
    --itsame: #3686A7;
    --pooler: #64382f;
}
html,
body,
div[disconnected],
.page-container,
.page-container [appContent="cluster"] .head,
.page-container [appContent="friends"] .head {
    background-color: #181818;
    border-color: transparent;
}

header.row,
.row {
    background: transparent;
    color: white;
}

meta[name="theme-color"] {
    content: '#111';
}

body > header #news {
    background: #181818;
    border: none;
}

.cursus_info,
.page-container .main-content {
    background-color: #181818;
}
.form-control {
    background-color: #151515;
    border-color: transparent;
}
.form-control:focus {
    background-color: #151515;
}


.page-container .sidebar-menu {
    background: #1c1c1c;
}
.page-container .sidebar-menu li[active] {
    background-color: #181818;
}
.page-container .sidebar-menu li:hover {
    background-color: #151515;
}
.page-container .sidebar-menu li[disabled]:hover {
    background-color: #191919;
}

.page-container [appContent="cluster"] .table,
.page-container [appContent="friends"] .table {
    background: #181818;
    color: white;
}
.modal-content {
    background-color: #181818;
    box-shadow: 10px 10px 30px #000;
}

#infoModal .modal-body,
#infoModal .modal-body div.row > div,
#infoModal .modal-body #hoverBlueData,
.modal-footer {
    background: transparent;
    color: white;
}
#hoverBlueData {
    background-color: transparent;
}
.btn-simple {
    color: white;
}
.table table td {
    border-right: none !important;
    border-bottom: none !important;
}

.page-container [appContent="cluster"] .table table td:first-of-type,
.page-container [appContent="cluster"] .table table td:last-of-type,
.page-container [appContent="friends"] .table table td:first-of-type,
.page-container [appContent="friends"] .table table td:last-of-type {
    background: #0000 !important;
}

.page-container [appContent="cluster"] .head,
.page-container [appContent="friends"] .head {
    border: none !important;
}

.page-container [appContent="cluster"] .table table td:empty {
    background-color: #161616;
}

.page-container [appContent="cluster"] .table table td[connected],
.page-container [appContent="friends"] .table table td[connected] {
    background-color: var(--random);
    color: #ddd;
}
.page-container [appContent="cluster"] .table table td[connected][me],
.page-container [appContent="friends"] .table table td[connected][me] {
    background-color: var(--itsame);
    color: #ddd;
}
.page-container [appContent="cluster"] .table table.finder td.finder,
.page-container [appContent="friends"] .table table.finder td.finder {
    background-color: var(--finder);
    color: #ddd;
}
.page-container [appContent="cluster"] .table table td[connected][pooler],
.page-container [appContent="friends"] .table table td[connected][pooler] {
    background-color: var(--pooler);
    color: #ddd;
}
.page-container [appContent="cluster"] .table table td[connected][friend],
.page-container [appContent="friends"] .table table td[connected][friend] {
    background-color: var(--friend);
    color: #ddd;
}

.popover {
    color: #cccccc;
    background-color: #181818;
}

.popover h6 {
    display: none;
}

#search,
#addFriend {
    border-color: transparent;
    background-color: #222;
    color: #cccccc;
}

.autocomplete-suggestions {
    border: solid #000 0px;
    background-color: #222;
    color: #cccccc;
}
.autocomplete-suggestion:hover,
.autocomplete-suggestion:active,
.autocomplete-selected {
    background-color: #181818;
}

span.title {
    border-color: transparent;
}

.thumber,
.page-container [appContent="cluster"] .thumber .thumbs,
.page-container [appContent="friends"] .thumber .thumbs,
.page-container [appContent="cluster"] .list-thumber,
.page-container [appContent="friends"] .list-thumber,
.thumber,
.list-thumber {
    background-color: #151515;
    color: white;
    border-color: #222;
}

.page-container [appContent="cluster"] .list-thumber .thumbs .friendPicture span,
.page-container [appContent="friends"] .list-thumber .thumbs .friendPicture span,
.page-container [appContent="cluster"] .thumber .thumbs .friendPicture span,
.page-container [appContent="friends"] .thumber .thumbs .friendPicture span {
    border: 5px solid #111;
}

.main-content > .row {
    background-color: #181818;
}
tspan {
    fill: #cccccc !important;
}

.header_panel {
    background-image: url("https://signin.intra.42.fr/assets/background_login-a4e0666f73c02f025f590b474b394fd86e1cae20e95261a6e4862c2d0faa1b04.jpg") !important;
}
.page-container [appContent="cluster"] .table table td,
.page-container [appContent="friends"] .table table td {
    color: #666;
}

#highcharts-4 .highcharts-graph {
    stroke: #fff;
}
.highcharts-background,
.highcharts-label-box.highcharts-tooltip-box {
    fill: #181818;
}
.thumber {
    display: flex;
    flex-wrap: wrap;
}
.thumbs {
    flex-grow: 1;
    min-width: 200px;
}
}
