css:
@-moz-document domain("lmaobox.net") {
.ItemDiscussion, .ItemComment {
    background: rgb(54, 57, 62);
}
.Message {
    color: #ffffffb3;
}
h1, h2, h3 {
    line-height: 20px;
}
#Frame, body {
    background-color: rgb(32, 34, 37);
}
input.DateBox, input.InputBox, input.SmallInput, textarea {
    background-color: rgb(37, 39, 42);
    border: 0px solid rgb(32, 34, 37)
}
input.SmallInput:focus, input.InputBox:focus, textarea:focus {
    background-color: rgb(37, 39, 42);
    box-shadow: none;
    border-color: rgb(37, 39, 42);
    border: 1px solid #000
}
.EditCommentForm textarea.TextBox, .CommentForm textarea.TextBox {
    min-height: 75px;
    color: rgb(192, 193, 195);
}
h1, h2, h3, h4, h5, h6 {
    color: #ffffffb3;
}
.HomepageTitle, .PageTitle h1 {
    background: rgb(47, 49, 54);
}
a:hover, a:focus {
    color: rgb(52, 152, 219);
}
a {
    color: rgb(52, 152, 219);
}
.SpIgnoreList, .SpWhosOnline, .SpDelete, .SpBan, .SpWarn, .SpWarnings, .SpActivity, .SpPublicActivities, .SpModeratorActivities, .SpMod, .SpAdminActivities, .SpUnansweredQuestions, .SpQuote, .SpSignatures, .SpThumbnail, .SpPicture, .SpPhoto, .SpPreferences, .SpPassword, .SpEdit, .SpInfraction, .SpActivity, .SpAllCategories, .SpMyDrafts, .SpDashboard, .SpOptions, .SpCog.SpBookmarks, .SpInbox, .SpNotifications, .SpEditProfile, .SpMessage, .SpMyDiscussions, .SpDiscussions, .SpComments {
    display: inline-block;
    min-height: 14px;
    min-width: 14px;
    background: url('https://i.imgur.com/z33EmzU.png') 0 0 no-repeat transparent;
    background-position: 0;
    /* If anyone knows how to force remove the above line, please say so. Otherwise I'm just gonna have to manually re-add background-position to every single button. */
}
.SpPreferences, .SpNotifications {
    background-position: -174px -101px !important;
}
.SpDashboard, .SpOptions, .SpCog {
    background-position: -221px -53px !important;
}
.SpAllCategories {
    background-position: -150px -78px !important;
}
.SpDiscussions {
    background-position: -246px -78px !important;
}
.SpActivity, .SpPublicActivities {
    background-position: -222px -101px !important;
}
.SpMyDiscussions {
    background-position: -342px -30px !important;
}
.SpMyDrafts {
    background-position: -270px -78px !important;
}
.SpEditProfile {
    background-position: -245px -29px !important;
}
.SpComments {
    background-position: -174px -78px !important;
}
.MenuItems a:hover {
    background-color: #bdbfee;
    /*If you see this, I forgot to change this.*/
}
.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {
    color: #ffffffb3 !Important;
    padding: 3px 15px;
    line-height: 18px;
    background-color: rgb(41, 43, 47);
}
.MeBox.FlyoutRight .Flyout {
    background-color: rgb(41, 43, 47);
}
.MenuItems {
    border: 1px solid rgb(36, 38, 42);
}
.MeMenu .SpBookmarks {
    background-position: -80px -245px !important;
}
.FlyoutMenu {
    color: #ffffffb3;
}
input.DateBox, input.InputBox, input.SmallInput, textarea {
    color: rgb(123, 125, 129);
}
.navbar-inverse .navbar-inner {
    background-image: none;
    background-color: #0000;
    border: none;
    border-radius: 0
}
.navbar-inverse .nav .active > a, .navbar-inverse .nav .active > a:hover, .navbar-inverse .nav .active > a:focus {
    background-color: rgb(66, 70, 77) !important;
}
.navbar-inverse .brand:hover, .navbar-inverse .nav > li > a:hover, .navbar-inverse .brand:focus, .navbar-inverse .nav > li > a:focus {
    color: #ffffff;
    background-color: rgb(53, 55, 61);
}
.DataList .ItemDiscussion {
    background: rgb(47, 49, 54);
}
li.Announcement {
    background-color: rgb(20, 21, 24) !important;
}
.HasNew {
    background: rgb(221, 90, 85);
}
.Tag {
    background: #000;
}
body.Profile.EditMode {
    font-weight: normal;
    color: rgb(108, 112, 123);
}
.Warnings {
    text-align: center;
    background-image: none;
    text-transform: none;
    color: rgb(170, 39, 39);
}
.NoWarning {
    text-align: center;
    background-image: none;
    text-transform: none;
    color: rgb(108, 112, 123) !important;
}
.btn, .Button, .NavButton, .SocialIcon, .PhotoWrapLarge .ChangePicture, .ActionBlock .React {
    line-height: 1.1;
}
.NavButton {
    text-shadow: none
}
body {
    color: rgb(163, 166, 169);
}
.OptionsTitle {
    background: url("https://i.imgur.com/YrXe0FK.png") no-repeat 0 -101px;
}
.MenuItems {
    background: #292b2f;
}
div.Popup .Body {
    background: rgb(41, 43, 47);
}
.Preview {
    background: rgb(54, 57, 62);
}
select {
    background-color: rgb(114, 137, 218);
    color: rgb(250, 251, 253) !important;
    border:none
}
.FlyoutMenu a {
	color: rgb(52, 152, 219);
}
/*[[hide_image]]*/
/*[[avatar_shape]]*/
}
