css:
@-moz-document domain("practitest.com") {
/* PractiTest compact styles by Vsevolod Strigunov */

/* Top menu */
body {padding-top: 27px;}

.row {margin:0;}

.navbar {min-height:0;}

.navbar-brand {padding:1px 15px;}

.pt-app-frame-navbar .nav>li>a {padding: 5px 10px 5px 10px;color: #dadada;}

.pt-app-frame-navbar .nav>li:first-child>a {padding: 5px 20px 5px;}

.pt-app-frame-navbar .nav>li.setting-dropdown>a {padding:5px 15px;}

.pt-app-frame-navbar .navbar-right>li:last-child>a {padding: 5px 30px 5px 20px;}

.navbar-inverse .navbar-nav>li>a:hover,
.navbar-inverse .navbar-nav>li>a:focus {text-decoration:underline;}

.navbar-nav>li>a{line-height:1.2em;}

.dropdown-menu .divider {margin:0 0;}

.navbar-form {margin-top:4px;}

.pt-app-frame-navbar .stylish-input-group .form-control {padding:0 0 0 6px;height: auto;}

.pt-app-frame-navbar .stylish-input-group .input-group-addon {padding:0;}

/* subheader */
.sub-controllers {
    margin-bottom: 5px;
    padding: 4px 0 2px 0;
    border-bottom: 1px solid lightgray;
    }
    
/* elements */
.btn, .form-control {
    padding:2px 6px;
    height:auto;
    }

input[type=text],
input[type=number],
input[type=tel],
select {height:auto;}

.navbar-search #display_id {margin-bottom:3px;}

#entity_legend_ul {margin-top:4px;}

#entity_legend_ul span,
#entity_legend_ul h4 {
    margin:0;
    display:inline-block;
    line-height:20px;
    font-size: 1.1em;
    }

div#gbox_ngrid-issues3 table.ui-pg-table tr td,
div#gbox_ngrid-issues3 table.ui-jqgrid-htable tr th,
div#pg_ngrid-instances_toppager table.ui-pg-table tr td,
div#pg_ngrid-pager table.ui-pg-table tr td,
div.ui-jqgrid-hbox table.ui-jqgrid-htable tr th,
.ui-widget-header {font-weight:300;}

.ui-jqgrid .ui-jqgrid-pager .ui-pg-div {padding-left:10px!important;}

.table>thead>tr>th,
.table>thead>tr>td,
.table>tfoot>tr>th,
.table>tfoot>tr>td {padding: 4px 8px;}

.num_of_rows_css {height:auto;}

.num_of_rows_css select,
#grid_table_js {margin-top:0;}

ul.jqtree-tree li.jqtree-selected>div,
ul.jqtree-tree li.jqtree-selected>div:hover {
    background-color: #d2e0e8;
    background-image: none;
    text-shadow: none;
    border-radius: 3px;
}

legend {margin-bottom: 10px;font-size: 14px;}

legend + .row {margin-bottom:20px;}

.row + legend {margin-top:20px;}

.common_entity_form label {font-size:12px;}

#instances .run_now_btn_js,
#test_status #pass_all_steps {color: white;
    background-color: #449d44;
    border-color: #398439;
    font-family: Verdana,Arial,sans-serif!important;
}

#test_status #pass_all_steps {padding: 2px 6px;border-radius: 3px;}

#test_status #pass_all_steps a {color:#fff; }

img.test_legend_img#test_legend_run {display:none;}

.navbar-text {
    margin-top: 5px;
    margin-bottom: 5px;
    }

#run_bar_css {
    font-size: 14px;
    margin-top: 5px;
    }

#test_step_container .affix,
#test_run_content .affix {
    top: 26px;
    padding: 3px 8px!important;
    left:0;
    right:0;
}

/*footer*/
#footer {
    width: 100%;
    height: auto;
    background-color: #f8f8f8;
    margin-top: 20px;
    padding: 2px 0;
    border-top: 1px solid lightgray;
    font-size:0.7em;
}
#footer img[alt*="Practitest"] {height:14px;margin:0;}

#footer .container ul:not([class]) {margin-bottom:0;}

/* running steps*/
#test_run_content .entry_grad {background-image:none;}

#test_run_content form.edit_step_run {display:flex;}

#test_run_content .row {
    margin:0;
    display: flex;
    padding-top:5px;
    padding-bottom:5px;
}

#test_run_content .row:not(.several_resize_texts):not(:last-child) {flex-direction: column;}

#test_run_content .row:last-child {min-width: 240px!important;max-width: 240px!important;}

#test_run_content .row:last-child > div:first-child {display:flex; flex-direction:column; width:100%;}

#test_run_content .row:last-child > div:last-child {width:0; padding:0;}

#test_run_content .row:last-child > div:first-child > div{margin:0!important;}

#test_run_content .entry_grad ~ .row:not(:last-child) {border-right:1px solid #888;}

#test_run_content .entry_grad + .row h4 {font-size:14px; margin:0; padding:3px;}

#test_run_content .entry_grad + .row > div {
    padding-left:0;
    width: 250px;
    float:none!important;
}

#test_run_content .entry_grad + .row {padding-lefT:10px;}

#test_run_content .entry_grad + .row  div {float:none!important; position:static;}

#test_run_content .entry_grad + .row .step_actions {box-shadow: 2px 2px 2px #333;border-radius: 3px;}

#test_run_content .entry_grad + .row .step_actions a {padding: 2px 5px;}

#test_run_content .row.several_resize_texts >div {width:33%;}

.actual_result textarea {width:100%;height:100%;}

#test_run_content div.step_linking_issues {
    margin: 0 0 0px 0;
    background-color: #f3f3f3;
    padding: 0px 10px;
    display: flex;
    justify-content: space-between;
    background-color: rgba(0,0,0,0.1);
}
#test_run_content div.step_linking_issues .linking_issues_actions {
    order: 1;
    border-left: 1px solid #bbb;
    padding-left: 10px;
    position: relative;
    padding: 0 10px;
    margin-right: -10px;
}

#test_run_content div.linking_issues_actions form > br {display:none;}

#test_run_content div.step_linking_issues .linking_issues_actions + div:not(.class) h4,
#test_run_content div.step_linking_issues .linking_issues_actions + div:not(.class) .linked_issues_js {
    margin:0;
    padding:5px 0;
    font-size:14px;
    display:inline-block;
}
#test_run_content div.step_linking_issues .linking_issues_actions + div:not(.class) .linked_issues_js p {margin:0px 5px; display:inline-block;} 

#test_run_content div.step_linking_issues .linking_issues_actions + div:not(.class) .linked_issues_js p a {color:#800;/*#B94A48;*/ font-weight:bold;}

#test_run_content div.step_linking_issues .linking_issues_actions form  {margin:3px 0 2px 0;}

#description_expected {display:flex;justify-content:space-between;}

#description_expected > div{width:auto!important;}

#description_expected > div:first-child {margin-left:240px;}

.add-attachments div.single_attachment a{word-break: break-word;}

.step_run_class {
    background:none;
    border-bottom: 1px solid gray;
    position:relative;
}

.container-fluid {
    padding-left: 0px;
    padding-right: 0px;
}
#test_run_content .entry_grad {height:0;}

/*passed*/
.step_run_class h4[style="color: #468847"],
.step_run_class h4.text-success,
.step_run_class h4[style="color: #468847"]:after,
.step_run_class h4.text-success:after {background-color:#bddbbd; color:#000!important;}

/*failed*/
.step_run_class h4[style="color: #b94a48"],
.step_run_class h4[style="color: #b94a48"]:after {background-color:#ec9393; color:#000!important;}

/*block*/
.step_run_class h4[style="color: #c09853"],
.step_run_class h4[style="color: #c09853"]:after {background-color:#f5daa3; color:#000!important;}

/*n/a*/
.step_run_class h4[style="color: #3a87ad"],
.step_run_class h4[style="color: #3a87ad"]:after {background-color:#ddd; color:#777!important;}

.step_run_class h4[style^="color: #"]:after {
    content: "";
    position: absolute;
    display: block;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    z-index:-1;
}

#steps_tab i.fa-list + a[href*="/sort_steps"],
a.remove_a_step_link {
    background-color: #d25555;
    color: #fff;
    padding: 2px 5px;
    border-radius: 3px;
    box-shadow: 2px 2px 4px #444;
}

#step_designs > br + br {display:none;}

#step_designs > #test_step_container {margin-top:8px;}

#step_designs #description_expected:before {content:"Step name";margin-left:50px;}

#step_designs #description_expected.affix-top {left: 0;}

#step_designs #description_expected div:last-child {margin-right:450px;}

#step_designs .step_id {
    margin-top: 0;
    padding: 0;
    display:flex;
    flex:1;
    align-items: stretch;
}

#step_designs .step_id + div {border-left: 1px solid #BEBEBE;}

#step_designs .step_id > * {height: 100%;}

#step_designs .step_id > table {
    max-width: 200px!important;
    min-width: 200px!important;
}

#step_designs .step_id > table tr {
    display: flex;
    flex-direction: row;
    height: 100%;
}
#step_designs .step_id > table tr td {width:auto!important;}

#step_designs .step_id > table tr td h4 {
    margin:5px 0 0 0;
    word-break: break-word;
}

#step_designs .step_id > table tr td textarea {
    height:100%!important;
    width:100%!important;
    min-height: 100px; 
    margin:0;
    border-radius:0;
    border-style:none none none solid;
}

#step_designs  textarea, #step_designs  .CodeMirror {margin:0;border-radius:0;}

.several_resize_texts {padding: 5px;flex:1;}

#step_designs .several_resize_texts {padding: 0!important;}

#step_designs .step_id .several_resize_texts > div {padding: 0px;}

#step_designs .step_id .several_resize_texts .editor-toolbar {padding: 0 5px;}

#step_designs .step_id .several_resize_texts .editor-toolbar:before,
#step_designs .step_id .several_resize_texts .editor-toolbar:after {margin: 0px !important;}

#step_designs .step_id .several_resize_texts .editor-toolbar a {width: 24px;height: 20px;}

#step_designs .step_id .several_resize_texts .editor-toolbar a:before {line-height: 20px;}

#step_designs .step_id .several_resize_texts .CodeMirror {
    padding:4px;
    border-style: none none none solid;
    border-left-color:#BEBEBE;
}

#step_designs .step_design .row_semi_inverted + div .row .col-sm-3,
#step_designs .step_design .changedInput .row .col-sm-3 {
    max-width: 200px;
    min-width: 200px;
}
#step_designs .step_design .row_semi_inverted + div .row .col-sm-3 div{margin-top:5px;}

#step_designs .step_design .row_semi_inverted + div .row .col-sm-3 div tr,
#step_designs .step_design .changedInput .row .col-sm-3 div tr{
    display: flex;
    flex-direction: column-reverse;
}

#step_designs .step_design .row_semi_inverted + div .row .col-sm-3 div td,
#step_designs .step_design .changedInput .row .col-sm-3 div td {
    width:100%;
    text-align:left;
    max-width: unset!important;
}

#step_designs .step_design .row_semi_inverted + div .row .col-sm-3 div td .well-sm,
#step_designs .step_design .changedInput .row .col-sm-3 div td .well-sm {
    padding:5px;
    margin:5px 0;
    border:2px solid #c00;
    background-color:#ffd7d7;
}

#step_designs .single_attachment img[alt="Attachment"] {display:block;}
#step_designs i.fa-times[alt="Remove Attachment"],
#step_designs i.fa-times[title="Remove Attachment"] {color:red;}
div#run_save_print {
    position: fixed;
    z-index: 9999;
    padding: 0;
    top: 0;
    margin-top: 26px!important;
    right: 250px;
}
div#run_save_print button {
    background-color: mediumseagreen;
    color: #fff;
    border: 0;
}
div#run_save_print button + button {
    border-left: 1px solid #fff;
}

.qtest_form_container {padding: 0;}
.row_semi_inverted {
    box-shadow: 0 10px 10px -10px #777 inset, 0 -10px 10px -10px #777 inset;
    background-color: #f9f9f9;
}

.step_design .row_semi_inverted + div:not([class]) .row, 
.step_design .changedInput .row {
    padding:0 10px;
    display: flex;
    justify-content: space-between;
    align-items:stretch;
}

.step_design .changedInput {border-top: 1px solid #9c9c9c;}
.step_design .editor-toolbar {display:none;}

.step_design  .description_mk, #step_designs .CodeMirror {height:100%;}

div[style*="clear:both;height: 5px"],
div[style*="clear:both;height:5px"] {height:0!important;}


#step_designs .alert {
    background-color: #ffd2c0;
    margin: 10px;
    border: 1px solid #800;
}

#step_designs .alert button.close {color: red; opacity: 1;}
}
