@-moz-document url-prefix("https://bts.ai/market/") {
 
#top-nav,
#market_asset_ids {
    display: none
}
#market {
    font-size: 8px;
    padding: 0 2px;
    margin: 0;
}
#market span.float-right {
    font-size: 8px;
}
#market span.float-right label {
    cursor: pointer;
}
input#chk-auto-refresh[type=checkbox] + label {
    color: #f00;
    font-weight: bold;
}
input#chk-auto-refresh[type=checkbox]:checked + label {
    color: #ccc;
    font-weight: normal;
}
main.container > .row th .th-inner,
main.container > .row td {
    padding: 2px !important;
}
main.container > .row .panel-heading {
    padding: 2px 5px;
    font-size: 10px;
}
main.container > .row th .th-inner {
    font-size: 12px;
}
main.container > .row .panel-body {
    margin: 0;
    padding: 0 5px;
}
main.container > .row > .col-md-6 .panel {
    margin-bottom: 5px;
}
main.container > .row .panel-body .bootstrap-table .fixed-table-container {
    height: 40vh !important;
}
