css:
@-moz-document domain("www.taobao.com"),
url("https://www.taobao.com/"),
url("http://www.taobao.com/")
{
  
  
  

.logo h1 .logo-bd {
    display: block;
    margin-left: 22px;
    padding-top: 58px;
    width: 142px;
    height: 0;
    overflow: hidden;
    text-indent: -9999px;
    background: url(https://s20.postimg.cc/l3tzr0dql/TB1_u_T8a5_ERMe_Jj_Sspi_XXb_ZLFXa-143-59.png) 0 0 no-repeat;
}

.tb-bg {
    background-image: -webkit-linear-gradient(left,#ff9000 0,#ff5000 100%);
    background-image: -o-linear-gradient(left,#ff9000 0,#ff5000 100%);
    background-image: linear-gradient(to right,#ff9000 0,#ff5000 100%);
    background-repeat: repeat-x;
    background: #f36 !important;
}

.tbh-nav h2 {
    float: left;
    width: 190px;
    text-align: center;
    font-size: 16px;
    background: #e26 !important;
}

.tbh-nav .nav-bd li.hover::before, .tbh-nav .nav-bd li:hover::before, .tbh-nav .nav-hd li.hover::before, .tbh-nav .nav-hd li:hover::before {
    content: '\E606';
    font-family: iconfont;
    color: #f36 !important;
    top: -19px;
    position: absolute;
    left: 50%;
    font-size: 12px;
    margin-left: -15px;
}

.search-bd .search-panel .search-common-panel::before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    height: 36px;
    border: 2px solid #f36 !important;
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px;
    width: 20px;
    border-right: none;
}

search-bd .search-triggers .selected {
    color: #FFF;
    font-weight: 700;
    background-image: -webkit-linear-gradient(left,#ff9000 0,#ff5000 100%);
    background-image: -o-linear-gradient(left,#ff9000 0,#ff5000 100%);
    background-image: linear-gradient(to right,#f36 0,#f36 100%);
    background-repeat: repeat-x;
    border-top-right-radius: 6px;
    border-top-left-radius: 6px;
    background: #f36 !important;
}

.search-bd .search-panel .search-common-panel {
    margin-right: 74px;
    border: 2px solid #f36 !important;
}

.search-bd .search-triggers li {
    color: #F36 !important;
}

.service-bd {
    border: 1px solid #f36 !important;
}

.service-bd li.on a {
    color: #F36 !important;
}

.h {
    color: #f36 !important;
}


.conve-cz-pop-container a.btn-recharge, a.btn-recharge {
    background: #f36 !important;
}

span.price{
    color: #f36 !important;
}

.conve_on li.selected {
    position: relative;
    border-color: #F36 !important;
}

.conve_on .conve-bd-box {
    top: 76px;
    display: block;
    border-color: #F36 !important;
}    

.tbh-shopping .list li:hover .subtitle {
    color: #f36 !important;
}

.wrap-fixed .search-bd .search-triggers::before {
    position: absolute;
    top: 0;
    left: 0;
    width: 72px;
    content: '';
    height: 36px;
    border: 2px solid #f36 !important;
}

.fixedtool a.on, .fixedtool a:hover {
    background: #f36 !important;
}

.fixedtool a.on {
    color: #fff !important;
}

.qiang-inner .yan, .qiang-inner .cur {
    font-size: 20px;
    color: #f36 !important;
}

.logo h2 a {
    display: block;
    padding-top: 33px;
    width: 80px;
    height: 0;
    text-indent: -9999px;
    overflow: hidden;
    background: url(https://s20.postimg.cc/t1t06fb9p/TB1_TGf_Mcw_MPMe_Jjy1_Xb_XXcwx_VXa-80-33.png) 0 0 no-repeat !important;
}
.qiang-inner .progress .progress-bar {
    background: #f36 !important;
}

.tbh-hotsale .item-more{
    background: #f36 !important;
}
.tbh-hotsale .list .item:hover {
    border-color: #F36 !important;
}

#J_Sale .sale-bd .item:hover {
    border: 1px solid #f36 !important;
}

#J_Sale .p4p-promo {
    color: #f36 !important;
}

#J_Sale .sale-bd .item:hover {
    border: 1px solid #f36 !important;
    z-index: 1;
}

.quality-inner .right .item:hover {
    border-color: #f36 !important;
}

.tbh-quality.special .mod-hd {
    color: #f36 !important;
}

.fixedtool .wangwang.tb-ifont {
    position: absolute;
    height: 36px;
    width: 50px;
    line-height: 36px;
    text-align: center;
    font-size: 32px;
    color: #f36 !important;
}

.tbr-result .tbr-price strong {
    color: #f36 !important;
}

.conve-bd-box.conve-btn-hover {
    background: #f36 !important;
    text-decoration: none;
    color: #fff !important;
}

.conve-bd-box .conve-btn {
    display: inline-block;
    margin: 0 10px 0 0;
    padding: 3px 10px;
    height: 21px;
    line-height: 21px;
    font-weight: 700;
    color: #fff;
    border: none;
    background: #f36 !important;
}

.tbh-tipoff a {
    color: #f36 !important;
}

.tbh-notice .notice-hd .selected {
    padding: 0;
    font-weight: 700;
    border-bottom: 2px solid #F40 !important;
}


.tbh-superbanner, .superbanner-inner .mask::after, .tbh-superbanner .mask {

    background: none !important;

}

.decorations-item {
    position: absolute;
    text-indent: -9999px;
    background: none !important;
}

body {
    background: none !important;
}

  
  
  
  
  
}
