css:
@-moz-document domain("teamwork.com") {
#topNavBarNew {
    background: #444;
}
body,
#header,
.app-header,
.w-notification__count:after {
    background-color: #46494C !important;
}
body, #header, .app-header, .app-header__base, .w-side-nav__block, .w-notification__count:after {
    background-color: #46494C !important;
}
#topLeftDashTabsNew ul li.sel a {
    background-color: #1985A1;
}
html body {
    background-color: #4C5C68 !important;
}
.sectionContent {
    background: #4C5C68;
}
h1,
h2,
h3,
h4,
h5,
h6,
p,
span,
a {
    color: #FFF;
}
body#dashboard .projectActivity h3 a.pn {
    color: #1985A1 !important;
}
.projectActivityContainer div.task {
    background-color: #4C5C68;
}
.projectActivity .h3wrap {
    background-color: #4C5C68;
}
div.taskList {
    background: #4C5C68;
}
#mainContent {
    background: #4C5C68;
}
div#mlogo {
    background: #46494C;
}
div.dashboardSidebarContent#sidebarContent #dashboardSidebarContent #yourProjects #activeProjList ul#projList {
    background: #46494C;
}
#showAllProjs,
#showInactiveProjs {
    background: #46494C;
}
td.sidebar,
div#TaskListsContent table td.projCol {
    background: #46494C;
}
#mainTable {
    background: #4C5C68;
}
.projectActivityContainer .activityDateRow .content_alignment {
    background-color: #46494c;
}
ul#statusList li div.statusText {
    background-color: #dcdcdd !important;
    color: #484848 !important;
}
ul#statusList li div.statusHeader {
    background-color: #4c5c68;
}
ul#statusList div.statusNipple {
    border-color: #d9d9d9 !important;
}
ul#statusList div.statusNipple:after,
ul#statusList div.statusNipple:before {
    border-bottom: 10px solid #dcdcdd;
}
table.dataGrid th {
    background-color: #46494c;
}
table.dataGrid tbody tr:nth-child(even) {
    background: #46494c;
}
div.moreData a {
    background-color: #777 !important;
    color: #FFF !important;
}
.moreData a i,
.w-filter-info-bar__info,
h3.tlh3 a.ql,
span.tasklist-dropdown {
    color: #FFF !important;
}
#calendar .fc-other-month {
    background-color: #46494c;
    opacity: 1;
}
#calendar .fc-other-month > *,
#calendar .calendar-task-count {
    opacity: 1;
    color: #FFF !important;
}
.fc-row .fc-content-skeleton td,
.fc-row .fc-helper-skeleton td {
    border-color: #FFF;
}
.new__main-header__secondary.is-fixed,
.w-filter-info-bar {
    background-color: #46494c;
}
body#projects ul.projectViewHolder li:hover {
    background-color: #777;
}
h3.tlh3 {
    background: #46494c !important;
    color: #FFF !important;
}
.gu-mirror.task-groupHold-wrapper .task-groupHold,
.gu-transit.task-groupHold-wrapper .task-groupHold,
.tasks--container .task-groupHold {
    border-bottom: 1px solid #f6f6f6;
    background-color: #4c5c68;
}
a.taskBubble.mine,
span.taskBubble.mine {
    background-color: #4ca12f;
}
div#pageFilter.mine {
    background-color: #c1f5c1;
    border: 1px solid #0d570d;
}
.taskList--container.board .tasks--container {
    background-color: #c5c3c6 !important;
}
.project-card,
.task-card {
    background-color: #1985a1;
}
body .sectionContent div.formatted,
body .sectionContent div.fr-view,
body .sectionContent div.redactor-editor {
    color: #FFF;
}
body .sectionContent div.formatted a,
body .sectionContent div.fr-view a,
body .sectionContent div.redactor-editor a {
    color: #4AC772 !important;
    text-decoration: none;
}
.btn-default {
    background-color: #c5c3c6 !important;
}
.main-tabs__item.sel span.main-tabs__label {
    color: #FFF;
}
rect.highcharts-background,
path {
    fill: #4c5c68;
}
.highcharts-stack-labels text tspan {
    text-shadow: none;
    fill: #dcdcdd;
}
#topLeftDashTabsNew ul li.sel a {
    background-color: #1985a1;
}
a.inlineButton,
a.inlineButton.mid {
    background: #46494c;
    border-left: none;
}
a:link,
a:visited,
#mainContent div.section div#subCats ul li.sel a {
    color: #1985a1;
}
#footer ul li a:link,
#footer ul li a:visited {
    color: #dcdcdd;
    opacity: 1;
}
ul#userStatusTimeline .commentBlock .statusColumn {
    border: 1px solid #999;
    background: #666;
    box-shadow: 1px 1px 3px #999;
}
ul#userStatusTimeline .commentBlock .statusColumn .name span {
    color: #26c0e8;
}
span.ml--small {
    color: #46494c;
}
.filters-sidebar .filters-holder,
.filters-tipped .filters-holder {
    background-color: #c5c3c6;
}
.tpd-content-relative,
.project-board__base,
.project-board {
    background: #46494c;
}
.w-switch-project__tabs-nav .is-active {
    color: #dcdcdd;
}
.w-switch-project__latest-row.is-active .w-switch-project__latest-block {
    background-color: #4c5c68;
}
.color--gray-dark {
    color: #1985a1 !important;
}
.w-switch-project__latest-number {
    background-color: #1985a1;
}
ul.tippedMenuBlue li a,
ul.tippedMenu li a,
ul.tippedMenu li a i,
h1.subTitle,
h1.subTitle span,
h2.subTitle,
h3.projectH3 span a,
h3.projectH3 span {
    color: #dcdcdd !important;
}
body#projects ul.projectViewHolder li a.zpn:link,
body#projects ul.projectViewHolder li a.zpn:visited,
h1.subTitle a,
div.msName a.msName {
    color: #26c0e8;
}
table.timeGrid th,
table.dataGrid th {
    background-color: #1985a1 !important;
}
a.addTag {
    opacity: 1;
}
table.dataGrid td,
table.timeGrid td,
span.dp,
.h3wrap h3 span.cn {
    color: #dcdcdd;
}
h3.projectH3 {
    background-color: #1985a1 !important;
}
.everything-messages-project-separator {
    background: #1985a1;
}
.everything-message-category-name a,
a.everything-message-title {
    color: #dcdcdd !important;
}
table.timeGrid tbody tr:nth-child(even) {
    background: #46494c;
}
table.timeGrid tr:hover {
    background: #555 !important;
}
.cliMainCompanyName {
    background: #1985a1;
}
table#cliPeopleListTable tbody tr:hover td {
    background-color: #555 !important;
}
div.notebookFrame {
    background: #555;
    background-image: none;
}
}
