css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.org") {

#hattrick, #hattrickNoSupporter 
{ 
	width: 98% !important; 
	min-width: 600px !important; 
	margin-left: 1% !important; 
	margin-right: 1% !important;
}

#ticker
{ 
	width: 50% !important; 
	margin-left: 50px !important; 
	margin-right: 0px !important; 
}

#shortcuts
{
        top: 5px !important;
}

#time, #online
{
        margin-top: 23px !important;
}

#page, #footer 
{ 
	width: inherit !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important; 
	position: relative !important; 
}

body
{
	background: #cccccc !important;
}
 
.subMenu 
{ 
	width: 16% !important; 
	margin-left: 0px !important; 
	margin-right: 1% !important; 
}

.mainRegular 
{ 
	width: 61% !important; 
	margin-left: 0px !important; 
	margin-right: 1% !important; 
}
 
div#sidebar 
{ 
	width: 21% !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important; 
}
 
.mainWide 
{ 
	width: 83% !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important; 
}

.subMenuConf 
{ 
	width: 29% !important; 
	margin-left: 0px !important; 
	margin-right: 0px !important;
}
 
.mainConf 
{ 
	width: 70% !important; 
	margin-left: 1% !important; 
	margin-right: 0px !important; 
}

.url
{ 
	width: 220px !important;
        max-width: 250px !important;
	overflow: hidden !important;
}

.fplLongThreadName
{ 
	width: 90% !important; 
	overflow: hidden !important;
}

.fplThreadName, .fplFolderName, .fplLongFolderName
{ 
	width: 75% !important; 
	overflow: hidden !important;
}

.subMenuBox, .sidebarBox, .ownerNotes textarea, .notebook, .gbPreviewMsg 
{ 
        width: 100% !important; 
}
 
#field 
{ 
	min-width: 590px !important; 
	width: 100% !important; 
	background: 100% !important; 
}

/* img[class="scMySupporters"] 
{ 
	background:transparent url(http://img14.imageshack.us/img14/2262/benannt.png) no-repeat !important; 
	padding:0px 0px 22px 22px !important; 
	height:0px !important; 
	width:0px !important; 
	border: 0px !important;
} */

img[title="Abonniert/In den Favoriten"]
{
        display: none !important;
}

div[class="main mainFull"]
{
       width: 99% !important;
}

.liveTabsContainer
{
       position: absolute !important;
       right: 0px !important;
}

.liveMatchContainer
{
       width: 77% !important;
}

.liveReport
{
margin-left: -10px !important;
margin-right: -10px !important;
}

.liveReportHeader
{
margin-top: -5px !important;
}

.liveEvent, td[class="date"], .liveEvent>div
{
padding-bottom: 1px !important;
padding-right: 2px !important;
padding-left: 2px !important;
}

}
