css:
@-moz-document domain("tell.kirschn.de") {
@font-face {
  font-family: 'Roboto Condensed';
  font-style: normal;
  font-weight: 300;
  src: local('Roboto Condensed Light'), local('RobotoCondensed-Light'), url(https://fonts.gstatic.com/s/robotocondensed/v16/ieVi2ZhZI2eCN5jzbjEETS9weq8-33mZGCQYbw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
body {
    height: 100%;
    display: inline-block;
    font-size: 16px;
    background: linear-gradient(to bottom right, #63002c, #5a1f66);
    margin: 0;
    background-repeat: no-repeat;
    background-attachment: fixed;
    color: #ede7ea;
}
.elBackground {
    position: absolute;
    height: 100%;
    width: 100%;
    top: 0;
    left: 0;
    z-index: -1;
}
.elHeadContainer {
    width: 100%;
    text-align: center;
    position: absolute;
    animation: fadein 2s;
    top: 4.5%;
    animation-fill-mode: forwards;
    font-family: 'Sintony', sans-serif;
    font-weight: 100;
    -webkit-font-smoothing: antialiased;
    color: #d8b6c5;
}
.elHeadContainer h1 {
    font-family: 'Roboto Condensed', sans-serif;
    font-size: 300%;
    font-weight: 300;
}
.inputElement {
    margin-left: 25%;
    margin-right: 25%;
    width: 50%;
    text-align: center;
    position: absolute;
    top: 35%;
    animation: fadeinContent 3s;
}
.inputElement textarea {
    resize: vertical;
    /* user can resize vertically, but width is fixed */
}
.btn-info {
    color: #ffffff;
    background-color: #8c2f4899;
    border-color: #8c2f48;
}
.btn-info:hover {
    color: #ffffff;
    background-color: #8c2f48e6;
    border-color: #8c2f48;
}
.btn-danger {
    margin-left: 1em;
}
.btn-primary {
    margin-right: -1em;
}
.tell {
    background: #0006;
    padding-bottom: 1.5em;
    padding-left: 1.5em;
    padding-right: 1.5em;
    border-radius: 0.5em;
}
hr {
    border-top: 1px dotted #fcb3c633;
}
#input {
    background: #69515c66;
    border: none;
    color: #f2dce6e6;
}
#copy-input {
    background: #69515c66;
    border: none;
    color: #f8e6eee6;
}
#copy-button {
    color: #f8e6eee6;
    background-color: #320117b3;
    border-color: #320117b3;
}
#modalbody {
	color: #fffc;
}
@keyframes fadein {
    from {
        opacity: 0;
        top: 2%;
    }
    to {
        opacity: 1;
        top: 17.5% - 50pt;
    }
}
@keyframes fadeinContent {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}
}
