@namespace url(http://www.w3.org/1999/xhtml);

/* * * * * *
 * Author		: inthel
 * Updated		: 2015-10-23 17:01:03
 * Link			: http://userstyles.org/styles/15635
 * Description		: A simple style redesign of the appearance and feel for trow.cc
 * Usage		: Use with the Stylish Firefox extension or import to usercss in your profile
 * * * * * */

@-moz-document url("http://trow.cc/"), url-prefix("http://trow.cc/"), url("https://trow.cc/"), url-prefix("https://trow.cc/") {
	body, input, textarea, select, #userlinks, .newslink, .formbuttonrow3, .errorwrap2, .row1, .row2, .post1, .post2, .darkrow2, .ipbtable td, .tablepad2, .pagelink, .formsubtitle7, #ucpmenu, #ucpcontent2, .quotetop, .codetop, .sqltop, .htmltop, .spoilertop, .spoilermain, .quotemain, .loginsubtitle3, .errorwrap, .minipagelink, .tablepad, .searchsubtitle, .calmonths, .calday, .tablepad3, .pagelinklast, .popupmenu-category, .popupmenu-item, .popupmenu-item-last, .rteiframe, .rtebuttonbar1, .post3, .minipagelinklast, .tablepad4, .formsubtitle14, #pmnotewrap, .pmnotefoot, .subtitle, #redirectwrap, #redirectwrap h4, .redirectfoot, .formsubtitle8, .formsubtitle15, .bar, .barc, .codemain, .loginsubtitle6, .formsubtitle4, .sqlmain, .formsubtitle5, .htmlmain, #userlinksguest, .row9, .fieldwrap, .fieldwrap h4, .divbutton a, .loginsubtitle, .loginwrap, .logintitle, .formsubtitle10, .subtitlediv, .barc2, .tablepad5, .errorwrap3, .hiddenGlobal p, .fauxbutton, .sheet th, .sheet td, #dw__editform, #edit__wrap, button.toolbutton, .loginsubtitle2, .formsubtitle21, #media__left, #media__right {
		background: #F0F0F0 !important;
	}

	.ipbtable .post1shaded, .ipbtable .post1shaded td, .ipbtable .post2shaded, .ipbtable .post2shaded td {
		background: #E3DFE7 !important;
	}

	.maintitle, .maintitle a, .maintitlecollapse, .maintitlecollapse a, .maintitle2, .maintitle3, .cpsubtitle, .cpsubtitle2, .cpsubtitle3, .nopad, .maintitle4, .maintitle4 a {
		background: #F0F0F0 !important;
		color: #000 !important;
	}

	#fo_70 .maintitle, #fo_13 .maintitle, #fo_79 .maintitle, #fo_4 .maintitle, #fo_3 .maintitle, #fo_1 .maintitle, #fo_stat .maintitle, .maintitle p a, .cpsubtitle4, #ucpcontent2 .formsubtitle, .maintitle5, #fo_idm_stats .maintitle, #fo_idm_cats .maintitle {
		background: #F0F0F0 !important;
		color: #000 !important;
		border-bottom: 0 !important;
	}

	#logostrip, .copyright, .row2 .forumdesc, .row2 .forumdesc td, .postdetails > img:first-child {
		display: none !important;
	}

	#submenu {
		background: #F0F0F0 !important;
		border-top: 1px solid #633108 !important;
	}

	.formsubtitle, .formsubtitle6 {
		background: #F0F0F0 !important;
		border-bottom: 1px solid #633108 !important;
	}

	.rteiframe2 {
		border-bottom: 2px solid #633108 !important;
	}

	.ipbtable th, .ipbtable th a {
		background: #F0F0F0 !important;
		color: #666 !important;
	}

	#ucpcontent2 th {
		border-bottom: 1px solid #633108 !important;
	}

	#gfooter {
		background: #F0F0F0 !important;
		margin-bottom: 0 !important;
	}

	#userlinks a:hover, #gfooter a:hover, .ipbtable th a:hover {
		color: #DD0000 !important;
	}

	#term_in td, #term_in input {
		background: #000 !important;
		color: #CCC!important;
	}
}
