css:
@-moz-document domain("dfcomps.ru"){
/*Дн на таймере*/
#timeholder span{
     background-color: rgba(205, 205, 205, 0) !important; 
    color: white !important;
    border-left: solid 5px rgba(205, 205, 205, 0);
}
/*-----------------*/
/*лого */

.logo img{
    border: 2px solid rgba(205, 205, 205, 1) !important;
}
/*-------*/

/*Текст в коментах*/

textarea {
color: rgba(35, 35, 35, 1) !important;
}

.add_comment {
    color: rgba(35, 35, 35, 1) !important;
       
}
/*-----*/

/* ЦВЕТ ТЕКСТА НАД ТАБЛИЦАМИ РЕЙТИНГА >_< */
div:nth-child(2) 
{

     color: rgba(205, 205, 205, 1) !important;
}

div:nth-child(1) 
{
     color: rgba(205, 205, 205, 1) !important;
}
/*-----------------------------------------------*/
  /*бэкграунд*/
div.bg{
    position:fixed;
    z-index:-1;
    top:0;
    left:0;
    width:100%;
    height:100%;
    background-size:cover;
    background-position:50%;
    background:url('http://www.igmdb.org/images/screenshots/5423.png') !important;
    /*https://a.radikal.ru/a14/1802/83/69c6d0f60d7ct.jpg*/
}
/*----------------------*/
/*Кнопка скачать дефраг*/
.button {
    background-color: rgb( 15, 15, 15) !important;
}
/*------------------------*/
  
 /*таймер*/
div.opacity_bg{
    border:1px solid white;
    width:326px;
    height:180px;
    position:absolute;
    z-index:-1;
    top:0;
    left:0;
    background:rgb( 15, 15, 15) !important;
    opacity:0.7;
}
/*-------------*/
/*шапка*/
.menu ul li {
    border-bottom: 2px solid rgba(205, 205, 205, 0) !important;
    
}

.menu a:hover {
    background-color: rgba(205, 205, 205, 1) !important; 
	
}

a{
    cursor:pointer !important;
    font-weight: bold !important;
    color: #337ab7 !important;
}

div.logo{
	background: rgba(35, 35, 35, 1) !important;
        color: rgba(205, 205, 205, 1) !important;
}
  
  div.menu{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}
/*--------------------------------------*/
/*бэкграунды таблиц,новостей, комментов*/
  div.article.shadowed{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}

div.header.shadowed{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}

div.comment_header{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}

/*скрыть комменты*/
div.hide_comments:hover {
color: rgba(35, 35, 35, 1) !important;
}

div.hide_comments{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}
/*-----------------*/

div.leaders.shadowed{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}

div.leaders.shadowed{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}

div.leaders_table{
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
    
}
/*-------------------------------------*/
  /*таблицы*/

table.leaders_table td{
  border-left: 1px solid #ddd !important;
    border-right: 1px solid #ddd !important;
}

table {
    background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}

td{
    background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}
/*разные костыли хДД*/
span{
    background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}

h1{
    color: rgba(205, 205, 205, 1) !important;
}
/*мувики*/
body > div.body > div.content > div.news_column > div > a:nth-child(even) > div > div:nth-child(even) {
		background: rgba(15, 15, 15, 1) !important;
        color: rgba(205, 205, 205, 1) !important;
}

body > div.body > div.content > div.news_column > div > a:nth-child(odd) > div > div:nth-child(even) {
		background: rgba(15, 15, 15, 1) !important;
        color: rgba(205, 205, 205, 1) !important;
}



/*----*/
/*залитые демки*/
.popup_content {
	background: rgba(35, 35, 35, 1) !important;
    color: rgba(205, 205, 205, 1) !important;
}
/*-------*/

}
