css:
/*
 * FireFox 3.5 - Small - Vista
 */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

button{height: 12px !important; margin:1px 1px 1px 1px  !important; padding:0px 0px 0px 0px !important; border: none !important;}

#piclens-toolbarbutton2, #piclens-toolbarbutton1, #piclens-toolbarbutton { margin: -5px -7px -5px -8px !important; padding: 0px !important; -moz-appearance: none !important; border: none !important; }


/* Stop-Reload-Button */
/* Stop muss vor Reload-Button sein! */
#stop-button:not([disabled="true"]) { display: -moz-box !important; }
#stop-button[disabled="true"] { display: none !important; }
#stop-button[disabled="true"] + #reload-button { display: -moz-box !important; }
#stop-button:not([disabled="true"]) + #reload-button { display: none !important; }



/* ------------- FindBar ------------- */
/* FindBar: size */
.findbar-container{ max-height: 24px !important; margin: -2px 0px 0px 0px !important; padding:0px 0px 0px 0px !important; min-height: 1px !important; }
#FindToolbar textbox { font-size: 90% !important; width: 300px !important; height: 14px !important;margin: 0px 0px 0px 0px !important; padding: 1px !important; min-height: 1px !important; }
#FindToolbar checkbox label{font-size: 90% !important;}
#find-field,label[control="findbar-textbox"] { font-size: 90% !important; padding:0px 0px 0px 0px !important; margin:-2px 6px 0px 4px  !important; }
#FindToolbar checkbox{ height: 12px !important; padding:0px 0px 0px 0px !important; margin:-2px 0px 0px 0px  !important; }

.findbar-highlight label{ height: 12px !important; font-size: 90% !important; padding:0px 0px 0px 0px !important; margin:-3px 0px 0px 0px  !important; }
.findbar-highlight > .toolbarbutton-icon{ height: 12px !important; padding:0px 0px 0px 0px !important; margin:-2px 0px 0px 0px  !important; }
.findbar-find-next label, .findbar-find-previous label{ height: 12px !important; font-size: 90% !important; padding:0px 0px 0px 0px !important; margin:-8px 0px 0px 0px  !important; }
.findbar-find-next > .toolbarbutton-icon, .findbar-find-previous > .toolbarbutton-icon{ height: 12px !important; padding:0px 0px 0px 0px !important; margin:-6px 0px 0px 0px  !important; }
.findbar-closebutton, .findbar-find-next, .findbar-find-previous, .findbar-highlight{ height: 12px !important; padding:0px 0px 0px 0px !important; margin:2px 2px 0px 2px  !important; }
/* --------END-- FindBar --END-------- */

/* ------------- Tabs ------------- */
/* Tabs: Font um 10% Tabs */
.tabbrowser-tabs .tab-text {font-size: 100%;}

/* Tabs: height */
/*.tabbrowser-tab {max-height: 20px !important;}
.tabbrowser-tabs { max-height: 18px !important; }*/
.tabbrowser-tab > hbox { max-height: 14px !important; height: 12px !important; }

/* Tabs: height  buttons */
.tabs-alltabs-button { margin: 2px 0px 3px 0px  !important;} 
.tabs-alltabs-box { padding: 0px 4px 0px 0px !important; margin: -1px 0px -6px -1px  !important;  } 
.tabs-newtab-button { height: 16px !important; padding: 0px 0px 0px 0px !important; margin: 2px 0px 1px 1px  !important;  }
.tabbrowser-arrowscrollbox { margin: -1px 0px 0px 0px  !important;}

/* Tabs: CloseButton only on mouse hover shown */
.tabbrowser-tabs :not(:hover) .tab-close-button > .toolbarbutton-icon { display:none !important; }
/* --------END-- Tabs --END-------- */

/* ------------- ToolBar ------------- */
/* ToolBar: DROPMARKER moved closer to its icon */
.toolbarbutton-menu-dropmarker { padding: 0px 0px 0px 0px !important; margin: 0px 0px 0px 0px  !important;}
.toolbarbutton-menubutton-dropmarker { padding: 0px 0px 0px 0px !important; margin: 0px 0px 0px 0px  !important; min-height: 10px !important; border: 0 !important; }

/* ToolBar: small icons */
.toolbarbutton-icon  {margin: -4px 0px 0px 0px  !important; width: 16px !important;height: 16px !important; min-width: 10px !important; max-width: 40px !important;}

/* ToolBar: small height */
#navigator-toolbox > toolbar { max-height: 24px !important; }
#nav-bar { height:  24px !important; }
/* --------END-- ToolBar --END-------- */

/* ------------- URLBar ------------- */
/* URLBar: size */
#urlbar-container, .urlbar-textbox { margin: 0px 0px 0px 0px !important; padding: 0px !important; min-height: 1px !important; height: 18px !important; }
#urlbar { margin: -6px 0px 0px 0px !important; padding: 0px !important; min-height: 1px !important; height: 18px !important; }

/* URLBar: Hide Site Identity Button if URL Bar is Focused */
#identity-box:not([style="-moz-user-focus: ignore;"]) { display:none; }

/* URLBar: Compress Separator Between URL Bar and SearchBar */
.textbox-input-box{ min-height: 10px !important; }
/* --------END-- URLBar --END-------- */

/* ------------- SearchBar ------------- */
/* SearchBar: size */
#search-container, #searchbar, .searchbar-textbox{ margin: -2px 1px 0px 0px !important; padding: 0px !important; min-height: 1px !important; height: 16px !important; }
#search { margin: 0px 0px 0px 0px !important; padding: 0px !important; min-height: 1px !important; height: 18px !important;}

/* SearchBar: Remove the search button (magnifying glass) */
.search-go-button { display: none !important; } 
/* --------END-- SearchBar --END-------- */


/* ------------- MenuBar ------------- */
/* MenuBar: size */
#toolbar-menubar { height: 12px !important; margin: 0px 0px 0px 0px !important;}

/* MenuBar: Compress Main Menubar TITLES closer together */
#main-menubar menu{ border: 0px !important; margin: 0px -8px 0px 0px !important; }

/* MenuBar: small */
#file-menu, #edit-menu, #view-menu, #go-menu, #bookmarksMenu, #tools-menu, #helpMenu, #history-menu { font-size: 10px !important; margin-top: -6px !important; }
/* --------END-- MenuBar --END-------- */


/* ------------- StatusBar ------------- */
/* Small Statusbar */
.chromeclass-status { height: 8px !important; font-size: 10px !important; }

/* StatusBar: slim and small */
#status-bar { border: none !important; padding: 0px !important; margin: -2px !important; border: none !important; }
#status-bar > * , #status-bar > * > * { margin: 1px !important; padding: 0px !important; }
/* --------END-- StatusBar --END-------- */


/* Smaller Awesome Bar Text */
richlistitem.autocomplete-richlistitem * {margin: 0 !Important; padding: 0 !important;}
richlistitem.autocomplete-richlistitem > hbox{margin: -1px -2px -2px -2px !important;}
richlistitem.autocomplete-richlistitem[selected=true] { background: rgba(120,120,255,0.6) !important; }
richlistitem.autocomplete-richlistitem .ac-title image:first-child { width: 12px !Important; height: 12px !important; display: none !important; }
.ac-comment:first-child {font: bold 13px/14px 'tahoma' !important; color: #222 !important; margin: -2px 0 0 0 !important; padding: 0px !important; }
.ac-comment:not(:first-child) {font: bold 10px/14px 'segoe ui' !important; color: #aa0 !important; margin: -2px 0 0 0 !important; padding: 0px !important;}
.ac-comment[selected="true"] {color: white !important; }
.ac-comment { font-size: 100%! important; color: #000000! important; }
.ac-comment[selected="true"] { color: #FFFFFF !important; }
.ac-url-text { font-size: 100% ! important; color: #000099 ! important; }
.ac-url-text[selected="true"] { color: #FFFFFF !important; }
.ac-comment { font-family: 'Verdana',sans-serif !important; font-size: 120% !important; color: #333 !important; }
.ac-comment[selected="true"] { color: #fff !important; }
.ac-url-text { font-family: Verdana,sans-serif !important; font-size: 100% !important; color: #030 !important; }
.ac-url-text[selected="true"] { color: #fff !important; }
.ac-title .ac-normal-text .ac-emphasize-text, .ac-url .ac-normal-text .ac-emphasize-text { text-decoration: inherit !important; font-family: inherit !important; font-weight: 600 !important; font-style: italic !important; font-size: inherit !important; color: #930 !important; }
