css:
@-moz-document domain("lambda-the-ultimate.org") {
body {
    background-color: #002b36;
    color: #93a1a1;
}
a, a:link, a:active, li a, li a.active {
    color: #268bd2;
}
a:visited {
    color: #6c71c4;
}
pre, div.content blockquote, div.comment blockquote {
    border-left-color: #073642;
    background-color: #073642;
    color: #eee8d5;
}
h1.site-name {
    margin: 0.25em auto;
    text-align: center;
}
h1.site-name a {
    color: #859900 !important;
    text-align: center;
    margin: 0 auto;
}
div#header img {
    display: none;
}
input.form-text, input.form-password {
    background-color: #657b83;
    color: #fdf6e3;
    border: 2px;
}
input.form-submit {
    background-color: #073642;
    color: #cb4b16;
    border: 2px;
    font-weight: bold;
}
div.form-item select {
    background-color: #073642;
    color: #d33682;
    border: 2px;
    font-weight: bold;
}
ul.tabs li a {
    background-color: #586e75;
    color: #002b36;
}
ul.tabs li.active a.active {
    background-color: #657b83;
    color: #002b36;
}
tr.dark {
    background-color: #073642;
}
tr.light {
    background-color: #073642;
}
}
