css:
@-moz-document domain("openlayers.org") {
p {
    color: white;
}
.nameContainer {
    color: white;
}
.main {
    background-color: black;
}
thead {
    color: white;
}
pre.prettyprint.source {
    background: #0e0e0e;
    border-color: #272727;
}
h2 {
    color: white;
}
code {
    background-color: #1f1f1f;
}
dd {
    color: white;
}
.navbar-inverse .navbar-inner {
    background-color: #031c1f;
}
a.active {
    background-color: #0f2d40;
}
.navbar-inverse .navbar-nav>li>a:hover,
.navbar-inverse .navbar-nav>li>a:focus,
.navbar-inverse .navbar-nav>li>a.active {
    background-color: #0f2d40;
}
body {
    background-color: #1b1b1b;
}
.example {
    background-color: black;
}
.navbar {
    background-color: #031c1f;
}
header.navbar.navbar-fixed-top {
    background-color: #031c1f;
}
input.search-query {
    background-color: #333;
    color: white;
    border-width: 0;
}
h1 {
    color: white;
}
footer {
    background-color: #031c1f;
}
div#news {
    background-color: black;
    border: none;
}
h3 {
    color: white;
}
a.big-link {
    border: none;
}
.nav>li>a:hover,
.nav>li>a:focus,
.nav>li>a.active {
    background-color: #0f2d40;
}
th {
    color: white;
}
h4 {
    color: white;
}
li {
    color: white;
}
td {
    color: white;
}
.example:hover {
    background-color: #1e333a;
}
div#api-links {
    color: white;
}
button {
    color: #eaf5ff;
    background-color: #383838;
    border-width: 0;
    border-radius: 5%;
}
pre.language-markup {
    background-color: #2d2d2d;
}
code[class*="language-"],
pre[class*="language-"] {
    text-shadow: none;
    color: white;
}
span.token.operator {
    background-color: #2d2d2d;
}
.nameContainer .anchor.highlighted + h4 {
  background-color: #001152;
}
}
