css:
@-moz-document domain(forum.pirati.cz) {
  body {
  	padding: 0 !important;    
  }
  
  a:link, a:visited {
    text-decoration: none;   
  }
  
  a:hover {
    text-decoration: underline;   
  }
  
  div.wrap {
      max-width: inherit !important;
      border: none !important;
  }
  
  #page-header .inner {
      display: table;
      width: 100%;
  }
  
  #nav-main {
      display: table-footer-group;
      position: relative;
  }
  
  #nav-main::after {
  	border-bottom: none;
  }
  
  #nav-main li.responsive-hide {
      padding-left: 2px !important;
  }
  
  #nav-breadcrumbs {
      display: table-header-group;
  }
  
  #nav-breadcrumbs::after {
  	border-bottom: solid 1px #fff !important;   
  }
  
  #quick-links, .pagination li.page-jump {
   	display: none !important;   
  }
  
  #username_logged_in {
      left: -5px !important;
  }
  
  #nav-main li.rightside {
  	position: absolute;
  }
  
  #nav-main li.responsive-hide {
  	margin-top: 25px;
  }
  
  .pagination li a, .pagination li.active span, .pagination i, input.tiny {
      font-size: 8pt !important;
  }
  
  .postbody .post-buttons .button span.ok_showText {
      font-size: 8pt;
  }
  
  .postbody .sr-only {
      height: 8px;
      width: auto;
      margin: auto;
      position: inherit;
  }
  
  div.post div.notice {
      line-height: 12pt;
      font-size: 7pt;
  }
  
  div.post div.notice dd {
      line-height: 12pt;
      font-size: 8pt;
  }
  
  ul.topiclist dt {
      margin-right: -640px;
  }
  
  ul.topiclist dt .list-inner {
      margin-right: 640px;
  }
  
  ul.topiclist dd.lastpost {
      width: 450px;
  }
  
  /*.forum_unread_subforum div a.subforum {
      list-style: circle;
      display: list-item !important;
      margin-left: 15px;
  }*/
  
  #ok_logoutButton {
      position: absolute;
      right: 0;
      top: 10px;
      font-size: 8pt;
  }
  
  #ok_thanksList {
      position: absolute;
      right: 0;
      top: 30px;
  }
  
  #ok_groupsList {
   	position: absolute;
      right: 100px;
      top: 30px;
  }
  
  #ok_modPanel {
      position: absolute;
      right: 285px;
      top: 30px;
  }
}
