css:
@-moz-document domain("www.cubecraft.net") {
.mcPlayerMessage.withGroup .playerData {
	padding: 8px 2px 8px 6px;
	height: 0;
}
.mcPlayerMessage {
	position: relative;
	background: none;
	z-index: 0;
}
.messageUserBlock .userBanner:last-child {
	margin-top: -3px;
	margin-bottom: 5px;
}
.mcPlayerMessage .playerData {
	width: 100px;
}
.mcPlayerMessage:not(.withGroup) {
	background-color: #00000050;
}
.mcPlayerMessage .group-badge {
	visibility: hidden;
}
.mcPlayerMessage .group-badge::before {
	visibility: visible;
	content: '';
	position: absolute;
	left: 0px;
	top: 0px;
	width: 100%;
	height: 100%;
	z-index: -1;
}
.mcPlayerMessage .group-badge-1::before {
	background-color: #aaaaaa50;
}
.mcPlayerMessage .group-badge-2::before {
	background-color: #0000aa50;
}
.mcPlayerMessage .group-badge-3::before {
	background-color: #fce30050;
}
.mcPlayerMessage .group-badge-4::before {
	background-color: #05caed50;
}
.mcPlayerMessage .group-badge-5::before {
	background-color: #03c70050;
}
.mcPlayerMessage .group-badge-6::before {
	background-color: #aa00aa50;
}
.mcPlayerMessage .group-badge-90::before {
	background-color: #ff55ff50;
}
.mcPlayerMessage .group-badge-91::before {
	background-color: #ffaa0050;
}
.mcPlayerMessage .group-badge-92::before {
	background-color: #ffaa0050;
}
.mcPlayerMessage .group-badge-93::before {
	background-color: #aa000050;
}
.mcPlayerMessage .group-badge-94::before {
	background-color: #00aaaa50;
}
.mcPlayerMessage .group-badge-95::before {
	background-color: #00aa0050;
}
.mcPlayerMessage .group-badge-96::before {
	background-color: #5555ff50;
}
.userText .username span {
	padding-left: 2px;
	padding-right: 2px;
}
.userText .username .style7 {
	background: rgba(255,255,255,.5);
}
.userText .username .style11, .userText .username .style4 {
	background: rgba(0,0,0,.5);
}
.group-badge-plus, h1.username div {
	background-image: none !important;
}
}
