css:
@-moz-document domain("twitter.com") {
.trends {
	display: none;
}
.Footer {
	display: none;
}
.wtf-module {
	display: none;
}
.wtf-module.has-content {
	display: none;
}
.dashboard .dashboard-left {
	display: none;
}
.dashboard {
	display: none;
	float: none;
	width: auto;
}
.content-main {
	float: none;
	width: auto;
}
.stream-item:not(.no-header-background-module) {
	margin-bottom: 11px;
	box-shadow: 0px 2px 20px 2px #00000024;
}
body {
	background-color: #fff;
}
.fullname {
	font-size: 14px;
}
.TweetTextSize {
	font-size: 18px;
	letter-spacing: -0.4px;
	line-height: 25px;
}
.js-tweet-text-container {
	margin-bottom: 25px;
	margin-top: 10px;
}
.tweet {
	padding: 30px 20px;
	font-family: Roboto-Regular;
}
.top-timeline-tweetbox .timeline-tweet-box {
	display: none;
}
.js-stream-tweet.tweet-has-context .self-thread-tweet-cta .mini-avatar-with-thread:before {
	left: 40px;
	bottom: 58px;
	top: 108px;
}
.conversation-module > li:after,
.conversation-module > li:before {
	left: 41px;
}
}
