css:
@-moz-document url-prefix("https://prohardver.hu/forum"), domain("fototrend.hu") {
/*felső menüsáv - háttérszín, aktív elem, egér alatti elem, árnyékolás, betűszín, menüsáv magassága*/
#header-menu{
    background: radial-gradient(circle, #6BA2B3, #498699);
    border-radius: 0 0 8px 8px;
}

#header-menu .navbar-nav > .active > a  {
	background-color: rgba(51, 47, 40, 0.9);
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 -18px 20px 0 rgba(0, 0, 0, 0.4);
    z-index: 100;
}

#header-menu .navbar-nav > li > a:hover {
	background-color: rgba(108, 93, 66, .63);
    box-shadow: 0 0 8px 0 rgba(0, 0, 0, 0.2), 0 -18px 20px 0 rgba(0, 0, 0, 0.2);
    z-index: 101;
}

#center {
    z-index: 102;
}

#header-menu .navbar-nav > li {
    box-shadow: 0 0 2px 0 rgba(0, 0, 0, 0.2);
}

#header-menu a {
	color: #e1e1e1;
}

.navbar-nav > li > a {
    line-height: 10px;
}

.navbar {
    min-height: 40px;
}



/*paddig eltűntetése a statikus header miatt, statikus header, header színezése, keresősáv, gombok*/
#header-padding {
	display:none;
}

#header {
	position: relative;
}

#header-sticky {
	background-color: #658A8A;
    background: linear-gradient(to bottom right,#658A8A, #427070);
    box-shadow: 0 2px 24px 0 rgba(0, 0, 0, 0.3);
}

.input-group .twitter-typeahead:first-child .tt-input, .input-group .twitter-typeahead:first-child .tt-hint, #search-sticky .form-control {
	background-color: #0000001a!important;
    border-radius: 3px 0px 0px 10px!important;
    color: #D4D4D4;
} 

.twitter-typeahead .tt-input {
    color: #393939;
}

.btn-default, a.btn-default,.btn-primary, a.btn-primary, #search-sticky .btn-secondary {
    background-color: #77abce;
    color: #000;
    border-color: #0000;
}

.btn-secondary, a.btn-secondary, #search-sticky .btn-secondary:hover {
    background-color: #2E5D7D;
    color: #B6E3E3;
}

.input-group-btn .btn, .btn {
    border-color: transparent!important;
}



/*Dropdown menük, profilkép*/
.dropdown-menu .dropdown-item:hover {
    background: #FFF0;
    color: #333029;
     text-shadow: 0px 0px 8px #000000;
}

.dropdown-divider {
	border-top: 1px solid #333029;
}

.dropdown-menu .dropdown-item {
    background: #FFF0;
    color: #333029;
}

.dropdown-menu {
	background-color: #4c9290e6;
    border: 0px;
    border-radius: 8px;
    
}

ul.dropdown-menu li a:hover {
    background-color: #AFC4C4;
    text-shadow: 0px 0px 8px #000000;
}

container-fluid >  .pager-navbar .navbar-nav .btn-default, .pager-navbar .navbar-nav .btn-default:hover, .pager-navbar .navbar-nav .btn-default:focus, .pager-navbar .navbar-nav .btn-default, .pager-navbar .navbar-nav .btn-default:hover, .pager-navbar .navbar-nav .btn-default:focus {
    background-color: #FFF3!important;
    border-radius: 3px!important;
}

.dropdown-header, ul.dropdown-menu li h4 {
    color: #EFEFEF;
}

.dropdown-item.active {
    background-color: #3E5251!important;
    color: #FFF;
}

.msg-list .card .card-header .dropdown-menu {
	background-color: #49B3B1e6;
    border: 0px;
    border-radius: 8px;   
}

.msg-list .card .card-header .dropdown-menu :not(.disabled) a:hover {
    background: #FFF0;
    color: #B6E3E3;
     text-shadow: 0px 0px 8px #000000;
}

#header-menu .user-face, #header-sticky .user-face {
	border-radius: 8px;
    background: #DFD9C3CC;
}



/*footer színe, footer linkek színe, PH! ikonok beállítása a footernél és a headernél*/
#foot {
    background: linear-gradient(#8ca8b1, #0d262966);
    color: #263C3C;
}

#foot a {
	color: #2b4c4e;
}

.ph::before, .ma::before, .it::before, .gp::before, .lo::before, .ha::before {
    width: 12px!important;
    height: 12px!important;
    border: 0px!important;
    border-radius: 2px!important;
}



/*háttér színe, betűméret, fórum felső navi-sáv-> elválasztók színe*/
main {
	background:  linear-gradient(#bcd7d6, #8CA8B1);
}

body {
    font: normal /*[[body-font-size]]*/ sans-serif;
    background-color: #7DC9C8;
}

.breadcrumb > li + li::before {
    color: #326587;
}

/*kártya háttér színe, kártya fejléc szín*/
.card-body {
	background-color: #cae7e9!important;
}

.card .card-header {
	background-color: #7FC4C9;
}

#center .card {
    background-color: transparent;
}

/*topic lista dobozméret, komment header, komment header elválasztók, komment header dátum,
kommentek háttere, árnyékolása*/
.thread-list .media-body {
    padding: 4px 0;
}

.msg-list .card .card-header a:not(.dropdown-item) {
    color: #2F6487!important;
}

.msg-list .card .card-header .list-inline > li + li {
	border-left: 1px solid #2E5A78;
}

.msg-list .card .card-header {
	color: #000;
	line-height: 12px;
    height: 24px;
    font-size: 110%;
}

 .card, .thread-list {
	background-color: transparent;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1), 0 6px 20px 0 rgba(0, 0, 0, 0.1);
}

/*heading-betűméretek, heading színe, bal oldali headingek elrejtése, kártya keretek eltőntetése*/
#left .card .card-header, #right .card .card-header {
    font-size: 14px;
	background-color: #73C3D1;
	color: #303030;
}

h4, .h4 {
    font-size: 16px;
}

#left > div > h4 {
	display: none;
}

.card {
    background-color: transparent;
	border: 0px;
}


/*fórum-lista lekerekítés+színezés, árnyékolások, lapozó sáv lekerekítése*/
.forum-list .forum-group, .thread-list .thread-list-header, .pager-navbar, .msg-list .card .card-header, .modal .modal-header, .rtif-formats {
    background: linear-gradient(to bottom right, #5ba7bcb3, #5ba7bc);
    border-radius: 4px 4px 0 0;
}

.forum-list .media {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1), 0 6px 20px 0 rgba(0, 0, 0, 0.1);
}
    
.pager-navbar {
	border-radius: 4px 4px 4px 4px;
}
    
.panel {
	background-color: #DDD0;
}

.forum-list a {
    color: black!important;
}
    
/*Beszúrás: háttér elsötétítése ki (bugos telefonon)*/
.modal-backdrop {
    display: none!important;
}

/*Fórum főoldal színek*/
.forum-list .media:not(.forum-group) {
	background: #C3E2E4;
}

/*Topik-lista váltott soros színezése, csicsa árnyék egér alatti listaelemre, kedvencek formázása(betűméret, szín), gombok (?)*/
.thread-list .media {
	background-color: #AED7D7;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1), 0 6px 20px 0 rgba(0, 0, 0, 0.1);
}

.thread-list .media:nth-of-type(2n+1) {
    background: #C3E2E4;
}

.thread-list .media:nth-of-type(1) {
    background: #0000;
}

.list-group .list-group-item:hover, .forum-list .media-body:hover, 
.thread-list .media-body:hover {
    box-shadow: inset 0px 0px 15px 3px rgba(0, 0, 0, 0.05);
}

.user-thread-list .thread-actions a.new-msgs {
    color: #3a739a;
    font-size: larger;
    font-weight: normal;
}

.forum-list .forum-group:not(:nth-child(1)) {
    margin-top: 10px;
}

.modal-content {
    background-color: #DFD9C3;
}

.rtif-formats > .btn-group > .btn {
    background-color: #5EB2B8;
}

.msg-controls > div > .btn-group > .btn[data-close=""] {
    background-color: #5EB2B8;
}

/*oldalsó kártyák háttérszíne*/
.card .list-group li {
	background-color: #cae7e9!important;
}

/* témaindító írás háttere, face-frame, oldalsávok betűmérete*/
#right > .card > .card-body, .panel.panel-default.user-profile-form > .panel-body {
	background-color: #e8fbfc !important;
}

#left, #right {
	top: 0px!important;
    font-size: smaller!important;
}


/*új komment jelző ikon, új üzenetek száma betűméret
https://css-tricks.com/examples/ShapesOfCSS/
*/

.fa-comment-alt {
 position: relative;
    background: #427FA8; 
    height: 10px; 
    width: 10px; 
    -moz-border-radius: 5px; 
    -webkit-border-radius: 5px; 
    border-radius: 5px;
}

.fa-comment-alt::before, .fa-comment-alt::after {
 	content: ''; 
    position: absolute; 
    border-bottom: 7px solid #427FA8; 
    border-left: 4px solid transparent; 
    border-right: 4px solid transparent; 
    top: 7px; 
    left: -1px; 
    -webkit-transform: rotate(-140deg); 
    -moz-transform: rotate(-140deg); 
    -ms-transform: rotate(-140deg); 
    -o-transform: rotate(-140deg);
}

.fa-comment-alt::after {
 left: auto; 
    right: -1px; 
    -webkit-transform: rotate(140deg); 
    -moz-transform: rotate(140deg); 
    -ms-transform: rotate(140deg); 
    -o-transform: rotate(140deg);
}

.user-thread-list .thread-actions a.new-msgs {
    color: #3a739a;
    font-size: larger;
    font-weight: normal;
}

/*mobilos üzenetszám-badge átszínezése*/
.badge {
	background-color: #3a739a;
}

/*Kommentek közti cikkajánlás letiltása (mobilos bug miatt)*/
.msg-list .content-list {
	display:none;
}

/*Kommentrész szélessége nagy felbontáson (jellemző monitorméretekhez igazított széelsség) + mobil bugfix*/

@media (max-width: 767px) {
    .card .list-group-item {
		background-color: #cae7e9!important;
    }
    
    #header-menu .navbar-nav > .active > a  {
    box-shadow: none;
	}
    
    /*Fögzített fejléc mobil nézetben*/
    #header {
		position: /*[[phone-fixed-header]]*/;
	}
    
    .msg-list .card .card-header a:not(.dropdown-item) {
        font-size: smaller!important;
	}
    
    /*komment fejléc fix*/
   	.msg-list .card .card-header {
		height: auto;
		line-height: 20px;                                        
	}
    
    .msg-list .card .card-header .msg-head-user, .msg-list .card .card-header ul {
    	padding: 0px!important;
	}
    
    .list-inline.float-right {
		display: flex;
		flex-direction: row;                                            
	}
    
    .list-inline.float-right li > a {
		margin: 5px;                                            
	}
    
    .list-inline.float-right li:nth-child(1) {
  		order: 4;                                            
    }
    
    .list-inline.float-right li:nth-child(2) {
  		order: 3;
    }
    
    .list-inline.float-right li:nth-child(3) {
  		order: 1;
    }
    
    
    
    /*MAGIC: 1337 kép körbefuttatás H4CK*/
    .msg-list .card-body .media-body {
		display: contents;
        /*float: left;
        padding-top: 10px;*/
    }
    
   	.msg-list .card .card-body {
		display: table-row;
	}
    
    .msg-list .card .card-body .media-body p {
		overflow: inherit;
	}

    #center {
        width: 100%!important;
    }

    .msg-editor .card-body .media-left {
    	float: none;                                    
    }
    
    .msg-editor .card-body .face-wrapper {
		display: inline;                                            
    }
   
}


@media (min-width: 1400px) {
    .container {
        width: 85%!important;
		max-width: none!important;    
		margin: 0 auto;                                            
    }

    /*.container > .row {
        width: 100%!important;
    }*/

    #center {
        width: calc(100% - 460px)!important;
    }
}

@media (min-width: 1600px) {
    
    .container {
        width: 80%!important;
        max-width: none!important;
		margin: 0 auto;
    }
}

@media (min-width: 1900px) {
    
    .container {
        width: 75%!important;
		max-width:2000px!important;      
		margin: 0 auto;                                            
    }
}
}
