css:
@-moz-document url-prefix("http://docwiki.embarcadero.com") {
body {
        background: #293134;
}

#p-logo {
    left: 0px;
    background: white;
}

div#p-cactions.portlet {
    left: 230px;
    width: 400px;
    background: none;
}

#content {
    margin: 35px 0px 0px 222px;
    padding: 1px 10px 0px 25px;
    min-height: 48em;
	border-radius: 10px 0px 0px 10px;
    background-color: #293134;
    color: #D5F1FF;

}

#footer {
    background-color: #293134;
}

/* */
.portlet{
    border-radius: 7px;
    width: 215px;
}
li#n-FireMonkey-Application-Platform {
    /* padding-left: 26px; */
    font-size: 12px;
}

div.pBody ul li  {    
	padding-top: 3px;
	padding-bottom: 3px;   
    width: 300px;
	-webkit-transition: margin 0.5s ease, padding 0.5s ease;
	-o-transition: margin 0.5s ease, padding 0.5s ease;
	transition: margin 0.5s ease, padding 0.5s ease;
    
}


div.pBody ul li:hover:not(#pt-createaccount):not(#pt-login):not(#ca-nstab-main):not(#ca-talk):not(#ca-viewsource):not(#ca-history):not(.first) {
    background-color: #fff373 !important;
    color: #5871d6 !important;
	margin-left: -10px;
	border-radius: 17px;
    box-shadow: 0 0 20px #9ab14a;  
}

div.pBody ul li:hover{
    background-color: #fff373 !important;
    color: #5871d6 !important;

    box-shadow: 0 0 20px #9ab14a;  
}

div.pBody li:hover:not(#ca-nstab-main):not(#ca-talk):not(#ca-viewsource):not(#ca-history):not(#pt-createaccount):not(#pt-login):not(.first) a
{
    background-color: #fff373 !important;
    color: #5871d6 !important;
    display: block;
    /* height: 23px; */
    width: 300px;
    position: relative;
	left: -21px;
    padding-left: 10px;
    
}


/* */


a {
    color: #45b5ac;
}
a:visited {
    color: #bbcc3b;
}
div.catlinks {
    background-color: #293134;
    border-color: #6FFF85;
    border-style: dotted;
    border-width: 1px;
}
div.generated-sidebar.portlet h5 {
    color: #6FFF85;
    font-variant: small-caps;
}
div.pBody ul {
    background-color: #293134!important;
}
div.pBody ul a {
    color: #ff99bb!important;
}
div.portlet {
    background-color: #293134;
}
div.portlet h5 {
    color: #6FFF85;
}
div.portlet h5 label {
    color: #6FFF85;
}
div.toc {
    background-color: #034 !important;
    border-style: solid;
    border-width: 2px;
}
h1.firstHeading span {
    color: #ffffa9;
    font-variant: small-caps;
    font-weight: bold;
}
li.selected a {
    background-color: #802d2d !important;
}

/***************************************/
/* blockquote font b,

*/
ol li font span font,
ol li code,
dl dd code,
ul li code,
p code ,
blockquote font b,
blockquote code
{
    background-color: #2d4457;
    border: 1px dashed #8aff90;   
    color: #ffadad;
    font-size: 13px;
    margin: 2px;
	padding: 1px 4px 2px 3px;	
}

ol li font b {
    color: #29c74b;              
    background-color:#272548;
    font-size: 12px;      
    font-weight:normal;          
    border: 1px dashed #2f7d7b;     
    margin: 2px;  
	padding: 1px 4px 2px 3px;		                       
}

ol li font b:hover,
ol li font span font:hover,
ol li code:hover,
dl dd code:hover,
ul li code:hover,
p code:hover ,
blockquote font b:hover,
blockquote code:hover {      
    background-color:#123;   
    box-shadow: 0 0 12px #9ab14a;          
}

/**************************************/
span.mw-headline {
    color: #6FFF85;
    font-variant: small-caps;
}
span.mw-headline a {
    color: #6FFF85;
    text-decoration: underline;
}

p font span font {
	color: #03A9F4;
    font-weight: bold;
    font-size: 14px;   
}

/* */



/* pascal code */
div.delphi.source-delphi {
    font-family: Consolas;
    font-size: 14px;
    background-color: #234;
}
div.delphi.source-delphi span.co1,
div.delphi.source-delphi span.coMULTI{
    color: #3d6380;
    font-style: italic;
    font-size: 13px;
    
}
div.delphi.source-delphi span.br0,		/* ()	*/
div.delphi.source-delphi span.sy1,		/* : ; ... ,  */
div.delphi.source-delphi span.sy2,		/* @	*/
div.delphi.source-delphi span.sy3 {		/* =	*/
    color: #00fff5;
    font-style: italic;
    font-weight: bold;
 	text-shadow: 0 0 8px #000;
}

div.delphi.source-delphi span.kw1 {		/* type object of var const procedure function if then begin end */
    color: #ff6b6b;
    font-weight: bold;
}
div.delphi.source-delphi span.kw2 {		/* true false */
    color: #f9ff0a;
    text-shadow: 0 0 6px #6f1111;
}
div.delphi.source-delphi span.kw3 {		/* Assigned */
    color: #0f8;
}
div.delphi.source-delphi span.kw4 {		/* Integer Double	*/
    color: #0ab357;
   /* #dd5 */
}



div.delphi.source-delphi pre.de1 {
    color: #eef;
}
div.delphi.source-delphi span.me1 {		/* ButtonClick */
    color: #4066ff;
}


div.delphi.source-delphi span.nu0 {	/* int */
    color: #a7e;

}
div.delphi.source-delphi span.st0 {	/* str	*/
    text-decoration: underline;
    color: #ff0;
    font-style: italic;
}

/* pas code v2 */
/*
234
6FFF85	light green
ffff4c	some light yellow
3d6380	dark blue grey
00fff5	light blue
ff6b6b	light red
f9ff0a	yellow
6f1111	dark red
0f8		light green blue
0ab357	green
eef		white
4066ff	dark light blue - sirenev
a7e		violet blue
ff0		yellow
*/

/* */
div.mw-content-ltr pre {
    background-color: #162231; /*#234 #123; */
    color: #ffff4c;
}

div.mw-highlight.mw-content-ltr {
    background: #134;
}
/* */
div.mw-highlight.mw-content-ltr span.k { /* keyword [class var property const begin end Offset function]*/
    color: #53bf57;
    font-weight: bold;
    text-shadow: 0 0 2px #478c49;
}
div.mw-highlight.mw-content-ltr span.kt { /* keyword data type  */
	color: #ff9ee5;
    font-size: 14px;
    text-shadow: 0 0 3px #7a218a;    
}
div.mw-highlight.mw-content-ltr span.kp { /* keyword private public protected virtual write override */
	color: #4caf50;    
}
div.mw-highlight.mw-content-ltr span.cm { /* compiler */
/*	color: #b3305c;  
    font-style: italic;
*/
    color: #3d6380;
    font-style: italic;
    font-size: 13px;    
    
}
div.mw-highlight.mw-content-ltr span.p { /*  ( )  */
    color: #00fff5;
    font-style: italic;
    font-size: 14px;
    font-weight: bold;
    text-shadow: 0 0 3px #00BCD4;
}
div.mw-highlight.mw-content-ltr span.o { /*  = : ;  */
    color: #FF5722;
    font-style: italic;
     font-weight: bold; 
    text-shadow: 0 0 8px #000;
    font-size: 15px;
}
div.mw-highlight.mw-content-ltr span.s { /* string */
    text-decoration: underline;
    color: #FFA726;
    font-style: italic;
}
div.mw-highlight.mw-content-ltr span.n { /* class name */
	color: #fff27d;   
}
div.mw-highlight.mw-content-ltr span.nc { /* class name TForm1 */
	color: #ce5959;   
}
div.mw-highlight.mw-content-ltr span.nf { /* name function */
	color: #ff9800;   
}
div.mw-highlight.mw-content-ltr span.nb { /* Writeln */
	color: #a7e;   
    
}
div.mw-highlight.mw-content-ltr span.mi {		/*  */
    color:#bd98ff;
    font-style: italic;
}

div.mw-highlight.mw-content-ltr span.py { /* property name */
	color: #ff6b6b;   
}
div.mw-highlight.mw-content-ltr span.c1 { /* comment // */
    color: #3d6380;
    font-style: italic;
    font-size: 13px;
}






/* */


/* cpp code ver2 ******************************************************************************/
div.cpp.source-cpp {
    background-color: #223; /* 233 */    
    font-size: 14px;
    border-color: #A7F;
}

/* comments */
div.cpp.source-cpp span.co1,
div.cpp.source-cpp span.coMULTI {
    color: #555;
    font-style: italic;
    font-size: 13px;
}

/* compiler */
div.cpp.source-cpp span.co2 {
    color: #66a; /* #6C7; */    /* 093 */    
    font-style: italic;
    font-size: 13px;
}

div.cpp.source-cpp span.br0,	/* () [] {} */
div.cpp.source-cpp span.sy1,	/* = < > << >> */
div.cpp.source-cpp span.sy2,	/* * ** + ++ - -- / */
div.cpp.source-cpp span.sy3,	/* & */
div.cpp.source-cpp span.sy4		/* ; :: */
{
    font-style: italic;
    font-weight: bold;
    color: #F88;    /* 09F */
}

div.cpp.source-cpp span.sy1 {
    margin-right: 5px;
}
div.cpp.source-cpp span.sy2 {
    margin-left: 5px;    
}

/* var proc type */
div.cpp.source-cpp pre.de1,
div.cpp.source-cpp span.me1,
div.cpp.source-cpp span.me2 {
    color: #58D;    /* 0d9 */
}

div.cpp.source-cpp span.kw1,	/* if else for break return */
div.cpp.source-cpp span.kw2,	/* true false , using namespace */
div.cpp.source-cpp span.kw3		/* class public , cout calloc sizeof puts free printf */
{
    color: #FF0;
    font-weight: bold;
}

/* void const extern int unsigned double char short */
div.cpp.source-cpp span.kw4		
{
    color: #091;    /* dd0 */    
    font-weight: bold;
}

/* double */
div.cpp.source-cpp span.nu16 {
    color: #e066e0; /* #F5F; */
    font-style: italic;
}
div.cpp.source-cpp span.nu17 {
    color: #e066e0;
    font-style: italic;
}

/* string */
div.cpp.source-cpp span.st0 {
    color: #0DF;
    /*  
	font-style: italic;
    text-decoration: underline;
    color: #ff0;
*/
}

/* integer */
div.cpp.source-cpp span.nu0 {
    color: #7F2;    /* af9 */
}

/* ??? */
div.cpp.source-cpp span.es1 {
    color: #09f;
}
/* cpp code end **************************************/


/* types page */
ul#childlinks {
    background: #136;
    color: #f90;
    border: 2px dashed #f90;
    margin: 10px 0 10px 0;
    padding: 0px 0px 0px 0px;
    -webkit-transition: box-shadow 0.2s ease;
    -webkit-transition: -webkit-box-shadow 0.2s ease;
    transition: -webkit-box-shadow 0.2s ease;
    -o-transition: box-shadow 0.2s ease;
    transition: box-shadow 0.2s ease;
    transition: box-shadow 0.2s ease, -webkit-box-shadow 0.2s ease;
}

ul#childlinks li {
    display: block; 
    padding: 0px 10px 0px 10px;
}

ul#childlinks li a {
    color: #9f3;
    display: block;
    padding: 5px 0px 5px 0px;
	text-decoration: none;
    position: relative;
	left: 0px;
    
}
ul#childlinks:hover {
	box-shadow: 0 0 20px #9ab14a;      
}

ul#childlinks li:hover {
    background: none;
}

div.cpp span.lang {
    color: #8ff;
}
div.delphi span.lang {
    color: #08f;
}
div #codesig .sig .thesig,
#mw-content-text table tbody tr td {
    background-color: #134;
    border-style: dashed;
    border-width: 1px;
    border-color: #199;
}
#mw-content-text table {
    border-style: dashed;
    border-width: 1px !important;
    border-color: #199 !important;
}
table#vclinfo div.cpp,
table#vclinfo div.delphi {
    background: #123;
}
tr.vclinfot th {
    background: #123;
}
table#vclinfo tr.vclinfot th {
    border-bottom: 1px solid #199;
}

/* xml */
div.xml.source-xml {
    background-color: #133;
    font-size: 14px;
    border: 1px dashed #070;
    color: #cc5;
}

div.xml.source-xml .re1 {
    color: #cc5;   
}

div.xml.source-xml pre.de1 {
    color: #0b9;
}





/*table*/
#mw-content-text table {
    background-color: #123;
    border-color: #099;
    color: #5b0;
}
#mw-content-text table tbody tr td, 
#mw-content-text table tbody tr th {
    background-color: #123;
    border-color: #099;
    color: #5b0;
}
#mw-content-text table tbody tr.alt td {
    background-color: #134;
    color: #5b0;
}

/* */

#toctitle h2 {
    color: #ffffa9;
}
#f-list {
    color: #20d9ca !important;
}
#contactus {
    text-decoration: underline;
}
/* */

tr td h3 {
    color: #c0d645;
}
#mw-pages h2 {
    color: #65e086;
    font-variant: small-caps;
    font-size: 24px;
    font-weight: bold;
    text-transform: capitalize;
}
div.pBody {
    text-transform: capitalize;
}
a:hover {
    text-shadow: 0 0 10px #993;
}
div.portlet {
    box-shadow: 3px 3px 10px -3px black;
}
div#toc.toc,
.mw-warning {
    box-shadow: 0px 10px 41px 0px black;
}
}
