css:
@-moz-document url-prefix("http://www.moyabimbo.ru")
{

#common-light, #common-light-offline {
    width: 1116px;
    height: 510px;
    background-image: url(https://s8.hostingkartinok.com/uploads/images/2018/03/6d143c18cae03c18cce3233bda121728.png);
    position: absolute;
    top: -70px;
    z-index: 490;
    left: -155px;
}

#common-container {
    position: relative;
    top: 67px;
    min-height: 620px;
    padding-top: 60px;
    border: 3px solid #fff;
    width: 906px;
    margin-left: 60px;
    background: #ea5170;
    background-image: linear-gradient(bottom,rgb(224,68,102) 61%,rgb(255,140,165) 100%);
    background-image: -o-linear-gradient(bottom,rgb(224,68,102) 61%,rgb(255,140,165) 100%);
    background-image: -moz-linear-gradient(bottom,rgb(224,68,102) 61%,rgb(255,140,165) 100%);
    background-image: -webkit-linear-gradient(bottom,rgb(224,68,102) 61%,rgb(2, 1, 1) 100%);
    background-image: -ms-linear-gradient(bottom,rgb(224,68,102) 61%,rgb(255,140,165) 100%);
    background-image: -webkit-gradient( linear,left bottom,left top,color-stop(0.61,rgb(255, 163, 189)),color-stop(1,rgb(253, 129, 179)) );
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    border-radius: 20px;
    box-shadow: 0 0 10px 3px grey;
}

body {
    position: relative;
    background: no-repeat #f5e3e3 58% 5%;
    background-image: url(https://s8.hostingkartinok.com/uploads/images/2018/03/b273ebbf7aa869cb8a0fa41b5636b024.png);
    width: 1024px;
    margin: auto;
    text-shadow: rgba(0,0,0,.01) 0 0 1px;
    -webkit-font-smoothing: subpixel-antialiased;
    font-family: Arial;
    font-size: 12px;
    line-height: 1.2em;
}

#common-header-light {
    background-image: url(https://s8.hostingkartinok.com/uploads/images/2018/03/a7aafe69c1e7a1387fc5db77452a4202.png);
    width: 862px;
    height: 190px;
    position: absolute;
    z-index: 390;
    top: -37px;
    left: 0;
}

#common-bimbo {
    background-image: url(https://s8.hostingkartinok.com/uploads/images/2018/03/dcb4cfe964765eadc3a2682026029b20.png);
    width: 219px;
    height: 224px;
    position: absolute;
    z-index: 400;
    top: -70px;
    left: -93px;
    cursor: pointer;

}

#common-logo {
    width: 524px;
    height: 128px;
    background-image: url(https://s8.hostingkartinok.com/uploads/images/2018/03/9b0baaced5a65a24705384c9b61bc38d.png);
    position: absolute;
    top: 0;
    z-index: 500;
    left: 63px;
}

#levels-header {
    position: relative;
    left: 20px!important;
    margin-top: 20px;
    height: 130px!important;
    width: 760px;
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/a42c35a0ad7c8719774d502a00bb9620.png);
}

#levels-header span {
    color: #000;
    font-size: 20px;
    font-weight: bolder;
    height: 60px;
    left: 20px;
    position: absolute;
    top: 65px;
    line-height: 0px;
    text-align: center;
    width: 170px;
}

#levels-center {
    position: relative;
    left: 20px;
    width: 735px;
    padding-left: 18px;
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/28b64e7074e4e7316621e5d94f2bfada.png);
}

#levels-footer {
    position: relative;
    left: 20px;
    height: 414px!important;
    width: 760px;
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/fa9bd0d2d0c8044371264fa7a7f7d23a.png);
}

#levels-center .checkbox {
    float: left;
    width: 40px;
    height: 40px;
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/f6db4f3466beb30f0517b95a40940897.png);
    left: 3px;
}

#levels-center hr {
    width: 470px;
    height: 10px;
    padding: 5px;
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/b71d0a5c0582e9e3a771bee274bb2a64.png) no-repeat center center;
    border: 0;
}

#common-offline-logo {
    width: 470px;
    height: 114px;
    background-image: url(https://s8.hostingkartinok.com/uploads/images/2018/03/7f9faa9415c5a2f653325ff496397706.png);
    position: absolute;
    top: 85px;
    left: 250px;
    z-index: 800;
}

#challenge #content {
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/d3281372400e6bd4edc744b579e64469.png) no-repeat;
    width: 840px;
    height: 875px;
    position: absolute;
    top: 0;
    left: 8px;
}

#me .menu-left .travels {
    display: block;
    width: 145px;
    height: 126px;
    position: absolute;
    top: 112px;
    left: 42px;
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/22b820633781f1e23954c46dc276c750.png)!important;
}

#me .menu-left .stars {
    display: block;
    width: 178px;
    height: 167px;
    position: absolute;
    top: 334px;
    left: 0;
    background: url(https://s8.hostingkartinok.com/uploads/images/2018/03/b538b47139f83de5459d4bd099203c1e.png)!important;
}


}
