css:
@-moz-document domain("drooodle.com") {
body {
    background-color: #403e3e;
    color: #d6d6d6;
}
.droodle-control-bar {
    color: #ffffff;
    background: #272727;
}
.droodle-list .droodle-item .info-top .half .creator {
    color: #ffffff;
}
.droodle-list .droodle-item .info-top .half .droodle-created {
    color: #c5c5c5;
}
.droodle-info-bottom {
    color: #ffffff;
    background: #272727;
}
.navbar .center-nav .active a {
    color: #ffffff;
}
.navbar {
    background-color: #272727;
}
.list-message.error {
    color: #fff;
    background: #2c77c5;
    box-shadow: 0 2px 0 0 #1e5c9c;
}
.droodle-list .droodle-item .info-top .half .creator span {
    border: 2px solid #272727;
}
.drawing-tool .description-options .form-group {
    background: #565656;
}
.colors.vertical {
    background: #565656;
}
.colors.horizontal {
    background: #565656;
}
.tools.vertical .draw-tool.active,
.tools.vertical .line-tool.active,
.tools.vertical .undo.active,
.tools.vertical .clear.active,
.tools.vertical .fill.active,
.tools.vertical .text-tool.active,
.tools.vertical .image-tool.active {
    background-color: #3e3e3e;
}
.slider-tool {
    background: #403e3e;
}
.drawing-tool .description-options .form-group input[type="text"] {
    background: #403e3e;
}
img[src*="/img/drooodle-logo.svg"] {
    content: url("https://i.imgur.com/djdGYtf.png");
}
.navbar .center-nav .latest.active a:after {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url("https://i.imgur.com/jyyeSf9.png") no-repeat;
    width: 57px;
    /* Width of new image */
    height: 5px;
    /* Height of new image */
    padding-left: 5000px;
    /* Equal to width of new image */
}
.navbar-mobile {
    background-color: #272727;
}
.form-control {
    color: #eeeeee;
}
.tools ul {
    background: #565656;
}
.tools.vertical .blur-tool.active,
.tools.vertical .draw-tool.active,
.tools.vertical .line-tool.active,
.tools.vertical .undo.active,
.tools.vertical .clear.active,
.tools.vertical .fill.active,
.tools.vertical .text-tool.active,
.tools.vertical .image-tool.active {
    background-color: #403e3e;
}
.tools.horizontal .tool-control {
    background: #565656;
}
.tools.horizontal .tool-control .draw-tool.active,
.tools.horizontal .tool-control .line-tool.active,
.tools.horizontal .tool-control .text-tool.active,
.tools.horizontal .tool-control image-tool.active,
.tools.horizontal .tool-control .blur-tool.active,
.tools.horizontal .tool-control .image-tool.active {
    background-color: #403e3e;
}
.droodle-item {
    background-color: #272727;
}
.droodle-created,
.creator-streak,
.droodle-updated {
    color: #cecece;
}
.droodle-creator {
    color: #ffffff;
}
.layoutToggle .active,
.layoutToggle .active:hover {
    background-color: rgb(0, 0, 0);
    color: #ffffff;
}
.layoutToggle button {
    background-color: rgba(0, 0, 0, 0.65);
    border: none;
    box-shadow: none;
    font-size: 14px;
    color: rgba(179, 179, 179, 0.6);
    line-height: 30px;
    text-align: center;
    padding: 0 12px;
    width: 100px;
    cursor: pointer;
}
.card {
    background-color: #272727
}
.clickable,
.btn-primary-outline {
    color: #fff
}
.btn-primary-outline {
    border: solid 1px #fff
}
.navbar-dropdown.navbar-dropdown-notifications {
    background: #272727
}
.navbar-dropdown.navbar-dropdown-profile .list-group .list-group-item {
    background: #272727
}
.navbar-dropdown.navbar-dropdown-profile .list-group .list-group-item:hover {
    background: #403e3e
}
.user-hero .user-info {
    background: #272727
}
}
