css:
@-moz-document domain("youtrack.studiodesigner.com"), domain("silvertreesystems.myjetbrains.com") {
.container_width {max-width: unset; padding:0px;}

.ring-header-react_issues {max-width: unset; padding:0 10px;min-height:0;}

.header-container {min-height:0;}

.header_41a--,
.yt-whats-new-link {height:auto; line-height:unset;}

.ring-header__logo__custom-image {max-height:30px;}

.search-panel {
    background: rgb(48, 103, 187) none repeat scroll 0 0;
    border: 0 none;
    border-radius: 0;
    box-shadow: none;
    clear: both;
    padding: 10px;
    position: relative;
    margin-bottom: 0;
}

.search-center {position: relative;}

.search-panel__context-btn {
    background: #c00 none repeat scroll 0 0;
    border: 1px solid #fff;
    border-radius: 0;
    box-shadow: none;
    color: #fff;
    cursor: pointer;
    display: block;
    font-size: 10px;
    height: 24px;
    left: -9px;
    line-height: 24px;
    margin-left: 2px;
    position: absolute;
    text-align: center;
    top: 0;
    width: 24px;
}

.selectProject {
    border-color: #fff;
    box-shadow: none;
    height: 24px;
    width: 100%;
}

.selectProject .inputWrapper {box-shadow: none;}

.search-panel__search-btn {
    background: #c00 none repeat scroll 0 0;
    border: 1px solid #fff;
    box-shadow: none;
    color: #515151;
    cursor: pointer;
    float: right;
    height: 24px;
    line-height: 23px;
    text-align: center;
    width: 28px;
}

.ring-font-icon_search::before {color: #fff;}

.search-panel-input {border: 1px solid #fff;}


.search-south {
    margin-left: 249px;
    padding: 0;
}

.search-south > div {
    background-color: #FFF1AC;
    border-color: #3067bb;
    border-image: none;
    border-radius: 0 0 3px 3px;
    border-style: none solid solid;
    border-width: 1px;
    color: #5b646c;    
    padding: 2px 5px;
}

.content {
    padding: 0 2em;
    margin:0;
}

.agile::before,
.smartui::before {background:#fff;}

.default-layout .issueContainer {
    display:flex;
    padding-top:0;
}

.default-layout .issueContainer > * {
    display:flex;
    flex:1;
}
.issueContainer .icon-star-2 {margin: 0 6px 0 4px;}

.issueContainer table.issue-wrp,
.issueContainer table.issue-wrp tbody,
.issueContainer table.issue-wrp tr {
    width:100%;
    display:flex;
}

.issueContainer .dtbl {padding-left: 0;}

.issue-actions {
    border-left: 1px solid lightgray;
    color: #6e6e6e;
    display: flex;
    flex: 0 1 0;
    flex-direction: row;
    float: none;
    height: auto;
    margin-top: 0;
    min-width: 90px;
    order: 1;
    position: relative;
}
.issue-actions .poll {
    display: inline-flex;
    margin-left: 30px;
    margin-right: 5px;
    min-width: unset;
}

.properties-container {
    border-left: 1px solid lightgray;
    display: flex;
    flex: 1 1 0;
    margin-right: 0;
}

.properties-container .properties {
    height: 100%;
    margin-top: 0;
}

.dtbl .properties > .regCC,
.dtbl .properties > .attributes,
.dtbl .properties > .attribute,
.dtbl .properties > span {padding: 0;}

.issues-sidebar {width:150px;}
.issues-center {    margin-left: 155px;}

.issue-actions .issue-attachment-link {
    margin-left: 5px;
    margin-right: 5px;
    padding-top: 6px;
    position: absolute;
}

.issue-details {padding-right: 24px;}

.issue-toggler {right: -21px;}

.issue-last-update {
    border-left: 1px solid lightgray;
    color: #6e6e6e;
    float: none;
    font-size: 85%;
    margin: 0;
    order: 1;
    padding-left: 7px;
    position: relative;
    text-align: right;
    width: 70px;
}
.issue-last-update .user-name {
    display: inline-block;
    overflow: hidden;
    overflow-wrap: unset;
    text-overflow: ellipsis;
    white-space: nowrap;
    width: 70px;
}

.issue-info {
    flex: 1 1 0;
    padding-right: 7px;
}

.issue-controls-wrp {
    display: flex;
    padding-left: 20px;
    vertical-align: top;
    width: auto;
}

.issue-details {
    display: flex;
    padding-right: 24px;
    vertical-align: top;
    width: 100%;
}

.issue-details .update-date {margin-top: -10px;}

.properties-container .properties .regCC:first-child .attribute,
.properties-container .properties .regCC .attribute[title^="State:"] {text-align:left;text-overflow:unset;}

.properties-container .properties .regCC .attribute[title="State: Feedback"] {background-color:#E6ADCD; color:purple;}
.properties-container .properties .regCC .attribute[title="State: To Verify"] {background-color:#ff3300;color:#ffffff;}
.properties-container .properties .regCC .attribute[title="State: Rejected"] {background-color: lightgray;color: #444;}
.properties-container .properties .regCC .attribute[title="State: On hold"] {background-color:#CFDFF6;}
.properties-container .properties .regCC .attribute[title="State: Done"] {background-color:#a6df96;color:green;}
.properties-container .properties .regCC .attribute[title="State: In Progress"] {background-color:#3399FF;color:#ffffff;}
.properties-container .properties .regCC .attribute[title="State: Open"] {background-color:#F7CCBD;}
.properties-container .properties .regCC .attribute[title="State: Duplicate"] {}

/* issue type cell */
.properties-container .properties .regCC:nth-child(3) {
    max-width: 40px;
    width: 40px;
}

.fsi-layout #spContainer + .content,
.issues-center {margin-top:10px;}

.fsi__toolbar.toolbar-unpined {
    right:2em!important;
    left:auto!important;
    top:4em!important;
}

/*.fsi-top-toolbar.toolbar-unpined {box-shadow: 0 4px 3px -3px #999999;}*/

.fsi-layout .issueContainer__fsi_notop > div:first-child {min-height:0px!important;}

.fsi-layout .content_fsi {margin-top:10px;}

.fsi-layout .toolbar_fsi {width:100%;}

.fsi-layout .toolbar_fsi.toolbar-unpined {top:0!important;}

.fsi-layout .vertical-properties-east {margin-top:1em;}

.fsi__top-data {margin-bottom: 2px;}
}
