        .Banner-image,
        .Pc-card .Banner-adsense,
        .Pc-card img,
        [title="third_ad"],
        .zh-footer {
            display: none !important;
        }
    }
    
    @-moz-document domain("www.baidu.com"), domain("zhidao.baidu.com") {
    #s_wrap,
        /*首页的我的导航框*/
        #bottom_container,
        /*首页的footer*/
        a.s-skin,
        .ad-block,
        /*招商广告*/
        .result.c-container:last-of-type+div,
        /*搜索结果中的广告*/
        #tpl,
        .wgt-daily,
        /*知道日报*/
        .hao123-box,
        iframe,
        .cms-slide,
        .widget-new-graphic,
        .widget-sma,
        .icon-hotlk,
        .qrcodeCon,
        /*热搜*/
        [tpl="right_toplist"] {
            display: none;
        }
