css:
@-moz-document domain("youku.com") {
  
  th,
td {
    text-align: left;
    vertical-align: top
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-size: 1em;
    font-weight: 400
}
p {
    margin: 0 0 1em
}
blockquote {
    margin: 0 0 1em
}
a {
    color: #333;
    text-decoration: none;
    cursor: pointer
}
a:hover {
    color: #35b5ff;
    text-decoration: none
}
a:focus {
    outline: 0
}
img {
    display: block;
    border: 0
}
li {
    list-style: none
}
ol {
    padding: 0 0 0 2em
}
input,
label,
select,
option,
textarea,
button,
fieldset,
legend {
    font-size: 1em;
    font-family: inherit
}
input[type=text],
textarea {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    outline: 0
}
input.text {
    color: #444;
    border-color: #9f9f9f #dcdcdc #dcdcdc #9f9f9f;
    border-style: solid;
    border-width: 1px;
    padding: 3px 2px;
    height: 14px
}
input.radio,
input.checkbox {
    margin-right: 2px;
    vertical-align: -2px
}
input.radio,
input.checkbox {
    margin-left: -2px
}
label.radio,
label.checkbox {
    margin-right: 5px
}
textarea {
    border-color: #9f9f9f #dcdcdc #dcdcdc #9f9f9f;
    border-style: solid;
    border-width: 1px;
    padding: 1px;
    resize: none
}
input::-ms-clear {
    display: none
}

.clearfix:after,
.fix:after {
    content: ".";
    display: block;
    height: 0;
    clear: both;
    visibility: hidden
}
.clearfix,
.fix {
    min-height: 1%
}
.fix {
    display: block
}
.clearleft {
    clear: left
}
.tui-mask {
    position: absolute;
    top: 0;
    left: 0;
    background: #000;
    filter: alpha(opacity=30);
    opacity: .3
}
.fl {
    float: left
}
.fr {
    float: right
}
.tui-dialog {
    background: #fff;
    position: absolute;
    top: -10000px;
    border: 1px solid #f1f3f7;
    border-radius: 2px;
    box-shadow: 0 3px 9px rgba(0, 0, 0, .3)
}
.tui-dialog .tui-dialog-content {
    width: 450px
}
.tui-dialog .tui-dialog-title {
    color: #5b5b5b;
    font-size: 14px;
    padding: 6px 0 0 22px
}
.tui-dialog .tui-dialog-close span {
    display: none
}
.tui-dialog .tui-dialog-close i {
    position: absolute;
    right: 6px;
    top: -3px;
    font-size: 30px;
    color: #ccc;
    line-height: 1em;
    font-style: normal
}
.tui-dialog .tui-dialog-body {
    padding: 22px
}
.tui-dialog .tui-dialog-footer {
    padding: 22px;
    text-align: right
}
.tui-dialog-alert .alert-text {
    padding: 22px
}
.g-login-dialog .tui-dialog-content {
    width: 540px
}
.g-login-dialog .tui-dialog-body {
    padding: 0;
    height: 473px
}
.g-login-dialog .tui-dialog-footer {
    display: none
}

.iconfont {
    font-family: tuiicon;
    speak: none;
    font-style: normal;
    font-weight: 400;
    font-variant: normal;
    text-transform: none;
    vertical-align: middle;
    vertical-align: baseline\9;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale
}

.yk-content .top-festival {
    height: 70px;
    position: relative;
    top: 0;
    z-index: 999;
    overflow: hidden;
    text-align: center;
    display: none !important;
}

.yk-AD-tong {
    width: 1740px;
    margin: 40px 0 12px;
    overflow: hidden;
	display: none !important;    
}

.bt-banner {
    width: 100%;
    height: 160px;
    position: fixed;
    bottom: 0;
    left: 0;
    z-index: 1000;
    display: none !important;
}

#ab_1453377679{
    display:none !important;
}

.yk-content .top70, .top70 {
    top: 0px !important;
}



  
  
  
