css:
@-moz-document domain("diigo.com") {

body {
    font-family: 'Arimo', Arial, Helvetica, sans-serif;
}

.sideBarNavigate li a {
    font-size: 12px;
    height: 35px;
    line-height: 40px;
}

.sideBarNavigate li.sub a {
    line-height: 35px;
    height: 35px;
}

.sideBarNavigate li.selected a, .sideBarNavigate li.sub a.selected {
    font-size: 14px;
}

.sideBarNavigate .moreNavAction {
    font-size: 12px;
    color: #2185d0;
}

.UnreadLabel .count {
    left: 112%;
    font-size: 11px;
}

.library .inner .items {
    margin-left: 310px;
}

.ItemTitle .titleArea .content {
    color: #13314ae0;
    font-family: 'Ubuntu',Arial,Helvetica,sans-serif;
    font-weight: 400;
    max-width: 95%;
}

.ItemListTop.summerize {
    font-size: 12px;
}

.DropDown label {
    font-size: 12px;
}

.ListItem .actionArea .date {
    font-size: 12px;
}

.keywords_highlight_color {
    color: #b9440de3;
}

b, strong {
    font-weight: 600;
}

.siteActionBarContainer {
    height: 72px;
}

.siteActionBarContainer .siteActionBar .actionBarInner {
    background-color: #3f98e5d1;
    height: 48px;
    padding: 7px 0;
}

.siteActionBarContainer .siteActionBar.fixed .actionBarInner {
    height: 48px;
}

.siteActionBarContainer .siteActionBar .actionBarInner .actionBarInnerContainer .search-box {
    padding-bottom: 0px;
}

.siteActionBarContainer .siteActionBar .actionBarInner .actionBarInnerContainer .title {
    font-size: 18px;
}

.userInfo .acctStatusBtn {
    display: none;
}

.addItemButton {
    width: 48px;
    height: 48px;
    line-height: 47px;
}

.siteActionBarContainer .siteActionBar .addButton {
    bottom: -6px;
}

.siteActionBarContainer .siteActionBar.fixed .addButton .addItemButton {
        transform: translateY(-20px) scale(.7);
}

.ui.fluid.image {
    display: none;
}

.library.headerGg .siteActionBarContainer {
    padding-bottom: 0px;
}

.siteActionBarContainer .siteActionBar .actionBarInner .actionBarInnerContainer {
    height: 52px;
}

.BookmarkItem .host {
    color: #3f98e5;
}

.MainNav {
    background-color: #62abea;
}

.MainNav .inner {
    padding-top: 8px;
    padding-bottom: 8px;
}

.Tag .d-tag {
    background: #fff93b4d;
}

.tagLine .tagArea:hover, .tagLine.excluded, .tagLine.excluded .tagArea:hover, .tagLine.selected, .tagLine.selected .tagArea:hover { background-color: #fffdc4;
}

.ItemTitle .titleArea .titleInner .expandArea .listAnnotationCount {
    background-color: #fffdc4;
    color: #c35b22;
}

.BookmarkItem .description {
    color: #304a60;
}

.highlight.yellow .content {
    border-left-color: #fffdc4;
}

.highlight.blue .content {
    border-left-color: #6eaaf575;
}
}
