css:
@-moz-document url-prefix("https://docs.oracle.com/javase/tutorial") {
body {
    width: 50em;
    margin: auto;
    font-family: Noto Sans, verdana, arial, helvetica, sans-serif;
    font-size: 14pt;
    font-weight: 400;
    line-height: 1.6em;
    letter-spacing: 0.02em;
    color: #5F574F;
    margin-top: 2em;
}

a {
    color: #FF004D !important;
}

i {
    color: #000000;
    padding: 0.1em;
    letter-spacing: 0.05em;
}

h1 {
    font-size: 18pt;
    font-weight: 400;
    color: #C2C3C7;
    letter-spacing: 0.1em;
    position: relative;
}

#LeftBar:before {
    content: 'Java Tutorials';
    position: absolute;
    color: #C2C3C7;
    margin-top: -2em;
}

#LeftBar {
    margin-top: 2.5em;
}

h2 {
    margin-top: 1em;
    padding-top: 1em;
    color: #000000;
    font-size: 16pt;
    font-weight: 700;
    border-top: 1px solid #C2C3C7;
    letter-spacing: 0.1em;
}

h3 {
    font-size: 14pt;
    color: #5F574F;
    letter-spacing: 0.1em;
    margin-top: 2em;
    margin-left: -2em;
}

li:not(:first-child) {
    margin-top: 1em;
}

div.note {
    background-color: #FFF1E8;
    box-shadow: 2px 2px 4px rgba(0, 0, 0, 0.25);
    padding: 1em;
    width: 320px;
    font-size: 11pt;
    line-height: 1.5em;
    position: absolute;
    margin-left: -375px;
    margin-top: 0.5em;
    margin-bottom: 1em;
}
div.note div.codeblock {
    background-image: linear-gradient(90deg, white 50%, transparent); 
    font-size: 10pt;
}
div.note code {
    font-size: 10pt;
}
div.note hr {
    display: none;
}

div.NavBit {
    line-height: 1.2em;
    font-size: 11pt;
    text-align: center;
}

table, td, tr, tbody, thead, th {
    border: 0;
    border-collapse: collapse;
    border-width: 0;
}

td {
    padding: 0.125em 0.5em;
    font-size: 12pt;
}
th {
    /* background-color: #fff1e8; */
    background-color: #ffccaa;
    text-align: left;
    font-weight: 400;
    padding: 0.125em 0.5em;
}

tr:nth-last-child(2n-1) {
    background-color: #fff1e8;
}

div#LeftBar {
    line-height: 1.2em;
    font-size: 11pt;
    width: 20em;
    position: fixed;
}
div#LeftBar div#Contents div:not(.linkLESSON):before {
    position: absolute;
    margin-left: -4px;
}

div#LeftBar div#Contents div.linkAHEAD:before {
    content: '•';
}

div#LeftBar div#Contents div.linkBHEAD:before {
    content: '◦';
}

div#LeftBar div#Contents div.nolinkAHEAD:before,
div#LeftBar div#Contents div.nolinkBHEAD:before {
    content: '➜';
    margin-left: -8px;
}

div#LeftBar div#Contents div.nolinkAHEAD,
div#LeftBar div#Contents div.nolinkBHEAD {
    font-weight: normal;
}

div#MainFlow {
    margin-left: 16em;
}

div.codeblock {
    border: 1px solid #FFCCAA;
    border-width: 0 0 0 4px;
    background-image: linear-gradient(90deg, #FFF1E8 50%, transparent); 
    line-height: 1.2em;
    margin: 0;
    padding: 0.25em;
    font-family: Source Code Pro, monospace;
    font-size: 12pt;
    width: 100%;
}

div.codeblock pre {
    font-family: Source Code Pro, consolas, ubuntu mono, monospace;
    letter-spacing: 0;
    margin: 0;
}

code {
    background-color: rgba(0, 0, 0, 0.125);
    border-radius: 100px;
    padding: 0.1875em 0.375em;
    margin: -0.1875em;
    font-size: 12pt;
    font-family: Source Code Pro, consolas, ubuntu mono, monospace;
    letter-spacing: 0;
}

code code {
    background-color: transparent;
    border-radius: 0;
    padding: 0;
    margin: 0;
}

div#PageContent {
    margin-left: 0;
}

div#TopBar, div#BreadCrumbs, div.header-container, div.Banner, div.NavBit:nth-child(3), div#Footer, hr.clearfloat {
    display: none;
}
}
