css:
@-moz-document domain(wowhead.com) {


.header {
    background: rgba(0, 0, 0, 0); }

.sidebar {display: none;}

body {
    background:#171717; }

.row-featured-content {display: none;}

.news-post, .recent, .comments, .leave-comment, .comment {
    padding: 44px 12px; }

.news-recent-content { display: none }

.header-nav-features { display: none }

.news-current-guides[data-tree="classic"]{ display: none }

.header-nav-class-guides { display: none }

.zul-bar { display: none }

.site-footer { display: none}

a.header-logo, body.premium-logo .header-logo {
    background-image: url(https://i.imgur.com/ojeL1aN.png) !important;
    height: 100px !important;
    width: 144px !important;
    left: 1px !important;
    top: 5px !important;
}

.featured-content-block.type-coupon-codes .coupon-code-shop-link a {
    color: inherit;
    display: none; }

body.news .header-social {
    display: none; }

.announcement.announcement-styling-blizzard-blue-gradient .announcement-inner { display: none; }

body.home .premium-promo-row { display: none; }

.featured-content-block.type-coupon-codes { display: none; }

.page-content {
    padding-right: 0px;
}}
