  }
  .bird-topbar-etched {
    color: #878787 !important;
  }
  .btn.active, .btn:active, .dropdown.open .user-dropdown, .btn.open, .open > .btn {
    background-color: #1D1D1D !important;
  }
  #user-dropdown-toggle {
    background-color: #242424 !important;
  }
  /* search bar */
  .global-nav .search-input:not(:focus) {
    background-color: #242424 !important;
    border-color: #1D1D1D !important;
  }
  .global-nav .search-input:focus,
  .global-nav .search-input.focus {
    background-color: #292929 !important;
  }
  .global-nav .search-input::placeholder,
  input::placeholder,
  textarea::placeholder,
  .global-nav .search-icon .Icon {
    color: #979797 !important;
  }

  /* saved searches */
  .dropdown-menu {
    background-color: #363636 !important;
  }
  .dropdown-caret .caret-inner {
    border-bottom-color: #292929 !important;
  }
  .typeahead-recent-search-item .Icon--close,
