css:
@-moz-document url-prefix("http://relax.akatsuki.pw"), url-prefix("http://osu.akatsuki.pw") {
body {
    background-image: url(https://i.imgur.com/cs0Y4Cm.jpg) !important;
    background-size: cover !important;
    background-position: 50% !important;
    background-repeat: no-repeat !important;
    background-attachment: fixed !important;
    color: #FFFFFF
}
.mainbody2 #coolheader {
	background-image: url('http://i.imgur.com/On1UZsk.jpg')!important;
}
.final_footer {
	background: url('http://i.imgur.com/On1UZsk.jpg')!important
}
body, textarea, input, textarea:focus, input:focus {
	color: #FFFFFF;
}

textarea {
	background-color: rgba(0,0,0,0.3);
}

a {
	color: #FFFFFF!important;
	text-shadow: #FFFFFF 2px 2px 20px;
}

a:hover {
	color: #FFFFFF;
}

a:visited {
	color: #FFFFFF;
}

input, select {
	border: 1px solid #FFFFFF;
	background-color: rgba(239,139,46,0.70);
	color: #FFFFFF;
}

input[type="submit"], input[type="button"] {
	background-color: rgba(0,0,0,0.3);
	color: #FFFFFF;
}

input[type="submit"]:hover, input[type="button"]:hover {
	background-color: rgba(224,240,48,0.7);
	color: #FFFFFF;
}

h1,h2,h3,h4,h5 {
	color: #E8CA35!important;
	text-shadow: #E8CA35 0px 0px 10px!important;
}

h2, h2 a {
	color: #FFFFFF;
	text-shadow: #FFFFFF 0px 0px 10px;
}

.sfHover {
	background-color: #FFFFFF!important;
}

.sf-menu, .sf-menu a, .sf-menu li {
	background-color: rgba(0,0,0,0.3)!important;
} 

a.hovering {
	color: #FFFFFF!important;
	background-color: rgba(224,240,48,0.7)!important;
}

div#coolheader, .mainbody2, .footer, .final_footer {
	background-image: none!important;
}

.footer {
	background-color: rgba(0,0,0,0.5);
	border-bottom-left-radius: 10px;
	border-bottom-right-radius: 10px;
}

#playstats {
	margin-right: 8px;
	padding-left: 5px;
	background-color: rgba(0,0,0,0.5);
}

.news-heading {
	border-bottom: 1px solid #FFFFFF;
}

.news-text {
	background-color: rgba(0,0,0,0.3);
	border-top: 1px solid #FFFFFF;
}

.content-infoline {
	background-color: rgba(0,0,0,0.7);
	border-top-left-radius: 10px;
	border-top-right-radius: 10px;
	height: 24px;
}

.content-with-bg {
	background-image: none!important;
	background-color: rgba(0,0,0,0.5);
	margin-top: 0px;
}

.row1 {
	background-color: rgba(0,0,0,0.3);
}

.row2 {
	background-color: rgba(0,0,0,0.2);
}

.row3 {
	color: #FFFFFF;
}

td.chattime {
	color: #FFFFFF;
}

/** userpage **/
.userpage, .posttext, .quickreply, .changelog-text {
	border: 1px solid #FFFFFF;
	background-color: rgba(0,0,0,0.3)!important;
}

.userpage-expand {
	background-color: rgba(0,0,0,0.5);
}

.userpage-expand:hover, .spoiler:hover .spoiler_head {
	background-color: rgba(224,240,48,0.5);
}

.frozen-header, .playstyle-container {
	background-color: rgba(0,0,0,0.3);
	border-radius: 5px;
}

.sectionHeading {
	background-color: rgba(0,0,0,0.5);
}

.sectionHeading, .sectionHeading:hover, #tablist a.active  {
	background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAIklEQVQIW2NkQAL8K1b8Z4TxQZyPERGMYAEYB8RmROaABAC0og5jYLfsBQAAAABJRU5ErkJggg==")!important;
}

.sectionHeading, .sectionHeading:hover {
	color: #ce3;
	text-shadow: 0px 0px 3px #FFFFFF;
}

svg text {
	fill: #FFFFFF;
}

.profile-tabs td {
	background-color: rgba(0,0,0,0.3);
	border-color: #FFFFFF;
}

.profile-tabs td:hover {
	background-color: rgba(0,176,208,0.3);
}

.profile-username {
	color: #FFFFFF;
	text-shadow: #FFFFFF 0px 0px 10px;
}

.avatar-holder {
	background-color: transparent;
}

.btn {
	background-color: transparent!important;
	border: 2px solid;
}

.profileGameModes a {
	background-color: rgba(0,0,0,0.3);
	border-color: #FFFFFF;
}

.profileGameModes .active {
	background-color: rgba(224,240,48,0.7);
	color: rgba(0,0,0,0.7)!important;
}

.spoiler_head {
	border-color: #FFFFFF;
}

.sectionContents, .spoiler_body {
	background-color: rgba(0,0,0,0.3);
}

.profileStatLine, .profileStatHeader {
	background-color: rgba(0,0,0,0.3);
}

.profileStatHeader {
	color: #FFFFFF;
}

.eprofileBeatmapHeader:hover {
	background-color: rgba(64,168,255,0.2);
	color: #FFFFFF;
}

.levelPeercent {
	color: #FFFFFF;
}

.prof-beatmap {
	background-color: rgba(0,0,0,0.3);
	border: 1px solid #FFFFFF;
	border-radius: 3px;
}

.prof-beatmap .pp-display {
	color: #FFFFFF;
	text-shadow: #FFFFFF 0px 0px 10px!important;
}

.prof-beatmap .pp-display-weight {
	color: #FFFFFF;
}
/** settings **/
.mutual {
	background-color: rgba(224,240,48,0.7);
	border-color: #FFFFFF;
}

.mutual a {
	color: rgba(0,0,0,0.7)!important;
}

.row1:hover, .row2:hover, tr.row1p:hover, tr.row2p:hover, tr.row3p:hover, tr.row4p:hover, .tablebg .row1:hover {
	background-color: rgba(0,0,0,0.4)!important;
}

.forum_table tr:hover {
	color: #FFFFFF;
}

.postcount {
	color: #FFFFFF;
}

.top-maps-beatmap .header {
	color: #FFFFFF!important;
}

.btn.red {
	background-image: none;
	color: red!important;
	text-shadow: 0px 0px 10px red;
}

.stream {
	border-color: #FFFFFF;
}

.stream:hover {
	background-color:  rgba(0,0,0,0.3);
}

.stream>.title {
	color: #FFFFFF;
}

/** beatmap **/
#tablist, #tablist a, #songinfo td.colour {
	background-color: rgba(0,0,0,0.3);
}

#tablist a.active {
	background-color: rgba(0,0,0,0.3)!important;
}

#songinfo {
	background-color: transparent;
	color: #FFFFFF;
}

#songinfo td {
	background-color: rgba(0,0,0,0.5);
}

#songinfo td.colour {
	color: #FFFFFF;
}

.row1p {
	background-color: rgba(0,0,0,0.3);
}

.row2p {
	background-color: rgba(0,0,0,0.5);
}

.row3p {
	background-color: rgba(200,240,100,0.5);
}

.row4p {
	background-color: rgba(255,255,255,1);
}

.row3p a, .row4p a {
	color: #FFFFFF!important;
	text-shadow: 0px 0px 10px #FFFFFF!important;
}

.row3p:hover a, .row4p:hover a {
	color: #FFFFFF!important;
	text-shadow: #FFFFFF 0px 0px 10px!important;
}

/** forum **/
.tablebgsideNoSig, .pack-msg {
	background-color: rgba(255,255,255,1);
}

.postLeft, .postTime, .postbody {
	background-color: transparent;
}

.postauthor {
	color: #FFFFFF!important;
	text-shadow: #FFFFFF 0px 0px 10px;
}

.codecontent {
	color: #FFFFFF;
}

.osu-modheader {
	color: #FFFFFF;
}

.osu-timecode {
	background-color: rgba(255,255,255,1);
	color: #FFFFFF;
}

.osu-timecode:hover {
	background-color: rgba(255,255,255,1);
}

.postKudosu, .postButtons, .codecontent {
	background-color: rgba(255,255,255,1);
}

.btn {
	background-image: none;
}

.quotecontent {
	background-color: rgba(255,255,255,1);
	border-width: 0px 0px 0px 5px;
	border-color: #FFFFFF;
}

.post {
	background-color: transparent;
}

.tablebgside {
	background-color: transparent;
}

.bar {
	background-color: rgba(255,255,255,1);
}

/** search **/
#searchArea a:hover, #searchArea a.selected {
	background-color: rgba(255,255,255,1);
	border: 1px solid #FFFFFF;
}

#searchArea dl {
	border-bottom: 1px dotted #FFFFFF;
}

.beatmapListing th {
	background-color: rgba(255,255,255,1);
	border-bottom: 1px solid #FFFFFF;
}
.beatmapListing th:hover {
	background-color: #ce3;
	border-bottom: 1px solid #FFFFFF;
	color: #FFFFFF;
}

.beatmap, .bmlistt {
	height: 64px;
}

.beatmap .artist {
	color: #FFFFFF;
}

.beatmap, .changelog_area, .beatmap .tags a {
	background-color: rgba(255,255,255,1);
	border-color: #FFFFFF;
}

.bmlist-options {
	background-color: rgba(255,255,255,1);
}

.beatmap:hover, .beatmap .tags a:hover {
	background-color: rgba(255,255,255,1);
	border-color: #FFFFFF;
}

.beatmap .rating {
	border-color: #FFFFFF;
}

.posthilit {
	color: #FFFFFF;
}

/** changelog **/
.cat h4 {
	background-image: none;
	background-color: rgba(255,255,255,1);
	color: #FFFFFF!important;
}

.blog-post {
	background-color: transparent;
}

/** support **/
.dialog-area, .peppynote {
	background-color: rgba(255,255,255,1)!important;
}

.peppynote {
	border-color: #FFFFFF!important;
}

.reasonpanels>li {
	background-color: rgba(255,255,255,1)!important;
	color: #444!important;
}

.slider, .ui-progressbar {
	border-width: 1px;
	border-color: #0bd;
	background-color: rgba(255,255,255,1.00);
	background-image: none;
}

.ui-slider-range, .ui-progressbar-value {
	background-color: rgba(224,240,48,0.5);
	background-image: none;
}
}
