button[type="button"].switch_off,
button[type="button"].switch_on{
  position: relative;
  display: block;
  height: 20px;
  width: 50px;
  background: rgb(90,90,90);
  border-radius: 100px;
  cursor: pointer;
  transition: all 0.3s ease;
    min-width: 50px;
    min-height: 20px;
}

button[type="button"].switch_off:after{
  position: absolute;
  left: -0px;
  top: -0px;
  display: block;
  width: 20px;
  height: 20px;
  border-radius: 100px;
  background: #fff;
  box-shadow: 0px 3px 3px rgba(0,0,0,0.05);
  content: '';
  transition: all 0.3s ease;
}

button[type="button"].switch_on:after{
    /*
  position: absolute;
  left: -2px;
    */
  margin-left: 30px;
  top: -0px;
  display: block;
  width: 20px;
  height: 20px;
  border-radius: 100px;
  background: #fff;
  box-shadow: 0px 3px 3px rgba(0,0,0,0.05);
  content: '';
  transition: all 0.3s ease;
}
