css:
@-moz-document domain('viuly.io') {
:root {
    --black-0: #000;
    --black-1: #101010;
    --black-2: #1e1e1e;

    --gray-0: #333;
    --gray-1: #555;
    --gray-2: #757575;
    --gray-3: #a0a0a0;
    --gray-4: #b7b7b7;

    --blue-0: #07001e;
    --blue-1: #0b002b;
    --blue-2: #2eb7e6;
}

body {
	background: var(--black-1);
    color: var(--gray-4);
}

header {
	background: var(--black-2);
    color: var(--gray-4);
}

header .navbar-brand {
    background-image: url('data:image/svg+xml;base64,');
    background-size: 60px auto;
	width: 60px;
}

header .navbar .navbar-nav .nav-item .nav-link {
	color: var(--gray-2);
    border-color: var(--gray-0);
}

header .navbar .navbar-nav .live .nav-link:hover {
	background: var(--blue-1);
	border-color: var(--blue-2);
	color: var(--gray-4);
}

header .navbar .navbar-nav .live span::before {
	display: inline-block;
	margin: 0 .25rem .1rem 0;
	position: static;
}

header .navbar .navbar-nav .active .nav-link {
	background: var(--black-1) !important;
    color: var(--gray-2) !important;
    border-color: var(--blue-1);
}

header .dropdown .dropdown-menu {
	background: var(--black-0);
    color: var(--gray-3);
    border-color: var(--gray-0);
}

header .dropdown ul li a {
    border-color: var(--gray-0);
    color: var(--gray-3);
}

header .search a {
	background-image: url('data:image/svg+xml;base64,');
}

header .search-close {
    background-image: url('data:image/png;base64,') !important;
}

header .search-block {
    background: var(--black-0);
    border-top-color: transparent;
}

header .search-btn {
	background-color: var(--blue-2);
}

header .join a,
header .lang-swich a {
	color: var(--gray-2) !important;
}

header [data-block="notifications"]>a {
    background-image: url('data:image/svg+xml;base64,');
    border: 2px solid var(--blue-2);
	border-radius: 48px;
	width: 48px;
	height: 48px;
}

header [data-block="notifications"] .notifications_block,
header .user-menu.dropdown .dropdown-menu{
	background: var(--gray-0);
    color: var(--gray-4);
}

header .user-menu.dropdown .user-links li a {
    color: var(--gray-4);
}

.page-content {
    padding-top: 65px;
}

footer {
	color: var(--gray-4);
}

/*Elements*/
.browse_results_title,
*[class^="block"] .video-info li:first-child .name a {
    color: var(--blue-2);
}
*[class^="block"],
[data-block=user-chanel-profile] .channel-content {
	background-color: var(--black-1);
    box-shadow: none;
}

[data-block=basic-form] .form-group input,
[data-block=basic-form] .form-group select {
    background: var(--black-0);
    color: var(--gray-3);
    border-color: var(--gray-3);
}

*[class^="block"] .video-title,
#recomended_block .video .video-name a,
[data-block=user-chanel-profile] .channel-name-and-buttons .channel-name .channel-nav li .button,
*[class^="block"] .big-video-item .name {
	color: var(--gray-4);
}

.video-desc {
	padding-bottom: 20px;
}

.video-desc .name,
.video-desc a,
*[class^="block"] .right-content .comments .tit,
.filter_item a,
*[class^="block"] .big-video-item .text {
	color: var(--gray-1);
}

.browse_results_title:hover,
.video-desc a:hover,
.filter_item a:hover {
    color: var(--gray-3);
}

*[class^="block"] .tags li {
	background: var(--gray-1);
    padding: 0.25rem;
	top: -0.9rem;
}

*[class^="block"] .left-content ul li .video {
	margin-bottom: 3rem;
}

*[class^="block"] .recomended .tit {
	margin-bottom: 2rem;
}

*[class^="block"] .recomended .recomended-videos li {
	margin-bottom: 1.5rem;
}

.video-item .video-thumb img {
	box-shadow: none;
}

.button-blue:hover,
.button-subscribe,
.button-white {
	background: var(--black-1);
    color: var(--blue-2);
}
.registry,
.top-shadow {
	box-shadow: none;
}
.title h1 {
	color: var(--gray-4);
}
.dropdown-menu,
.form-control,
#tokenfield_tags input,
[data-block=basic-form] .form-group textarea,
.custom-control-label::before,
.custom-checkbox .custom-control-input:checked ~ .custom-control-label::before,
.was-validated .custom-control-input:valid:checked ~ .custom-control-label::before,
.was-validated .custom-control-input:valid ~ .custom-control-label::before {
    background: var(--gray-0);
	color: var(--gray-3);
}
.dropdown-item {
    color: var(--gray-3);
}
.block15 {
    border: none;
}
button:active,
button:focus
{
    outline: none;
}
.button-gray {
    background: var(--gray-0);
    color: var(--gray-4);
}
.block6 .videos-wrapper {
	border-left-color: var(--gray-0);
}

/*Video overhaul*/
.block9 {
    padding: 3rem 0;
	background: var(--black-0);
}
.block9 .container {
	max-width: none;
	min-width: 1260px;
	width: 1420px;
}
@media only screen and (max-width: 1419px) {
    .block9 .container {
        width: 100%;
        padding-left: 20px;
        padding-right: 20px;
        min-width: 300px;
    }
}

.block9 .container div[data-module=VideoPlayer] {
    display: flex;
}

.block9 .container div[data-module=VideoPlayer] .playlist_container {
    position: static;
    margin: 6.2rem 0 0 1rem;
    width: 25rem;
}

/* Donation */
.donate-block[data-module="Donate"] {
	background: var(--gray-0);
}
#donate_form .button-white:hover {
	border-color: var(--black-2);
}
.block11 .right-content {
	border-color: var(--gray-0);
}
.block11 .right-content .comments .comments-list li form textarea {
    background: var(--gray-0);
    color: var(--gray-4);
}

/*Transactions*/
.user-balance .balance-wrapper .balance-amount,
.user-balance .transactions-list .one-transaction .transaction-info b {
	color: var(--gray-3);
}
.user-balance .transactions-list .one-transaction:nth-child(even) {
    background: var(--gray-0);
    border-left: 1px solid #ededed;
    border-right: 1px solid #ededed;
}
.search-cat-list,
.user-balance .balance-wrapper,
.user-balance .accordion .card .card-header button {
    background: var(--gray-0);
    color: var(--gray-3);
}
.user-balance .accordion .card .card-header .collapsed {
    background: var(--gray-0);
}

/*Adv vid*/
.adv-wrapper .adv-steps {
    background: var(--gray-0);
	color: var(--gray-3);
}

/*user channels*/
[data-block="user-playlist"] .playlist-items .playlist-item .video-item .playlist-buttons li .delete-playlist {
		background-image: url(data:image/svg+xml;base64,);
}
[data-block="user-playlist"] .playlist-items .playlist-item .video-item .playlist-buttons li .edit-playlist {
		background-image: url(data:image/svg+xml;base64,);
}
.upload-video .adv-wrapper #upload-step-3 .video-thumb-select li .upload-image,
.user-my-channels .video-item .add-item {
	background-color: var(--gray-0);
}

/*New channel*/
.profile_block2,
.profile_block3{
    background: var(--black-1);
    color: var(--gray-4);
}
.profile_block2 p,
.profile_block3 .channel-info label,
.profile_block3 .channel-type label {
    color: var(--gray-4);
}
.profile_block3 .channel-type a.dropdown-toggle {
	background: none;
}

/*Channel videos*/
[data-block="video-filter"] .serch-cat ul li {
	color: var(--gray-3);
}
[data-block="user-playlist"] .playlist-items .playlist-item .video-item .playlist-buttons {
	border-color: var(--gray-0);
}
.ads-enable-icon-control {
	background-image: url(data:image/svg+xml;base64,);
}

/*Upload Video*/
#upload-video-form {
	padding: 1rem;
    color: var(--gray-4);
}
.tokenfield .token .token-label {
	color: var(--gray-4);
}
.tokenfield .token .close {
    color: var(--gray-2);
}
#tokenfield_tags input {
	padding-left: 0;
	padding-right: 0;
}
.upload-video .adv-wrapper #upload-step-2 .progress {
	background: var(--gray-0);
}
.upload-video .adv-wrapper #upload-step-2 .progress .progress-bar {
	overflow: hidden;
}
.upload-video .adv-wrapper #upload-step-2 .progress .progress-bar .message {
	margin-right: .5rem;
}
.upload-video .adv-wrapper #upload-step-2 .progress .abort {
    background: var(--gray-2);
}
.upload-video .adv-wrapper #upload-step-3 .video-thumb-select li .video-thumb {
	box-shadow: 0 0 4px var(--gray-4);
}

/* channel owner view */
.video-thumb a {
	display: block !important;
}
[data-block="user-playlist"] .playlist-items .playlist-item .video-item .video-thumb img {
    width: 100%;
    height: auto;
    max-height: 200px;
}
.video-desc .name {
    min-height: 36px;
}

/* recommended list */
.recomended-videos.list img {
    width: 100%;
    height: auto;
    max-width: 100%;
    max-height: 130px;
}

/* history */
[data-block="user-playlist"] .playlist-items .playlist-item .video-item .playlist-item-action .dropdown-menu li a {
    border: none;
    color: var(--gray-4);
}
[data-block="user-playlist"] .playlist-items .playlist-item .video-item .playlist-item-action .dropdown-more {
    background-color: var(--gray-1);
}


/***************************************
 ***************************************
 ********* PRELIMINARY CHANGES *********
 ***************************************
 ***************************************/
/*main page*/
@media (min-width: 1000px) {
    [data-module=sliderScrollBar] .mCustomScrollBox:after {
        content: "";
        position: absolute;
        left: -15px;
        top: 0;
        height: 100%;
        width: 80px;
        transform: rotate(180deg);
    }
    [data-module=sliderScrollBar] .mCustomScrollBox:before,
    [data-module=sliderScrollBar] .mCustomScrollBox:after {
        background: -moz-linear-gradient(left, rgba(242,242,242,0) 0%, var(--black-1) 100%);
        background: -webkit-linear-gradient(left, rgba(242,242,242,0) 0%, var(--black-1) 100%);
        background: linear-gradient(to right, rgba(242,242,242,0) 0%, var(--black-1) 100%);
        pointer-events: none;
    }
    .mCustomScrollbar .for-button {
        position: static
    }
    [data-module=sliderScrollBar] .for-button .mCSB_buttonLeft {
        left:10px;
        top: -250px;
    }
    [data-module=sliderScrollBar] .for-button .mCSB_buttonRight {
        right: 10px;
        top: -250px;
    }
}
/*grid fixes*/
@media (min-width: 1354px) {
    .container {
        max-width: 1334px;
    }
}

[data-block="user-playlist"] .playlist-items .playlist-item,
[data-block="user-playlist"] .playlist-items .playlist-item:nth-child(3n+3) {
	width: 315px;
	margin: 0 14px 69px 0;
}
[data-block="user-playlist"] .playlist-items .playlist-item:nth-child(4n+4) {
	margin-right: 0;
}

/************************
 ** AD Blocking
*************************/
ul.tags+iframe,
.external_banner,
#ambn30410 {
    display: none !important;
}
}
