css:
@-moz-document domain("www.gizmodo.jp") {
#gizmodo_normal-0{
  display:none !important;  
  }  
  
#app > div > div > div.gz-headline{
  display:none !important;    
  }
  
  body{
    background-repeat: no-repeat !important;
    background-size: cover !important;
    background:#000 !important;
	background: url('https://s20.postimg.cc/fcly15srh/pokemon-gizmodo.jpg') center center fixed !important;                        
  }  

.gz-block-pattern--2a .gz-block-column--1::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 584px;
    height: 100%;
    background: rgba(255,255,255,0.5) !important;
    z-index: -1;
}  
  
.gz-block-pattern--2a .gz-block-column--1::before {
    background: rgba(255,255,255,0.7) !important;
  }  
  
.gz-block-pattern--2a .gz-block-column--sideAd1::before {
    background: rgba(255,255,255,0.5) !important;
  }  
  
  .psvr-block-item.psvr-block-item--1 > a.psvr-summary{
    background: rgba(255,255,255,0.5) !important;    
  }  
  
#app > div > div > main.gz-main.gz-main--index > div.gz-wrapper > div.gz-block > div{
    background: rgba(255,255,255,0.6) !important;      
  }  
  
.gz-block-item--button{
    background:none !important;  
  }  
  
#app > div > div > div.gz-block > div{
    background: rgba(255,255,255,0.6) !important;     
  }
  
#app > div > div > footer.gz-footer{
    background: rgba(255,255,255,0.6) !important;     
  }
  
  
.gz-footer {
    width: 100%;
    text-align: center !important;
  }
  
.gz-footerNav-pattern--other .gz-footerNav-item {
    width: 100% !important;
  }
  
.gz-footer .gz-footerCopyright {
    max-width: 100% !important;
    text-align: center !important;
  }  
  
#app > div > div > header.gz-header{
    background: rgba(255,255,255,0.7) !important;       
  }  
  
  #app > div > div > main > div.gz-wrapper > div > div{
    background: rgba(255,255,255,0.7) !important;           
  }
  
#app > div > div > main.gz-main.gz-main--archive > div.gz-wrapper > div.gz-block > div > ul#archive_list{
    background: rgba(255,255,255,0.5) !important;          
  }  
  
ul#archive_list > li > a{
    background: transparent !important;    
  }  
  
  
#app > div > div > main.gz-main.gz-main--index > div.gz-block > div.gz-issue-header > div.gz-issueHeader-summary{
  background:none !important;  
  }  
  
.gz-side-item--list > a.gz-summary{
    background: transparent !important;     
  }  
  
.gz-summary-imageWrapper {
    background: transparent !important;
}  
  
.gz-wrapper > div.gz-block > div.gz-block-pattern.gz-block-pattern--2a > div.gz-block-column.gz-block-column--1 > div.gz-block-item.gz-block-item--1.gz-block-item--withDetail.is-read > a.gz-summary {
  background:transparent !important;
  }
  
.gz-wrapper > div.gz-block > div.gz-block-pattern.gz-block-pattern--2a > div.gz-block-column.gz-block-column--1 > div.gz-block-item.gz-block-item--1.gz-block-item--withDetail.is-read > a.gz-summary img{
   width:100px !important;
  margin-top: 130px;
  margin-left:-220px;
  position: relative !important;
  }

.l-generalContent-primary.-main {
    background: none !important;
}

body,div,section,p,td{background:#000 !important;}
body,div,section,p,td{color:#ddd !important;}
a:link{color:#fff !important;}


.h-body-logo svg {
    fill: #eee !important;
}

.h-body-header {
    border-bottom: 5px solid #333;
}

.h-header-menuSearch {
    border-left: 1px solid #333!important;
    border-right: 1px solid #333!important;
    text-align: center;
    box-sizing: border-box;
    width: 58px;
    height: 50px!important;
}

.h-body-navContentSearchForm input {
    color: #fff;
    width: 1000px;
    display: block;
    margin: 32px auto 32px;
    padding: 0;
    font-size: 2.25rem;
    outline: none;
    line-height: 1.2;
    border: none;
    background: #111!important;
    padding: 10px!important;
}

.p-post {
    background-color: transparent !important;
}



  body{
    background-repeat: no-repeat !important;
	background: url('https://s20.postimg.cc/fcly15srh/pokemon-gizmodo.jpg') no-repeat !important;   
	background-size: 100% !important;   
    background-attachment:fixed !important;
  }  

.ad-banner div{
    background:none !important;
}


