}

.l-ad{    background:none !important;}


.l-ad{    background:none !important;}

.p-post-content {
    position: relative;
    width: 578px;
    margin: 32px auto 0;
    line-height: 1.75;
    padding: 50px !important;
}

.l-breadcrumbs {
    width: 1184px;
    margin: 16px auto 0;
    padding: 10px !important;
}

  
}
