css:
@-moz-document domain("tweetdeck.twitter.com") {
html.dark .flex-column {
    margin-right: -10px;
}

html.dark .is-wide-columns .column {
    overflow: hidden;
}

html.dark .stream-item {
    border-bottom: 1px solid #221629;
}

html.dark .is-wide-columns .column {
    margin-right: 3px;
}

html.dark .is-condensed .app-content {
    margin-bottom: -13px;
}

html.dark .column-nav-item, html.dark .app-header, html.dark .app-navigator, html.dark .app-title {
    background-color: #322447;
}

html.dark .stream-item {
    background-color: #251b36;
}

html.dark .column-header, html.dark .column-header-temp {
    background-color: #322447;
}

html.dark .app-search-fake {
    color: white;
    border: 1px solid white;
}

html.dark .app-nav-link.is-selected, html.dark .app-nav-link:hover {
    color: #a771ff;
}

html.dark .app-nav-link, html.dark .app-nav-tab {
    color: #9388fe;
}

html.dark a {
    color: #ffeac6;
}

html.dark .app-search-fake .icon, html.dark .app-search-fake .Icon {
    font-size: 19px;
}

html.dark .column-header, html.dark .column-header-temp {
    padding: 0 24px;
}
}
