css:
@-moz-document domain("graphemica.com") {

/* Main background color */
body {
  background-color: #212121 !important;
}

textarea,
input,
button,
select,
#auth-header,
#site-header {
  background-color: inherit !important;
}

/* Primary text color */
h1,
h2,
h3,
h4,
h5,
h6,
textarea,
input[type="text"],
input[type="email"],
input[type="password"],
#main-content h1 a,
#site-header a,
#characters ul.breadcrumbs,
#glyph-display ul.breadcrumbs {
  color: rgba(255, 255, 255, 0.93) !important;
}

/* Secondary text color */
body,
#comments h4 {
	color: rgba(255, 255, 255, 0.7) !important;
}

/* Hint text color */
#search-icon,
.form-group,
.checkbox {
  color: rgba(255, 255, 255, 0.5) !important;
}

::-webkit-input-placeholder { color: rgba(255, 255, 255, 0.5) !important; }
::-moz-placeholder          { color: rgba(255, 255, 255, 0.5) !important; }
:-moz-placeholder           { color: rgba(255, 255, 255, 0.5) !important; }
::-ms-input-placeholder     { color: rgba(255, 255, 255, 0.5) !important; }
:-ms-input-placeholder      { color: rgba(255, 255, 255, 0.5) !important; }
::placeholder               { color: rgba(255, 255, 255, 0.5) !important; }

/* Alternate primary text color */
#dislike,
#flash-alerts,
#errorExplanation {
  color: rgba(255, 64, 129, 1) !important;
}

/* Link text color */
a,
a:active,
a:hover,
a:focus,
a:visited {
  color: rgba(64, 196, 265, 1) !important;
}

/* Border color */
h2,
h3,
ul,
table,
td,
.userform div,
#site-header,
#comments .comment,
#likes header,
#feed header,
#likes .character,
#feed .feed-glyph,
#character-list .character {
  border-color: #424242 !important;
}

textarea,
input[type="text"],
input[type="email"],
input[type="password"] {
	border-bottom-color: #424242 !important;
}

/* Submit buttons */
#main-content h3 a,
#main-content h3 a:visited,
#main-content input[type="submit"] {
  border-color: #40C4FF !important;
  color: #40C4FF !important;
  -webkit-transition: background-color 0.2s, color 0.2s !important;
  -moz-transition:    background-color 0.2s, color 0.2s !important;
  -o-transition:      background-color 0.2s, color 0.2s !important;
  transition:         background-color 0.2s, color 0.2s !important;
}

#main-content h3 a:active,
#main-content h3 a:focus,
#main-content h3 a:hover,
#main-content input[type="submit"]:active,
#main-content input[type="submit"]:focus,
#main-content input[type="submit"]:hover {
  background-color: #40C4FF !important;
  color: #212121 !important;
}

/* Character map hover color */
.has-char:active,
.has-char:focus,
.has-char:hover {
  background-color: #303030 !important;
}

/* Glyph image adjustment */
.glyph-image > img {
  -webkit-filter: invert(0.87109375) !important;
  filter: invert(0.87109375) !important;
}

}
