@-moz-document url-prefix("https://ernie.erau.edu/Pages/default.aspx") {
*,
.btn {
    background-color: #111;
}
li.active,
#s4-workspace a:hover,
#s4-workspace a:focus,
#s4-workspace a:hover,
#s4-workspace a:focus,
.resp-tabs-wrapper,
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus,
.masthead .container,
#header {
    /* Most non-linked text*/
    background-color: #111 !important;
    color: #fff;
}
a,
a:hover,
a:focus,
a:visited,
#DeltaTopNavigation .ms-core-listMenu-item,
#DeltaTopNavigation .ms-core-listMenu-item:link,
#DeltaTopNavigation .ms-core-listMenu-item:visited {
    /*!!!!! Linked Text Coloring !!!!!*/
