.iconSnsImage{
	/*TODO: Green Icon*/
    background: url("data:image/svg+xml;utf8,<svg viewBox='0 0 30 30' xmlns='http://www.w3.org/2000/svg'><path d='M8,15 a 10,10 0 1 1 0,.1' fill='green'/></svg>") !important;
}

.theme-default .nivo-caption a,
.theme-default .nivo-controlNav,
.table-wrapper,
.ms-core-brandingText,
#suiteBarLeft *,
#suiteBarRight *,
.panel-default > .panel-heading{
    /*A bunch of minor background, 'Hello <name>' mail icons, and 'View Events from earlier' tab*/
    background-color: transparent;
}
.table.events tr.earlier:last-child td {
    /*Student org events 'View Events from earlier' tab*/
    border: 2px #ddd;
    background-color: transparent;
}
#SearchBox .ms-srch-sb-border:hover,
#SearchBox .ms-srch-sb-borderFocused,
#SearchBox .ms-srch-sb-border {
    /*Top right search box*/
    border-style: groove;
    border-width: 1px 1px 1px 1px;
}
.resp-tabs-wrapper {
    /*Top of all tab areas, My Schedule, News, etc*/
    border-top: 3px solid #0483ff;
}
li.active {
    /*Selector triangle above "My Schedule and such, probably not adaptive enough for diff displays, but applys to all tab areas*/
    background: url("data:image/svg+xml;utf8,<svg viewBox='0 0 30 10' xmlns='http://www.w3.org/2000/svg'><path d='M12,0 l3,3 l3,-3' fill='#0483ff'/></svg>");
    background-repeat: no-repeat;
}
#footer .col-md-12 {
    /*Replace golden footer area*/
    border: none;
}
#ms-designer-ribbon {
    /*Top golden banner*/
    background-color: #413a26;
}
#main {
    background: #111;
}
}

