css:
@-moz-document domain("djangoproject.com") {
/* Fork or comment on: https://gist.github.com/batisteo/4f4c1552ba18ffa51a4b8bfb527c9673 */
/* License: http://www.wtfpl.net/txt/copying */


    body,
    [role="main"],
    .mdzr-boxshadow.container.sidebar-right {
        background: #222 !important;
    }

    @media screen and (min-width: 768px) {
        .mdzr-boxshadow .sidebar-right [role="main"] {
            box-shadow: 0px 0px 0 0 rgba(255, 255, 255, 0);
        }
    }
    
    .mdzr-boxshadow .container.sidebar-right {
        box-shadow: -1200px 0 0 0px rgba(255, 255, 255, 0);
    }

    [role="contentinfo"], .copy-banner,
    .homepage .copy-banner {
        background-color: rgba(68, 183, 140, 0.6);
        background: #277658;
        border: none;
    }
    .homepage .copy-banner p em {
        color: white;
    }
    #doc-versions li.current, #doc-versions li,
    #doc-languages li.current, #doc-languages li {
        color: grey;
        background: rgba(20, 20, 20, 0.8);
    }
    #doc-versions li.current, #doc-languages li.current {
        border: thin solid rgba(256,256,256, 0.1);
    }
    #doc-versions li a, #doc-languages li a {
        color: grey;
        background: rgba(20, 20, 20, 0.8);
        border: thin solid rgba(256,256,256, 0.1);
    }
    #doc-versions li a:hover, #doc-languages li a:hover {
        border: thin solid rgba(68, 183, 140, 0.5);
    }
    [role="main"] h1, h2, h3, h2 a {
        color: #67CFA8;
    }
    h4, h5, h6, body, tt, code, kbd, pre, samp, .reference.internal {
        color: #ccc;
    }
    [role="banner"] [role="navigation"] a {
        color: #ddd;
    }
    code.descname {
        color: #67CFA8;
    }
    [role="main"] img {
        opacity: 0.8;
    }
    
    ul>li:first-child>.reference.internal,
    a,
    span.pre {
        color: #44b78b;
    }

    .admonition,
    .admonition.warning,
    .admonition.note,
    .admonition.danger,
    pre.literal-block,
    .literal-block {
        background-color: #181818;
        border: none;
    }

    .highlight {
        background-color: rgba(255, 255, 255, 0.2);
    }

    table.django-supported-versions tr {
        background-color: rgba(201, 240, 221, 0.5);
    }

    table.django-supported-versions tr.unsupported {
        background-color: rgba(255, 186, 186, 0.5);
    }
    
    .a.reference,
    #docs-content a.reference {
        color: #c77;
    }

    form input[type="search"], form input[type="text"], form input[type="email"], form input[type="password"], form input[type="number"], form input[type="url"], form textarea {
        background-color: rgba(0, 0, 0, 0.2);
        color: #ddd;
        border-color: #555;
    }
    
    [role="main"] .section,
    [role="complementary"] h2,
    [role="complementary"] h3,
    .layout-secondary h2 {
        border-bottom-color: #555;
    }

    hr,
    [role="complementary"] .list-links dt,
    [role="complementary"] .search-links dt,
    .list-news li,
    .list-events li,
    .list-links li,
    .search-links li,
    .nav-pagination {
        border-top-color: #555;
    }
    
    #dev-warning {
        background-color: #cfb421;
        color: #111;
    }

    ::-moz-selection { color: white; background: #44B78B; }
    ::selection      { color: white; background: #44B78B; }

    .snippet-filename {
        background: #0C4B33;
        color: #C9F0DD;
    }
    .highlight {
        border: none;
    }

    /* https://github.com/richleland/pygments-css/blob/master/native.css */
    .highlight .hll { background-color: #404040 }
    .highlight  { background: #181818; color: #d0d0d0 }
    .highlight .c { color: #999999; font-style: italic } /* Comment */
    .highlight .err { color: #a61717; background-color: #e3d2d2 } /* Error */
    .highlight .esc { color: #d0d0d0 } /* Escape */
    .highlight .g { color: #d0d0d0 } /* Generic */
    .highlight .k { color: #6ab825; font-weight: bold } /* Keyword */
    .highlight .l { color: #d0d0d0 } /* Literal */
    .highlight .n { color: #d0d0d0 } /* Name */
    .highlight .o { color: #d0d0d0 } /* Operator */
    .highlight .x { color: #d0d0d0 } /* Other */
    .highlight .p { color: #d0d0d0 } /* Punctuation */
    .highlight .ch { color: #999999; font-style: italic } /* Comment.Hashbang */
    .highlight .cm { color: #999999; font-style: italic } /* Comment.Multiline */
    .highlight .cp { color: #cd2828; font-weight: bold } /* Comment.Preproc */
    .highlight .cpf { color: #999999; font-style: italic } /* Comment.PreprocFile */
    .highlight .c1 { color: #999999; font-style: italic } /* Comment.Single */
    .highlight .cs { color: #e50808; font-weight: bold; background-color: #520000 } /* Comment.Special */
    .highlight .gd { color: #d22323 } /* Generic.Deleted */
    .highlight .ge { color: #d0d0d0; font-style: italic } /* Generic.Emph */
    .highlight .gr { color: #d22323 } /* Generic.Error */
    .highlight .gh { color: #ffffff; font-weight: bold } /* Generic.Heading */
    .highlight .gi { color: #589819 } /* Generic.Inserted */
    .highlight .go { color: #cccccc } /* Generic.Output */
    .highlight .gp { color: #aaaaaa } /* Generic.Prompt */
    .highlight .gs { color: #d0d0d0; font-weight: bold } /* Generic.Strong */
    .highlight .gu { color: #ffffff; text-decoration: underline } /* Generic.Subheading */
    .highlight .gt { color: #d22323 } /* Generic.Traceback */
    .highlight .kc { color: #6ab825; font-weight: bold } /* Keyword.Constant */
    .highlight .kd { color: #6ab825; font-weight: bold } /* Keyword.Declaration */
    .highlight .kn { color: #6ab825; font-weight: bold } /* Keyword.Namespace */
    .highlight .kp { color: #6ab825 } /* Keyword.Pseudo */
    .highlight .kr { color: #6ab825; font-weight: bold } /* Keyword.Reserved */
    .highlight .kt { color: #6ab825; font-weight: bold } /* Keyword.Type */
    .highlight .ld { color: #d0d0d0 } /* Literal.Date */
    .highlight .m { color: #3677a9 } /* Literal.Number */
    .highlight .s { color: #ed9d13 } /* Literal.String */
    .highlight .na { color: #bbbbbb } /* Name.Attribute */
    .highlight .nb { color: #24909d } /* Name.Builtin */
    .highlight .nc { color: #447fcf } /* Name.Class */
    .highlight .no { color: #40ffff } /* Name.Constant */
    .highlight .nd { color: #ffa500 } /* Name.Decorator */
    .highlight .ni { color: #d0d0d0 } /* Name.Entity */
    .highlight .ne { color: #bbbbbb } /* Name.Exception */
    .highlight .nf { color: #447fcf } /* Name.Function */
    .highlight .nl { color: #d0d0d0 } /* Name.Label */
    .highlight .nn { color: #447fcf; text-decoration: underline } /* Name.Namespace */
    .highlight .nx { color: #d0d0d0 } /* Name.Other */
    .highlight .py { color: #d0d0d0 } /* Name.Property */
    .highlight .nt { color: #6ab825; font-weight: bold } /* Name.Tag */
    .highlight .nv { color: #40ffff } /* Name.Variable */
    .highlight .ow { color: #6ab825; font-weight: bold } /* Operator.Word */
    .highlight .w { color: #666666 } /* Text.Whitespace */
    .highlight .mb { color: #3677a9 } /* Literal.Number.Bin */
    .highlight .mf { color: #3677a9 } /* Literal.Number.Float */
    .highlight .mh { color: #3677a9 } /* Literal.Number.Hex */
    .highlight .mi { color: #3677a9 } /* Literal.Number.Integer */
    .highlight .mo { color: #3677a9 } /* Literal.Number.Oct */
    .highlight .sa { color: #ed9d13 } /* Literal.String.Affix */
    .highlight .sb { color: #ed9d13 } /* Literal.String.Backtick */
    .highlight .sc { color: #ed9d13 } /* Literal.String.Char */
    .highlight .dl { color: #ed9d13 } /* Literal.String.Delimiter */
    .highlight .sd { color: #ed9d13 } /* Literal.String.Doc */
    .highlight .s2 { color: #ed9d13 } /* Literal.String.Double */
    .highlight .se { color: #ed9d13 } /* Literal.String.Escape */
    .highlight .sh { color: #ed9d13 } /* Literal.String.Heredoc */
    .highlight .si { color: #ed9d13 } /* Literal.String.Interpol */
    .highlight .sx { color: #ffa500 } /* Literal.String.Other */
    .highlight .sr { color: #ed9d13 } /* Literal.String.Regex */
    .highlight .s1 { color: #ed9d13 } /* Literal.String.Single */
    .highlight .ss { color: #ed9d13 } /* Literal.String.Symbol */
    .highlight .bp { color: #24909d } /* Name.Builtin.Pseudo */
    .highlight .fm { color: #447fcf } /* Name.Function.Magic */
    .highlight .vc { color: #40ffff } /* Name.Variable.Class */
    .highlight .vg { color: #40ffff } /* Name.Variable.Global */
    .highlight .vi { color: #40ffff } /* Name.Variable.Instance */
    .highlight .vm { color: #40ffff } /* Name.Variable.Magic */
    .highlight .il { color: #3677a9 } /* Literal.Number.Integer.Long */
}
